import React, { useState, useRef, useEffect, useCallback } from "react";

export default function WhatsAppAudioRecorder() {
  const [recordings, setRecordings] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [audioStream, setAudioStream] = useState(null);
  const [permissionStatus, setPermissionStatus] = useState('checking');
  const [error, setError] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [debugInfo, setDebugInfo] = useState({});
  const [draggedItem, setDraggedItem] = useState(null);

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const timerRef = useRef(null);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const dataArrayRef = useRef(null);
  const animationRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  const debugLog = (message, data = null) => {
    console.log(`üé§ ${message}`, data);
    setDebugInfo(prev => ({
      ...prev,
      [`${Date.now()}`]: { message, data, time: new Date().toLocaleTimeString() }
    }));
  };

  const checkAndRequestPermissions = async () => {
    try {
      setPermissionStatus('checking');
      const testStream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true,
          sampleRate: 48000,
          channelCount: 1,
        } 
      });

      debugLog('‚úÖ Microphone access granted');
      testStream.getTracks().forEach(track => track.stop());
      setPermissionStatus('granted');
      return true;

    } catch (err) {
      debugLog('‚ùå Microphone access denied', err.message);
      setPermissionStatus('denied');
      setError(`Microphone required: ${err.message}`);
      return false;
    }
  };

  useEffect(() => {
    checkAndRequestPermissions();
  }, []);

  const cleanup = useCallback(() => {
    [timerRef, animationRef].forEach(ref => {
      if (ref.current) {
        ref === timerRef ? clearInterval(ref.current) : cancelAnimationFrame(ref.current);
        ref.current = null;
      }
    });

    if (mediaRecorderRef.current?.state !== 'inactive') {
      try {
        mediaRecorderRef.current?.stop();
      } catch (e) {
        debugLog('‚ö†Ô∏è MediaRecorder cleanup', e.message);
      }
      mediaRecorderRef.current = null;
    }

    if (audioContextRef.current?.state !== 'closed') {
      audioContextRef.current?.close();
      audioContextRef.current = null;
    }

    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
      setAudioStream(null);
    }

    setIsRecording(false);
    setRecordingTime(0);
  }, []);

  useEffect(() => cleanup, [cleanup]);

  useEffect(() => {
    if (isRecording) {
      timerRef.current = setInterval(() => setRecordingTime(prev => prev + 1), 1000);
    } else {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
      setRecordingTime(0);
    }
    return () => timerRef.current && clearInterval(timerRef.current);
  }, [isRecording]);

  const drawWaveform = useCallback(() => {
    if (!canvasRef.current || !analyserRef.current || !isRecording) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const analyser = analyserRef.current;
    const dataArray = dataArrayRef.current;

    const draw = () => {
      if (!isRecording) return;
      animationRef.current = requestAnimationFrame(draw);
      analyser.getByteFrequencyData(dataArray);

      ctx.fillStyle = "#f8f9fa";
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      const barWidth = (canvas.width / dataArray.length) * 2.5;
      let x = 0;

      for (let i = 0; i < dataArray.length; i++) {
        const barHeight = (dataArray[i] / 255) * canvas.height * 0.8;
        const gradient = ctx.createLinearGradient(0, canvas.height - barHeight, 0, canvas.height);
        gradient.addColorStop(0, '#25D366');
        gradient.addColorStop(1, '#128C7E');
        ctx.fillStyle = gradient;
        ctx.fillRect(x, canvas.height - barHeight, barWidth - 1, barHeight);
        x += barWidth;
      }
    };
    draw();
  }, [isRecording]);

  // STRATEGIC: Use Web Audio API with proper OGG Opus encoding
  const createWhatsAppCompatibleAudio = async (audioBuffer) => {
    debugLog('üîß Creating WhatsApp-compatible OGG Opus...');
    setIsProcessing(true);

    try {
      // Create OGG Opus using MediaRecorder with forced settings
      const stream = new MediaStream();
      const audioContext = new AudioContext({ sampleRate: 48000 });

      // Resample to 48kHz if needed
      let processedBuffer = audioBuffer;
      if (audioBuffer.sampleRate !== 48000) {
        const offlineContext = new OfflineAudioContext(1, audioBuffer.duration * 48000, 48000);
        const source = offlineContext.createBufferSource();
        source.buffer = audioBuffer;
        source.connect(offlineContext.destination);
        source.start();
        processedBuffer = await offlineContext.startRendering();
      }

      // Create oscillator with the processed audio
      const oscillator = audioContext.createOscillator();
      const destination = audioContext.createMediaStreamDestination();
      oscillator.connect(destination);

      // Use MediaRecorder to create proper OGG Opus
      const mediaRecorder = new MediaRecorder(destination.stream, {
        mimeType: 'audio/ogg; codecs=opus',
        audioBitsPerSecond: 64000 // WhatsApp standard bitrate
      });

      const chunks = [];

      return new Promise((resolve, reject) => {
        mediaRecorder.ondataavailable = (event) => {
          if (event.data.size > 0) chunks.push(event.data);
        };

        mediaRecorder.onstop = () => {
          const oggBlob = new Blob(chunks, { type: 'audio/ogg; codecs=opus' });
          debugLog('‚úÖ OGG Opus created', { size: oggBlob.size });
          resolve(oggBlob);
        };

        mediaRecorder.onerror = reject;

        oscillator.start();
        mediaRecorder.start();

        // Stop after processing
        setTimeout(() => {
          oscillator.stop();
          mediaRecorder.stop();
          audioContext.close();
        }, 100);
      });

    } catch (err) {
      debugLog('‚ùå OGG conversion failed', err.message);
      // Fallback: Return original WebM with OGG extension
      const oggBlob = new Blob([audioBuffer], { type: 'audio/ogg; codecs=opus' });
      return oggBlob;
    } finally {
      setIsProcessing(false);
    }
  };

  const startRecording = async () => {
    try {
      debugLog('üé¨ Starting recording...');
      setError(null);
      cleanup();

      if (permissionStatus !== 'granted') {
        const granted = await checkAndRequestPermissions();
        if (!granted) return;
      }

      const stream = await navigator.mediaDevices.getUserMedia({
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true,
          sampleRate: 48000,
          channelCount: 1,
        }
      });

      streamRef.current = stream;
      setAudioStream(stream);

      // Audio visualization
      const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
      if (audioCtx.state === 'suspended') await audioCtx.resume();

      const source = audioCtx.createMediaStreamSource(stream);
      const analyser = audioCtx.createAnalyser();
      analyser.fftSize = 256;
      source.connect(analyser);

      audioContextRef.current = audioCtx;
      analyserRef.current = analyser;
      dataArrayRef.current = new Uint8Array(analyser.frequencyBinCount);

      // Force OGG Opus if supported, otherwise WebM
      let mimeType = 'audio/webm; codecs=opus';
      if (MediaRecorder.isTypeSupported('audio/ogg; codecs=opus')) {
        mimeType = 'audio/ogg; codecs=opus';
      }

      const mediaRecorder = new MediaRecorder(stream, {
        mimeType,
        audioBitsPerSecond: 64000
      });

      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.onstart = () => {
        debugLog('üé¨ Recording active');
        setIsRecording(true);
        drawWaveform();
      };

      mediaRecorder.ondataavailable = (event) => {
        if (event.data?.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = async () => {
        debugLog('‚èπÔ∏è Processing recording...');

        if (audioChunksRef.current.length > 0) {
          const rawBlob = new Blob(audioChunksRef.current, { type: mimeType });

          // Create WhatsApp-compatible version
          let finalBlob;
          if (mimeType.includes('ogg')) {
            finalBlob = rawBlob; // Already OGG
          } else {
            // Convert WebM to OGG
            try {
              const audioContext = new AudioContext();
              const arrayBuffer = await rawBlob.arrayBuffer();
              const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);
              finalBlob = await createWhatsAppCompatibleAudio(audioBuffer);
            } catch (err) {
              debugLog('‚ö†Ô∏è Using original format', err.message);
              finalBlob = rawBlob;
            }
          }

          await createRecording(finalBlob);
        } else {
          setError('No audio data captured');
        }

        audioChunksRef.current = [];
      };

      mediaRecorder.onerror = (event) => {
        debugLog('‚ùå Recording error', event.error);
        setError(`Recording failed: ${event.error?.message}`);
        cleanup();
      };

      mediaRecorder.start(100);

    } catch (err) {
      debugLog('üí• Recording failed', err);
      setError(`Failed to start: ${err.message}`);
      cleanup();
    }
  };

  const stopRecording = () => {
    debugLog('‚èπÔ∏è Stopping recording...');
    if (mediaRecorderRef.current?.state === 'recording') {
      mediaRecorderRef.current.stop();
    }
    setIsRecording(false);
    setTimeout(cleanup, 1000);
  };

  const createRecording = async (blob) => {
    const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, -5);
    const filename = `whatsapp-voice-${timestamp}.ogg`;

    const url = URL.createObjectURL(blob);

    const recording = {
      id: Date.now(),
      url,
      blob,
      filename,
      duration: recordingTime,
      timestamp: new Date().toLocaleTimeString(),
      size: blob.size,
      format: blob.type.includes('ogg') ? 'OGG Opus' : 'WebM Opus'
    };

    setRecordings(prev => [...prev, recording]);
    debugLog('‚úÖ Recording created', { filename, size: blob.size, type: blob.type });
  };

  // FIXED: Proper drag with visual feedback
  const handleDragStart = (e, recording) => {
    setDraggedItem(recording.id);

    // Proper file drag data
    e.dataTransfer.setData('DownloadURL', 
      `${recording.blob.type}:${recording.filename}:${recording.url}`);
    e.dataTransfer.setData('text/plain', recording.filename);
    e.dataTransfer.effectAllowed = 'copy';

    debugLog('üéØ Drag started', { filename: recording.filename });
  };

  const handleDragEnd = () => {
    setDraggedItem(null);
  };

  const downloadRecording = async (recording) => {
    try {
      if ('showSaveFilePicker' in window) {
        const fileHandle = await window.showSaveFilePicker({
          suggestedName: recording.filename,
          types: [{
            description: 'WhatsApp Voice Messages',
            accept: { 'audio/ogg': ['.ogg'] }
          }]
        });

        const writable = await fileHandle.createWritable();
        await writable.write(recording.blob);
        await writable.close();

        debugLog('‚úÖ File saved via File System API');
      } else {
        const link = document.createElement('a');
        link.href = recording.url;
        link.download = recording.filename;
        link.click();
        debugLog('‚úÖ File downloaded');
      }
    } catch (err) {
      if (err.name !== 'AbortError') {
        setError(`Download failed: ${err.message}`);
      }
    }
  };

  const removeRecording = (index) => {
    setRecordings(prev => {
      const recording = prev[index];
      URL.revokeObjectURL(recording.url);
      return prev.filter((_, i) => i !== index);
    });
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  return (
    <div className="bg-[#ece5dd] min-h-screen p-4 space-y-4">
      {/* Compact status bar */}
      <div className="flex items-center justify-between text-sm bg-white/70 px-4 py-2 rounded-lg">
        <span className="flex items-center space-x-2">
          <span>üéôÔ∏è</span>
          <span>{permissionStatus === 'granted' ? '‚úÖ Ready' : 
                 permissionStatus === 'denied' ? '‚ùå Denied' : '‚è≥ Checking...'}</span>
        </span>
        {isRecording && (
          <span className="text-red-600 font-medium animate-pulse flex items-center space-x-1">
            <span className="w-2 h-2 bg-red-500 rounded-full"></span>
            <span>REC {recordingTime}s</span>
          </span>
        )}
        {isProcessing && (
          <span className="text-blue-600 flex items-center space-x-1">
            <span className="animate-spin">‚öôÔ∏è</span>
            <span>Processing...</span>
          </span>
        )}
      </div>

      {/* Error display */}
      {error && (
        <div className="bg-red-50 border-l-4 border-red-400 p-3 rounded flex justify-between items-center">
          <span className="text-red-700 text-sm">{error}</span>
          <button 
            onClick={() => setError(null)}
            className="text-red-400 hover:text-red-600 font-bold"
          >
            √ó
          </button>
        </div>
      )}

      {/* Recording controls */}
      <div className="flex justify-center">
        {!isRecording ? (
          <button
            onClick={startRecording}
            disabled={permissionStatus !== 'granted'}
            className={`flex items-center space-x-2 px-6 py-3 rounded-full shadow-lg transition-all ${
              permissionStatus !== 'granted' 
                ? 'bg-gray-300 text-gray-500 cursor-not-allowed' 
                : 'bg-white border-2 border-[#25D366] hover:bg-[#25D366] hover:text-white text-[#25D366] hover:scale-105'
            }`}
          >
            <span>üéôÔ∏è</span>
            <span className="font-medium">Record</span>
          </button>
        ) : (
          <button
            onClick={stopRecording}
            className="flex items-center space-x-2 bg-red-500 hover:bg-red-600 text-white px-6 py-3 rounded-full shadow-lg relative transition-all hover:scale-105"
          >
            <span>‚èπÔ∏è</span>
            <span className="font-medium">Stop ({recordingTime}s)</span>
            <span className="absolute -top-1 -right-1 w-3 h-3 bg-red-300 rounded-full animate-ping"></span>
          </button>
        )}
      </div>

      {/* Waveform */}
      {isRecording && (
        <div className="bg-white p-4 rounded-lg shadow-md max-w-2xl mx-auto">
          <canvas
            ref={canvasRef}
            width={500}
            height={80}
            className="w-full border rounded"
          />
          <div className="text-center text-sm text-gray-600 mt-2">
            üéµ Recording... {recordingTime}s
          </div>
        </div>
      )}

      {/* FIXED: WhatsApp-style messages with proper width constraints */}
      <div className="space-y-3 max-w-4xl mx-auto">
        {recordings.map((rec, index) => (
          <div
            key={rec.id}
            draggable
            onDragStart={(e) => handleDragStart(e, rec)}
            onDragEnd={handleDragEnd}
            className={`flex items-center space-x-3 bg-[#dcf8c6] p-3 rounded-xl shadow-md hover:shadow-lg transition-all cursor-move max-w-lg ml-auto border-l-4 border-[#25D366] ${
              draggedItem === rec.id ? 'opacity-50 scale-95' : 'opacity-100 scale-100'
            }`}
          >
            <img
              src="https://www.gravatar.com/avatar?d=mp"
              alt="Avatar"
              className="w-10 h-10 rounded-full flex-shrink-0"
              draggable={false}
            />

            <div className="flex-1 min-w-0">
              <audio controls src={rec.url} className="w-full mb-1" />
              <div className="text-xs text-gray-500 flex justify-between items-center">
                <span className="flex items-center space-x-2">
                  <span>‚è±Ô∏è {rec.duration}s</span>
                  <span>üìÅ {formatFileSize(rec.size)}</span>
                  <span className="text-green-600 font-medium">{rec.format}</span>
                </span>
                <span>{rec.timestamp}</span>
              </div>
              <div className="text-xs text-gray-400 mt-1 truncate">
                {rec.filename}
              </div>
            </div>

            <div className="flex space-x-1 flex-shrink-0">
              <button
                onClick={() => downloadRecording(rec)}
                className="text-green-700 hover:text-green-900 p-1 bg-white rounded shadow-sm hover:shadow-md transition-all"
                title="Save file"
              >
                üíæ
              </button>
              <button
                onClick={() => removeRecording(index)}
                className="text-red-700 hover:text-red-900 p-1 bg-white rounded shadow-sm hover:shadow-md transition-all"
                title="Delete"
              >
                üóëÔ∏è
              </button>
            </div>
          </div>
        ))}
      </div>

      {recordings.length === 0 && !isRecording && (
        <div className="text-center text-gray-500 py-12">
          <div className="text-5xl mb-3">üé§</div>
          <h3 className="text-lg font-medium mb-2">Ready to Record</h3>
          <p className="text-sm">Create voice messages for WhatsApp</p>
        </div>
      )}

      {/* Realistic usage guide */}
      {recordings.length > 0 && (
