import React, { useState, useRef, useEffect, useCallback } from "react";

export default function WhatsAppAudioRecorder() {
  const [recordings, setRecordings] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [audioStream, setAudioStream] = useState(null);
  const [permissionStatus, setPermissionStatus] = useState('checking');
  const [error, setError] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [processingStatus, setProcessingStatus] = useState('');
  const [workingDirectory, setWorkingDirectory] = useState(null);
  const [supportedFormats, setSupportedFormats] = useState([]);
  const [selectedFormat, setSelectedFormat] = useState(null);

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const timerRef = useRef(null);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const dataArrayRef = useRef(null);
  const animationRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  const debugLog = (message, data = null) => {
    console.log(`üé§ ${message}`, data);
  };

  // STRATEGIC: Detect all supported MediaRecorder formats
  const detectSupportedFormats = () => {
    const formats = [
      { mimeType: 'audio/webm; codecs=opus', name: 'WebM Opus', priority: 1 },
      { mimeType: 'audio/webm', name: 'WebM', priority: 2 },
      { mimeType: 'audio/ogg; codecs=opus', name: 'OGG Opus', priority: 3 },
      { mimeType: 'audio/ogg', name: 'OGG', priority: 4 },
      { mimeType: 'audio/mp4', name: 'MP4', priority: 5 },
      { mimeType: 'audio/wav', name: 'WAV', priority: 6 },
      { mimeType: 'audio/mpeg', name: 'MP3', priority: 7 }
    ];

    const supported = formats.filter(format => 
      MediaRecorder.isTypeSupported(format.mimeType)
    ).sort((a, b) => a.priority - b.priority);

    setSupportedFormats(supported);

    if (supported.length > 0) {
      setSelectedFormat(supported[0]);
      debugLog('‚úÖ MediaRecorder formats detected', {
        supported: supported.map(f => f.name),
        selected: supported[0].name
      });
    } else {
      setError('No supported audio recording formats found');
      debugLog('‚ùå No MediaRecorder support detected');
    }

    return supported;
  };

  // STRATEGIC: Setup system FFmpeg integration with comprehensive batch scripts
  const setupSystemFFmpeg = async () => {
    try {
      if ('showDirectoryPicker' in window) {
        const dirHandle = await window.showDirectoryPicker({
          mode: 'readwrite',
          startIn: 'downloads'
        });
        setWorkingDirectory(dirHandle);

        // Create comprehensive FFmpeg setup script
        const setupScript = `@echo off
echo ========================================
echo WhatsApp Audio Studio - FFmpeg Setup
echo ========================================
echo.
echo Testing FFmpeg installation...
ffmpeg -version >nul 2>&1
if %ERRORLEVEL% EQU 0 (
    echo ‚úÖ FFmpeg is installed and working
    echo.
    ffmpeg -version | findstr "ffmpeg version"
    echo.
    echo Ready to process WhatsApp audio files!
    echo.
    echo USAGE:
    echo 1. Record audio in the web app
    echo 2. Run the generated process_audio_[timestamp].bat files
    echo 3. Use the output .ogg or .m4a files in WhatsApp
    echo.
) else (
    echo ‚ùå FFmpeg not found in PATH
    echo.
    echo INSTALLATION REQUIRED:
    echo 1. Download FFmpeg from: https://ffmpeg.org/download.html
    echo 2. Extract to C:\\ffmpeg\\
    echo 3. Add C:\\ffmpeg\\bin to your PATH environment variable
    echo 4. Restart this application
    echo.
)
echo ========================================
pause`;

        const setupBlob = new Blob([setupScript], { type: 'text/plain' });
        const setupHandle = await dirHandle.getFileHandle('00_setup_ffmpeg.bat', { create: true });
        const setupWritable = await setupHandle.createWritable();
        await setupWritable.write(setupBlob);
        await setupWritable.close();

        // Create README file
        const readme = `# WhatsApp Audio Studio - System FFmpeg Integration

## How it works:
1. Record audio in the web browser
2. Raw audio files are saved here as .webm
3. FFmpeg batch scripts are automatically generated
4. Run the batch scripts to create WhatsApp-compatible files

## Required Software:
- FFmpeg (download from https://ffmpeg.org)
- Add FFmpeg to your system PATH

## Generated Files:
- input_[timestamp].webm - Raw recorded audio
- process_audio_[timestamp].bat - FFmpeg processing script
- whatsapp_voice_[timestamp].ogg - Voice message (16kHz mono, 32kbps)
- whatsapp_music_[timestamp].m4a - Music file (44.1kHz stereo, 128kbps, faststart)

## WhatsApp Compatibility:
‚úÖ Voice messages: Upload .ogg files to WhatsApp for instant voice playback
‚úÖ Music files: Upload .m4a files to WhatsApp for high-quality music playback

## Troubleshooting:
- Run 00_setup_ffmpeg.bat to test your FFmpeg installation
- Make sure FFmpeg is in your system PATH
- Use Windows Command Prompt or PowerShell to run batch files`;

        const readmeBlob = new Blob([readme], { type: 'text/plain' });
        const readmeHandle = await dirHandle.getFileHandle('README.md', { create: true });
        const readmeWritable = await readmeHandle.createWritable();
        await readmeWritable.write(readmeBlob);
        await readmeWritable.close();

        debugLog('‚úÖ System FFmpeg integration setup complete', {
          directory: dirHandle.name,
          files: ['00_setup_ffmpeg.bat', 'README.md']
        });

        return true;
      }
    } catch (err) {
      if (err.name !== 'AbortError') {
        debugLog('‚ö†Ô∏è System FFmpeg setup declined', err.message);
        setError('File system access required for FFmpeg integration');
      }
      return false;
    }
  };

  // STRATEGIC: Create comprehensive FFmpeg processing scripts
  const createFFmpegProcessingScripts = async (webmBlob, timestamp) => {
    if (!workingDirectory) return null;

    try {
      setProcessingStatus('Creating FFmpeg processing scripts...');

      const inputFile = `input_${timestamp}.webm`;
      const voiceOutput = `whatsapp_voice_${timestamp}.ogg`;
      const musicOutput = `whatsapp_music_${timestamp}.m4a`;
      const processScript = `process_audio_${timestamp}.bat`;

      // Write input WebM file
      const inputHandle = await workingDirectory.getFileHandle(inputFile, { create: true });
      const inputWritable = await inputHandle.createWritable();
      await inputWritable.write(webmBlob);
      await inputWritable.close();

      // Create comprehensive processing script
      const ffmpegScript = `@echo off
echo ========================================
echo WhatsApp Audio Processing - ${new Date().toLocaleString()}
echo ========================================
echo.
echo Input file: ${inputFile}
echo Processing audio for WhatsApp compatibility...
echo.

REM Check if FFmpeg is available
ffmpeg -version >nul 2>&1
if %ERRORLEVEL% NEQ 0 (
    echo ‚ùå ERROR: FFmpeg not found in PATH
    echo Please install FFmpeg and add it to your PATH
    echo Download from: https://ffmpeg.org/download.html
    echo.
    pause
    exit /b 1
)

echo ‚úÖ FFmpeg found, starting processing...
echo.

REM Create WhatsApp Voice Message (OGG Opus)
echo üé§ Creating voice message: ${voiceOutput}
ffmpeg -i "${inputFile}" ^
    -c:a libopus ^
    -b:a 32k ^
    -vbr on ^
    -compression_level 10 ^
    -application voip ^
    -ac 1 ^
    -ar 16000 ^
    -f ogg ^
    "${voiceOutput}" ^
    -y

if %ERRORLEVEL% EQU 0 (
    echo ‚úÖ Voice message created successfully
) else (
    echo ‚ùå Voice message creation failed
)
echo.

REM Create WhatsApp Music File (M4A AAC with faststart)
echo üéµ Creating music file: ${musicOutput}
ffmpeg -i "${inputFile}" ^
    -c:a aac ^
    -b:a 128k ^
    -movflags +faststart ^
    -ac 2 ^
    -ar 44100 ^
    "${musicOutput}" ^
    -y

if %ERRORLEVEL% EQU 0 (
    echo ‚úÖ Music file created successfully
) else (
    echo ‚ùå Music file creation failed
)
echo.

REM Show file information
if exist "${voiceOutput}" (
    echo üìä Voice file info:
    ffprobe -v quiet -show_format -show_streams "${voiceOutput}" | findstr "codec_name\\|sample_rate\\|channels\\|bit_rate\\|duration"
    echo.
)

if exist "${musicOutput}" (
    echo üìä Music file info:
    ffprobe -v quiet -show_format -show_streams "${musicOutput}" | findstr "codec_name\\|sample_rate\\|channels\\|bit_rate\\|duration"
    echo.
)

echo ========================================
echo Processing Complete!
echo.
echo Files created:
if exist "${voiceOutput}" echo ‚úÖ ${voiceOutput} - Voice message for WhatsApp
if exist "${musicOutput}" echo ‚úÖ ${musicOutput} - Music file for WhatsApp
echo.
echo Usage:
echo 1. Upload ${voiceOutput} to WhatsApp for voice messages
echo 2. Upload ${musicOutput} to WhatsApp for music sharing
echo 3. Both files are optimized for instant playback
echo.
echo Note: You can now delete ${inputFile} if desired
echo ========================================
pause`;

      // Write processing script
      const scriptBlob = new Blob([ffmpegScript], { type: 'text/plain' });
      const scriptHandle = await workingDirectory.getFileHandle(processScript, { create: true });
      const scriptWritable = await scriptHandle.createWritable();
      await scriptWritable.write(scriptBlob);
      await scriptWritable.close();

      debugLog('‚úÖ FFmpeg processing scripts created', {
        input: inputFile,
        script: processScript,
        outputs: [voiceOutput, musicOutput]
      });

      return {
        inputFile,
        processScript,
        voiceOutput,
        musicOutput,
        timestamp,
        directory: workingDirectory.name
      };

    } catch (err) {
      debugLog('‚ùå FFmpeg script creation failed', err.message);
      throw err;
    }
  };

  const checkAndRequestPermissions = async () => {
    try {
      setPermissionStatus('checking');

      const testStream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: false,
          noiseSuppression: false,
          autoGainControl: false,
          sampleRate: 48000,
          channelCount: 2,
        } 
      });

      debugLog('‚úÖ Microphone access granted');
      testStream.getTracks().forEach(track => track.stop());
      setPermissionStatus('granted');

      // Detect supported formats after getting permission
      detectSupportedFormats();

      return true;

    } catch (err) {
      debugLog('‚ùå Permissions denied', err.message);
      setPermissionStatus('denied');
      setError(`Microphone required: ${err.message}`);
      return false;
    }
  };

  useEffect(() => {
    checkAndRequestPermissions();
  }, []);

  const cleanup = useCallback(() => {
    [timerRef, animationRef].forEach(ref => {
      if (ref.current) {
        ref === timerRef ? clearInterval(ref.current) : cancelAnimationFrame(ref.current);
        ref.current = null;
      }
    });

    if (mediaRecorderRef.current?.state !== 'inactive') {
      try {
        mediaRecorderRef.current?.stop();
      } catch (e) {
        debugLog('‚ö†Ô∏è MediaRecorder cleanup', e.message);
      }
      mediaRecorderRef.current = null;
    }

    if (audioContextRef.current?.state !== 'closed') {
      audioContextRef.current?.close();
      audioContextRef.current = null;
    }

    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
      setAudioStream(null);
    }

    setIsRecording(false);
    setRecordingTime(0);
  }, []);

  useEffect(() => cleanup, [cleanup]);

  useEffect(() => {
    if (isRecording) {
      timerRef.current = setInterval(() => setRecordingTime(prev => prev + 1), 1000);
    } else {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
      setRecordingTime(0);
    }
    return () => timerRef.current && clearInterval(timerRef.current);
  }, [isRecording]);

  const drawWaveform = useCallback(() => {
    if (!canvasRef.current || !analyserRef.current || !isRecording) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const analyser = analyserRef.current;
    const dataArray = dataArrayRef.current;

    const draw = () => {
      if (!isRecording) return;
      animationRef.current = requestAnimationFrame(draw);
      analyser.getByteFrequencyData(dataArray);

      ctx.fillStyle = "#f0f2f5";
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      const barWidth = (canvas.width / dataArray.length) * 2.5;
      let x = 0;

      for (let i = 0; i < dataArray.length; i++) {
        const barHeight = (dataArray[i] / 255) * canvas.height * 0.9;
        const gradient = ctx.createLinearGradient(0, canvas.height - barHeight, 0, canvas.height);
        gradient.addColorStop(0, '#25D366');
        gradient.addColorStop(0.5, '#128C7E');
        gradient.addColorStop(1, '#0d7377');
        ctx.fillStyle = gradient;
        ctx.fillRect(x, canvas.height - barHeight, barWidth - 1, barHeight);
        x += barWidth;
      }
    };
    draw();
  }, [isRecording]);

  const startRecording = async () => {
    try {
      debugLog('üé¨ Starting professional recording...');
      setError(null);
      cleanup();

      if (permissionStatus !== 'granted') {
        const granted = await checkAndRequestPermissions();
        if (!granted) return;
      }

      if (!selectedFormat) {
        setError('No supported recording format available');
        return;
      }

      // Professional audio recording setup
      const stream = await navigator.mediaDevices.getUserMedia({
        audio: {
          echoCancellation: false,    // Raw audio for FFmpeg processing
          noiseSuppression: false,    // Let FFmpeg handle processing
          autoGainControl: false,     // Maintain consistent levels
          sampleRate: 48000,          // High quality source
          channelCount: 2,            // Stereo for best quality
          latency: 0.01
        }
      });

      streamRef.current = stream;
      setAudioStream(stream);

      // Audio visualization
      const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
      if (audioCtx.state === 'suspended') await audioCtx.resume();

      const source = audioCtx.createMediaStreamSource(stream);
      const analyser = audioCtx.createAnalyser();
      analyser.fftSize = 512;
      analyser.smoothingTimeConstant = 0.3;
      source.connect(analyser);

      audioContextRef.current = audioCtx;
      analyserRef.current = analyser;
      dataArrayRef.current = new Uint8Array(analyser.frequencyBinCount);

      // Use the best supported format
      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: selectedFormat.mimeType,
        audioBitsPerSecond: 320000 // Maximum quality for FFmpeg processing
      });

      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.onstart = () => {
        debugLog('üé¨ Recording started', { format: selectedFormat.name });
        setIsRecording(true);
        drawWaveform();
      };

      mediaRecorder.ondataavailable = (event) => {
        if (event.data?.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = async () => {
        debugLog('‚èπÔ∏è Processing recorded audio...');
        setIsProcessing(true);

        if (audioChunksRef.current.length > 0) {
          const rawBlob = new Blob(audioChunksRef.current, { 
            type: selectedFormat.mimeType 
          });

          try {
            const timestamp = Date.now();

            // Create browser preview
            await createBrowserPreview(rawBlob, selectedFormat.name);

            // Create FFmpeg processing scripts if directory is available
            if (workingDirectory) {
              const ffmpegInfo = await createFFmpegProcessingScripts(rawBlob, timestamp);
              await createFFmpegRecord(ffmpegInfo);
            }

          } catch (err) {
            debugLog('‚ö†Ô∏è Processing failed', err.message);
            setError(`Processing failed: ${err.message}`);
          }
        } else {
          setError('No audio data captured');
        }

        setIsProcessing(false);
        setProcessingStatus('');
        audioChunksRef.current = [];
      };

      mediaRecorder.onerror = (event) => {
        debugLog('‚ùå Recording error', event.error);
        setError(`Recording failed: ${event.error?.message}`);
        cleanup();
      };

      mediaRecorder.start(50); // High frequency data collection

    } catch (err) {
      debugLog('üí• Recording failed', err);
      setError(`Failed to start: ${err.message}`);
      cleanup();
    }
  };

  const stopRecording = () => {
    debugLog('‚èπÔ∏è Stopping recording...');
    if (mediaRecorderRef.current?.state === 'recording') {
      mediaRecorderRef.current.stop();
    }
    setIsRecording(false);
    setTimeout(cleanup, 1000);
  };

  const createBrowserPreview = async (blob, formatName) => {
    const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, -5);
    const extension = blob.type.includes('webm') ? 'webm' : 
                     blob.type.includes('ogg') ? 'ogg' : 'audio';
    const filename = `browser-preview-${timestamp}.${extension}`;

    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = filename;
    link.style.display = 'none';
    document.body.appendChild(link);

    const recording = {
      id: Date.now(),
      url,
      blob,
      filename,
      duration: recordingTime,
      timestamp: new Date().toLocaleTimeString(),
      size: blob.size,
      type: 'browser',
      description: `Browser Preview (${formatName})`,
      downloadLink: link,
      isSystemProcessed: false
    };

    setRecordings(prev => [...prev, recording]);
    debugLog(`‚úÖ Browser preview created`, { filename, format: formatName });
  };

  const createFFmpegRecord = async (ffmpegInfo) => {
    const recording = {
      id: Date.now() + 1,
      filename: ffmpegInfo.processScript,
      duration: recordingTime,
      timestamp: new Date().toLocaleTimeString(),
      type: 'ffmpeg',
      description: 'System FFmpeg Processing',
      isSystemProcessed: true,
      ffmpegInfo
    };

    setRecordings(prev => [...prev, recording]);
    debugLog(`‚úÖ FFmpeg processing record created`, ffmpegInfo);
  };

  const downloadRecording = (recording) => {
    if (recording.downloadLink) {
      recording.downloadLink.click();
      debugLog('‚úÖ File downloaded', recording.filename);
    }
  };

  const removeRecording = (index) => {
    setRecordings(prev => {
      const recording = prev[index];
      if (recording.url) {
        URL.revokeObjectURL(recording.url);
      }
      if (recording.downloadLink && document.body.contains(recording.downloadLink)) {
        document.body.removeChild(recording.downloadLink);
      }
      return prev.filter((_, i) => i !== index);
    });
  };

  const formatFileSize = (bytes) => {
    if (!bytes) return 'Processing...';
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  const getRecordingIcon = (type) => {
    switch (type) {
      case 'browser': return 'üåê';
      case 'ffmpeg': return '‚öôÔ∏è';
      default: return 'üìº';
    }
  };

  return (
    <div className="bg-[#ece5dd] min-h-screen p-4 space-y-4">
      {/* Professional header */}
      <div className="bg-gradient-to-r from-[#075e54] via-[#128C7E] to-[#25D366] text-white p-6 rounded-lg shadow-xl">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="w-12 h-12 bg-white/20 rounded-full flex items-center justify-center">
              <span className="text-2xl">üé§</span>
            </div>
            <div>
              <h1 className="text-2xl font-bold">WhatsApp Audio Studio Pro</h1>
              <p className="text-sm opacity-90">Professional FFmpeg integration for WhatsApp compatibility</p>
            </div>
          </div>
          <div className="text-right text-sm">
            <div className="font-medium">
              {permissionStatus === 'granted' ? '‚úÖ Ready' : 
               permissionStatus === 'denied' ? '‚ùå Access Required' : '‚è≥ Initializing...'}
            </div>
            <div className="opacity-75">
              Format: {selectedFormat?.name || 'Detecting...'}
            </div>
            {workingDirectory && (
              <div className="text-xs opacity-75">üìÅ {workingDirectory.name}</div>
            )}
          </div>
        </div>
      </div>

      {/* Format compatibility status */}
      {supportedFormats.length > 0 && (
        <div className="bg-blue-50 border border-blue-200 p-4 rounded-lg">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="font-semibold text-blue-800">Browser Audio Support</h3>
              <p className="text-sm text-blue-600">
                Detected: {supportedFormats.map(f => f.name).join(', ')} ‚Ä¢ 
                Using: <strong>{selectedFormat?.name}</strong>
              </p>
            </div>
            <div className="text-blue-600">
              {supportedFormats.length} format{supportedFormats.length !== 1 ? 's' : ''} supported
            </div>
          </div>
        </div>
      )}

      {/* System FFmpeg setup */}
      {!workingDirectory && (
        <div className="bg-amber-50 border border-amber-200 p-6 rounded-lg">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <span className="text-3xl">‚öôÔ∏è</span>
              <div>
                <h3 className="font-bold text-amber-800">System FFmpeg Integration Required</h3>
                <p className="text-sm text-amber-600">
                  For guaranteed WhatsApp compatibility, enable system FFmpeg processing
                </p>
                <p className="text-xs text-amber-500 mt-1">
                  Creates professional-grade audio files with exact WhatsApp specifications
                </p>
              </div>
            </div>
            <button
              onClick={setupSystemFFmpeg}
              className="px-6 py-3 bg-amber-600 hover:bg-amber-700 text-white rounded-lg font-medium transition-colors shadow-md"
            >
              Setup FFmpeg Integration
            </button>
          </div>
        </div>
      )}

      {/* Processing status */}
      {(isProcessing || processingStatus) && (
        <div className="bg-green-50 border border-green-200 p-4 rounded-lg">
          <div className="flex items-center space-x-3">
            <div className="animate-spin text-green-600 text-xl">‚öôÔ∏è</div>
            <div>
              <div className="font-semibold text-green-800">Processing Audio</div>
              <div className="text-sm text-green-600">{processingStatus || 'Creating files...'}</div>
            </div>
          </div>
        </div>
      )}

      {/* Recording status */}
      {isRecording && (
        <div className="bg-red-50 border border-red-200 p-4 rounded-lg">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <span className="w-4 h-4 bg-red-500 rounded-full animate-pulse"></span>
              <span className="font-semibold text-red-800">
                Recording with {selectedFormat?.name} ‚Ä¢ Ultra-High Quality
              </span>
            </div>
            <span className="text-red-600 font-mono text-xl">{recordingTime}s</span>
          </div>
        </div>
      )}

      {/* Error display */}
      {error && (
        <div className="bg-red-50 border-l-4 border-red-400 p-4 rounded flex justify-between items-start">
          <span className="text-red-700">{error}</span>
          <button 
            onClick={() => setError(null)}
            className="text-red-400 hover:text-red-600 font-bold text-xl ml-4"
          >
            √ó
          </button>
        </div>
      )}

      {/* Recording controls */}
      <div className="flex justify-center">
        {!isRecording ? (
          <button
            onClick={startRecording}
            disabled={permissionStatus !== 'granted' || !selectedFormat}
            className={`flex items-center space-x-4 px-10 py-5 rounded-full shadow-2xl transition-all transform hover:scale-105 ${
              permissionStatus !== 'granted' || !selectedFormat
                ? 'bg-gray-300 text-gray-500 cursor-not-allowed' 
                : 'bg-gradient-to-r from-[#25D366] via-[#128C7E] to-[#075e54] hover:from-[#128C7E] hover:to-[#075e54] text-white'
            }`}
          >
            <span className="text-3xl">üé§</span>
            <div>
              <div className="font-bold text-lg">Start Recording</div>
              <div className="text-sm opacity-90">
                {selectedFormat ? `${selectedFormat.name} ‚Ä¢ 320kbps` : 'Format detection...'}
              </div>
            </div>
          </button>
        ) : (
          <button
            onClick={stopRecording}
            className="flex items-center space-x-4 bg-red-500 hover:bg-red-600 text-white px-10 py-5 rounded-full shadow-2xl relative transition-all transform hover:scale-105"
          >
            <span className="text-3xl">‚èπÔ∏è</span>
            <div>
              <div className="font-bold text-lg">Stop Recording</div>
              <div className="text-sm opacity-90">{recordingTime} seconds</div>
            </div>
            <span className="absolute -top-3 -right-3 w-6 h-6 bg-red-300 rounded-full animate-ping"></span>
          </button>
        )}
      </div>

      {/* Waveform visualization */}
      {isRecording && (
        <div className="bg-white p-6 rounded-xl shadow-lg max-w-5xl mx-auto">
          <canvas
            ref={canvasRef}
            width={900}
            height={140}
            className="w-full border-2 border-gray-200 rounded-lg"
          />
          <div className="text-center text-gray-600 mt-4 flex items-center justify-center space-x-3">
            <span className="animate-pulse text-2xl">üéµ</span>
            <span className="font-semibold text-lg">Professional Audio Capture ‚Ä¢ {recordingTime}s</span>
            <span className="text-sm bg-gray-100 px-2 py-1 rounded">{selectedFormat?.name}</span>
          </div>
        </div>
      )}

      {/* Recordings display */}
      <div className="grid gap-4 max-w-6xl mx-auto">
        {recordings.map((rec, index) => (
          <div
            key={rec.id}
            className="flex items-center space-x-6 bg-white p-6 rounded-xl shadow-lg hover:shadow-xl transition-all border-l-4 border-[#25D366]"
          >
            <div className="w-16 h-16 bg-gradient-to-br from-[#25D366] to-[#075e54] rounded-full flex items-center justify-center flex-shrink-0 text-white text-3xl shadow-lg">
              {getRecordingIcon(rec.type)}
            </div>

            <div className="flex-1 min-w-0">
              <div className="flex items-center space-x-3 mb-3">
                <span className={`px-4 py-2 rounded-full text-sm font-semibold ${
                  rec.type === 'ffmpeg' ? 'bg-purple-50 text-purple-600' : 'bg-blue-50 text-blue-600'
                }`}>
                  {rec.description}
                </span>
                {rec.type === 'ffmpeg' && (
                  <span className="text-xs bg-green-100 text-green-600 px-2 py-1 rounded font-medium">
                    ‚úÖ WhatsApp Compatible
                  </span>
                )}
              </div>

              {rec.url && <audio controls src={rec.url} className="w-full mb-3" />}

              <div className="flex justify-between items-center text-sm text-gray-600">
                <div className="flex items-center space-x-4">
                  <span>‚è±Ô∏è {rec.duration}s</span>
                  {rec.size && <span>üìÅ {formatFileSize(rec.size)}</span>}
                </div>
                <span>{rec.timestamp}</span>
              </div>

              {rec.isSystemProcessed && rec.ffmpegInfo && (
                <div className="mt-3 p-4 bg-purple-50 border border-purple-200 rounded text-sm">
                  <div className="font-semibold text-purple-800 mb-2">üìÅ System FFmpeg Processing:</div>
                  <div className="text-purple-600 space-y-1">
                    <div><strong>Directory:</strong> {rec.ffmpegInfo.directory}</div>
                    <div><strong>Process Script:</strong> <code className="bg-purple-100 px-1 rounded">{rec.ffmpegInfo.processScript}</code></div>
                    <div><strong>Outputs:</strong></div>
                    <ul className="ml-4 space-y-1">
                      <li>‚Ä¢ <code className="bg-purple-100 px-1 rounded">{rec.ffmpegInfo.voiceOutput}</code> - WhatsApp voice message</li>
                      <li>‚Ä¢ <code className="bg-purple-100 px-1 rounded">{rec.ffmpegInfo.musicOutput}</code> - WhatsApp music file</li>
                    </ul>
                    <div className="mt-2 p-2 bg-purple-100 rounded border border-purple-300">
                      <strong>Instructions:</strong>
                      <ol className="list-decimal list-inside mt-1 space-y-1">
                        <li>Navigate to: <strong>{rec.ffmpegInfo.directory}</strong></li>
                        <li>Double-click: <strong>{rec.ffmpegInfo.processScript}</strong></li>
                        <li>Wait for FFmpeg processing to complete</li>
                        <li>Upload the .ogg/.m4a files to WhatsApp</li>
                      </ol>
                    </div>
                  </div>
                </div>
              )}

              <div className="text-xs text-gray-400 mt-2 font-mono truncate">
                üì± {rec.filename}
              </div>
            </div>

            <div className="flex space-x-3 flex-shrink-0">
              {rec.downloadLink && (
                <button
                  onClick={() => downloadRecording(rec)}
                  className="px-5 py-3 bg-[#25D366] hover:bg-[#128C7E] text-white rounded-lg font-medium transition-all shadow-md hover:shadow-lg"
                >
                  üíæ Save
                </button>
              )}
              <button
                onClick={() => removeRecording(index)}
                className="px-5 py-3 bg-red-500 hover:bg-red-600 text-white rounded-lg font-medium transition-all shadow-md hover:shadow-lg"
              >
                üóëÔ∏è Delete
              </button>
            </div>
          </div>
        ))}
      </div>

      {recordings.length === 0 && !isRecording && (
        <div className="text-center text-gray-500 py-20 max-w-2xl mx-auto">
          <div className="text-8xl mb-6">üé§</div>
          <h3 className="text-3xl font-bold mb-4">Professional WhatsApp Audio Studio</h3>
          <p className="text-lg mb-3">System FFmpeg integration for guaranteed compatibility</p>
          <p className="text-sm text-gray-400">
            Records in {selectedFormat?.name || 'best available format'} and processes with professional FFmpeg
          </p>
        </div>
      )}

      {/* Comprehensive usage guide */}
      {recordings.length > 0 && (
        <div className="bg-gradient-to-r from-green-50 via-blue-50 to-purple-50 border border-green-200 p-8 rounded-xl max-w-6xl mx-auto">
          <h3 className="font-bold text-xl text-green-800 mb-6 flex items-center space-x-3">
            <span>üéØ</span>
            <span>WhatsApp Integration Workflow</span>
          </h3>

          <div className="grid md:grid-cols-2 gap-6 mb-6">
            <div className="bg-blue-50 p-6 rounded-lg border border-blue-200">
              <h4 className="font-semibold text-blue-700 mb-4 flex items-center space-x-2">
                <span>üåê</span>
                <span>Browser Preview</span>
              </h4>
              <ul className="space-y-2 text-sm text-blue-600">
                <li>‚Ä¢ <strong>Purpose:</strong> Immediate playback for quality check</li>
                <li>‚Ä¢ <strong>Format:</strong> {selectedFormat?.name || 'Auto-detected'} at 320kbps</li>
                <li>‚Ä¢ <strong>Compatibility:</strong> Basic preview only</li>
                <li>‚Ä¢ <strong>Usage:</strong> Test recording quality before processing</li>
              </ul>
            </div>

            <div className="bg-purple-50 p-6 rounded-lg border border-purple-200">
              <h4 className="font-semibold text-purple-700 mb-4 flex items-center space-x-2">
                <span>‚öôÔ∏è</span>
                <span>System FFmpeg Processing</span>
              </h4>
              <ul className="space-y-2 text-sm text-purple-600">
                <li>‚Ä¢ <strong>Voice:</strong> OGG Opus (16kHz mono, 32kbps, VOIP mode)</li>
                <li>‚Ä¢ <strong>Music:</strong> M4A AAC (44.1kHz stereo, 128kbps, faststart)</li>
                <li>‚Ä¢ <strong>Quality:</strong> Professional-grade encoding</li>
                <li>‚Ä¢ <strong>Compatibility:</strong> Guaranteed WhatsApp playback</li>
              </ul>
            </div>
          </div>

          <div className="bg-yellow-50 border border-yellow-200 p-6 rounded-lg">
            <h4 className="font-semibold text-yellow-800 mb-4 flex items-center space-x-2">
              <span>üöÄ</span>
              <span>Step-by-Step WhatsApp Workflow</span>
            </h4>
            <ol className="list-decimal list-inside space-y-3 text-sm text-yellow-700">
              <li>
                <strong>Setup:</strong> Click "Setup FFmpeg Integration" and select a working directory
                <div className="text-xs text-yellow-600 ml-4 mt-1">
                  ‚Üí Installs setup scripts and README in your chosen folder
                </div>
              </li>
              <li>
                <strong>Record:</strong> Click "Start Recording" to capture high-quality audio
                <div className="text-xs text-yellow-600 ml-4 mt-1">
                  ‚Üí Creates both browser preview and FFmpeg processing files
                </div>
              </li>
              <li>
                <strong>Process:</strong> Navigate to your working directory and run the .bat file
                <div className="text-xs text-yellow-600 ml-4 mt-1">
                  ‚Üí FFmpeg creates WhatsApp-compatible .ogg and .m4a files
                </div>
              </li>
              <li>
                <strong>Upload:</strong> Use the processed files in WhatsApp
                <div className="text-xs text-yellow-600 ml-4 mt-1">
                  ‚Üí .ogg files for voice messages, .m4a files for music
                </div>
              </li>
            </ol>
            <div className="mt-4 p-3 bg-yellow-100 rounded border border-yellow-300">
              <p className="text-sm text-yellow-800">
                <strong>üí° Pro Tip:</strong> Run <code>00_setup_ffmpeg.bat</code> first to verify your FFmpeg installation. 
                The system uses identical parameters to professional audio studios for guaranteed compatibility.
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
