import React, { useState, useRef, useEffect, useCallback } from "react";

export default function WhatsAppAudioRecorder() {
  const [recordings, setRecordings] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [audioStream, setAudioStream] = useState(null);
  const [permissionStatus, setPermissionStatus] = useState('checking');
  const [error, setError] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [debugInfo, setDebugInfo] = useState({});

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const timerRef = useRef(null);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const dataArrayRef = useRef(null);
  const animationRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  const debugLog = (message, data = null) => {
    console.log(`ðŸŽ¤ ${message}`, data);
    setDebugInfo(prev => ({
      ...prev,
      [`${Date.now()}`]: { message, data, time: new Date().toLocaleTimeString() }
    }));
  };

  // Strategic permission handling with comprehensive fallbacks
  const checkAndRequestPermissions = async () => {
    try {
      debugLog('ðŸ” Checking microphone permissions...');
      setPermissionStatus('checking');

      const testStream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true,
          sampleRate: 48000,
          channelCount: 1
        } 
      });

      debugLog('âœ… Permission granted', {
        tracks: testStream.getTracks().length,
        settings: testStream.getTracks()[0]?.getSettings()
      });

      testStream.getTracks().forEach(track => track.stop());
      setPermissionStatus('granted');
      return true;

    } catch (err) {
      debugLog('âŒ Permission denied', { error: err.message, name: err.name });
      setPermissionStatus('denied');
      setError(`Microphone access required: ${err.message}`);
      return false;
    }
  };

  useEffect(() => {
    checkAndRequestPermissions();
  }, []);

  const cleanup = useCallback(() => {
    debugLog('ðŸ§¹ Starting cleanup...');

    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }

    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
      animationRef.current = null;
    }

    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
      try {
        mediaRecorderRef.current.stop();
      } catch (e) {
        debugLog('âš ï¸ MediaRecorder cleanup warning', e.message);
      }
      mediaRecorderRef.current = null;
    }

    if (audioContextRef.current && audioContextRef.current.state !== 'closed') {
      audioContextRef.current.close();
      audioContextRef.current = null;
    }

    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
      setAudioStream(null);
    }

    setIsRecording(false);
    setRecordingTime(0);
    debugLog('âœ… Cleanup completed');
  }, []);

  useEffect(() => {
    return cleanup;
  }, [cleanup]);

  useEffect(() => {
    if (isRecording) {
      timerRef.current = setInterval(() => {
        setRecordingTime(prev => prev + 1);
      }, 1000);
    } else {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
      setRecordingTime(0);
    }

    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, [isRecording]);

  const drawWaveform = useCallback(() => {
    if (!canvasRef.current || !analyserRef.current || !isRecording) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const analyser = analyserRef.current;
    const dataArray = dataArrayRef.current;

    const draw = () => {
      if (!isRecording || !analyserRef.current) return;

      animationRef.current = requestAnimationFrame(draw);
      analyser.getByteFrequencyData(dataArray);

      ctx.fillStyle = "#f8f9fa";
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      const barWidth = canvas.width / dataArray.length * 2.5;
      let x = 0;

      for (let i = 0; i < dataArray.length; i++) {
        const barHeight = (dataArray[i] / 255) * canvas.height * 0.8;

        const gradient = ctx.createLinearGradient(0, canvas.height - barHeight, 0, canvas.height);
        gradient.addColorStop(0, '#25D366');
        gradient.addColorStop(1, '#128C7E');

        ctx.fillStyle = gradient;
        ctx.fillRect(x, canvas.height - barHeight, barWidth - 1, barHeight);

        x += barWidth + 1;
      }
    };

    draw();
  }, [isRecording]);

  // Strategic audio format enforcement for WhatsApp compatibility
  const getOptimalAudioFormat = () => {
    // STRATEGIC: Force OGG container for WhatsApp compatibility
    const formats = [
      { mimeType: 'audio/ogg; codecs=opus', ext: 'ogg', priority: 1 },
      { mimeType: 'audio/ogg', ext: 'ogg', priority: 2 },
      { mimeType: 'audio/webm; codecs=opus', ext: 'webm', priority: 3 },
      { mimeType: 'audio/webm', ext: 'webm', priority: 4 }
    ];

    for (const format of formats) {
      if (MediaRecorder.isTypeSupported(format.mimeType)) {
        debugLog(`ðŸŽµ Selected audio format: ${format.mimeType}`, format);
        return format;
      }
    }

    throw new Error('No WhatsApp-compatible audio format supported');
  };

  // Client-side audio conversion for WhatsApp compatibility
  const convertToWhatsAppFormat = async (webmBlob) => {
    try {
      debugLog('ðŸ”„ Converting audio to WhatsApp format...');
      setIsProcessing(true);

      // Strategy: Use Web Audio API to re-encode as OGG
      const audioContext = new (window.AudioContext || window.webkitAudioContext)();
      const arrayBuffer = await webmBlob.arrayBuffer();
      const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);

      // Create offline context for rendering
      const offlineContext = new OfflineAudioContext(
        1, // mono
        audioBuffer.sampleRate * audioBuffer.duration,
        audioBuffer.sampleRate
      );

      const source = offlineContext.createBufferSource();
      source.buffer = audioBuffer;
      source.connect(offlineContext.destination);
      source.start();

      const renderedBuffer = await offlineContext.startRendering();

      // Convert to WAV first (browser compatibility)
      const wavBlob = audioBufferToWav(renderedBuffer);

      debugLog('âœ… Audio conversion completed', { 
        originalSize: webmBlob.size, 
        convertedSize: wavBlob.size 
      });

      setIsProcessing(false);
      return wavBlob;

    } catch (err) {
      debugLog('âŒ Audio conversion failed', err.message);
      setIsProcessing(false);
      // Return original if conversion fails
      return webmBlob;
    }
  };

  // WAV encoding function for maximum compatibility
  const audioBufferToWav = (buffer) => {
    const length = buffer.length;
    const arrayBuffer = new ArrayBuffer(44 + length * 2);
    const view = new DataView(arrayBuffer);
    const channels = buffer.numberOfChannels;
    const sampleRate = buffer.sampleRate;

    // WAV header
    const writeString = (offset, string) => {
      for (let i = 0; i < string.length; i++) {
        view.setUint8(offset + i, string.charCodeAt(i));
      }
    };

    writeString(0, 'RIFF');
    view.setUint32(4, 36 + length * 2, true);
    writeString(8, 'WAVE');
    writeString(12, 'fmt ');
    view.setUint32(16, 16, true);
    view.setUint16(20, 1, true);
    view.setUint16(22, channels, true);
    view.setUint32(24, sampleRate, true);
    view.setUint32(28, sampleRate * 2, true);
    view.setUint16(32, 2, true);
    view.setUint16(34, 16, true);
    writeString(36, 'data');
    view.setUint32(40, length * 2, true);

    // Convert float32 to int16
    const channelData = buffer.getChannelData(0);
    let offset = 44;
    for (let i = 0; i < length; i++) {
      const sample = Math.max(-1, Math.min(1, channelData[i]));
      view.setInt16(offset, sample < 0 ? sample * 0x8000 : sample * 0x7FFF, true);
      offset += 2;
    }

    return new Blob([arrayBuffer], { type: 'audio/wav' });
  };

  const startRecording = async () => {
    try {
      debugLog('ðŸŽ¬ === STARTING RECORDING ===');
      setError(null);
      cleanup();

      if (permissionStatus !== 'granted') {
        const granted = await checkAndRequestPermissions();
        if (!granted) return;
      }

      const stream = await navigator.mediaDevices.getUserMedia({
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true,
          sampleRate: 48000,
          channelCount: 1,
        }
      });

      streamRef.current = stream;
      setAudioStream(stream);

      debugLog('ðŸŽ¤ Audio stream acquired', {
        tracks: stream.getTracks().length,
        settings: stream.getTracks()[0]?.getSettings()
      });

      // Setup audio visualization
      const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
      if (audioCtx.state === 'suspended') {
        await audioCtx.resume();
      }

      const source = audioCtx.createMediaStreamSource(stream);
      const analyser = audioCtx.createAnalyser();
      analyser.fftSize = 256;
      analyser.smoothingTimeConstant = 0.3;
      source.connect(analyser);

      audioContextRef.current = audioCtx;
      analyserRef.current = analyser;
      dataArrayRef.current = new Uint8Array(analyser.frequencyBinCount);

      // Get optimal format
      const audioFormat = getOptimalAudioFormat();

      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: audioFormat.mimeType,
        audioBitsPerSecond: 96000 // High quality for voice
      });

      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.onstart = () => {
        debugLog('ðŸŽ¬ MediaRecorder started', mediaRecorder.state);
        setIsRecording(true);
        drawWaveform();
      };

      mediaRecorder.ondataavailable = (event) => {
        if (event.data && event.data.size > 0) {
          audioChunksRef.current.push(event.data);
          debugLog('ðŸ“¦ Data chunk received', { 
            size: event.data.size, 
            total: audioChunksRef.current.length 
          });
        }
      };

      mediaRecorder.onstop = async () => {
        debugLog('â¹ï¸ MediaRecorder stopped', {
          chunks: audioChunksRef.current.length,
          totalSize: audioChunksRef.current.reduce((sum, chunk) => sum + chunk.size, 0)
        });

        if (audioChunksRef.current.length > 0) {
          let blob = new Blob(audioChunksRef.current, { type: audioFormat.mimeType });

          // Convert to WhatsApp-compatible format if needed
          if (audioFormat.ext === 'webm') {
            blob = await convertToWhatsAppFormat(blob);
          }

          if (blob.size > 0) {
            await createRecording(blob, audioFormat);
          } else {
            setError('Recording produced empty file');
          }
        } else {
          setError('No audio data captured');
        }

        audioChunksRef.current = [];
      };

      mediaRecorder.onerror = (event) => {
        debugLog('âŒ MediaRecorder error', event.error);
        setError(`Recording failed: ${event.error?.message || 'Unknown error'}`);
        cleanup();
      };

      mediaRecorder.start(200);

    } catch (err) {
      debugLog('ðŸ’¥ Recording start failed', { error: err.message });
      setError(`Failed to start recording: ${err.message}`);
      cleanup();
    }
  };

  const stopRecording = () => {
    debugLog('â¹ï¸ === STOPPING RECORDING ===');

    if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
      mediaRecorderRef.current.stop();
    }

    setIsRecording(false);
    setTimeout(cleanup, 1000);
  };

  const createRecording = async (blob, format) => {
    const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, -5);
    // Force .ogg extension for WhatsApp compatibility
    const filename = `whatsapp-voice-${timestamp}.ogg`;

    const url = URL.createObjectURL(blob);

    const recording = {
      id: Date.now(),
      url,
      blob,
      filename,
      duration: recordingTime,
      timestamp: new Date().toLocaleTimeString(),
      size: blob.size,
      format: format.ext,
      isWhatsAppCompatible: format.ext === 'ogg' || blob.type.includes('wav')
    };

    setRecordings(prev => [...prev, recording]);
    debugLog('âœ… Recording created', recording);

    // Auto-download for file system access
    autoDownload(recording);
  };

  // Strategic file system access with multiple methods
  const autoDownload = async (recording) => {
    try {
      // Method 1: File System Access API (Chrome 86+)
      if ('showSaveFilePicker' in window) {
        debugLog('ðŸ’¾ Using File System Access API');
        // Don't auto-trigger, but prepare for user action
        return;
      }

      // Method 2: Traditional download
      const link = document.createElement('a');
      link.href = recording.url;
      link.download = recording.filename;
      link.style.display = 'none';
      document.body.appendChild(link);

      // Store reference for manual trigger
      recording.downloadLink = link;

      debugLog('ðŸ“ Download link prepared', recording.filename);

    } catch (err) {
      debugLog('âŒ Download preparation failed', err.message);
    }
  };

  // Enhanced download with File System Access API
  const downloadRecording = async (recording) => {
    try {
      if ('showSaveFilePicker' in window) {
        const fileHandle = await window.showSaveFilePicker({
          suggestedName: recording.filename,
          types: [{
            description: 'WhatsApp Voice Messages',
            accept: {
              'audio/ogg': ['.ogg'],
              'audio/wav': ['.wav'],
              'audio/webm': ['.webm']
            }
          }]
        });

        const writable = await fileHandle.createWritable();
        await writable.write(recording.blob);
        await writable.close();

        debugLog('âœ… File saved via File System Access API');

      } else {
        // Fallback to traditional download
        if (recording.downloadLink) {
          recording.downloadLink.click();
        } else {
          const link = document.createElement('a');
          link.href = recording.url;
          link.download = recording.filename;
          link.click();
        }
        debugLog('âœ… File downloaded via traditional method');
      }
    } catch (err) {
      if (err.name !== 'AbortError') {
        debugLog('âŒ Download failed', err.message);
        setError('Download failed: ' + err.message);
      }
    }
  };

  const removeRecording = (index) => {
    setRecordings(prev => {
      const recording = prev[index];
      URL.revokeObjectURL(recording.url);
      if (recording.downloadLink && document.body.contains(recording.downloadLink)) {
        document.body.removeChild(recording.downloadLink);
      }
      return prev.filter((_, i) => i !== index);
    });
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  return (
    <div className="bg-[#ece5dd] min-h-screen p-4 space-y-4">
      {/* Status bar */}
      <div className="flex items-center justify-between text-sm bg-white/50 p-2 rounded">
        <span>
          ðŸŽ™ï¸ {permissionStatus === 'granted' ? 'âœ… Ready' : 
               permissionStatus === 'denied' ? 'âŒ No Access' : 
               'â³ Checking...'}
        </span>
        {isRecording && (
          <span className="text-red-600 font-medium animate-pulse">
            ðŸ”´ REC {recordingTime}s
          </span>
        )}
        {isProcessing && (
          <span className="text-blue-600">âš™ï¸ Processing...</span>
        )}
      </div>

      {/* Error display */}
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
          {error}
          <button 
            onClick={() => setError(null)}
            className="float-right text-red-500 hover:text-red-700 font-bold"
          >
            Ã—
          </button>
        </div>
      )}

      {/* Recording controls */}
      <div className="flex items-center space-x-2">
        {!isRecording ? (
          <button
            onClick={startRecording}
            disabled={permissionStatus !== 'granted'}
            className={`px-4 py-2 rounded-full shadow-md transition-colors ${
              permissionStatus !== 'granted' 
                ? 'bg-gray-300 text-gray-500 cursor-not-allowed' 
                : 'bg-white border border-gray-300 hover:bg-gray-100 text-black'
            }`}
          >
            ðŸŽ™ï¸
          </button>
        ) : (
          <button
            onClick={stopRecording}
            className="bg-white border border-gray-300 hover:bg-gray-100 text-black px-4 py-2 rounded-full shadow-md relative"
          >
            â¹ï¸ {recordingTime}s
            <span className="absolute -top-3 right-0 w-3 h-3 bg-red-500 rounded-full animate-ping" />
          </button>
        )}
      </div>

      {/* Waveform */}
      {isRecording && (
        <div className="bg-white p-3 rounded-lg shadow">
          <canvas
            ref={canvasRef}
            width={400}
            height={60}
            className="w-full border rounded"
          />
          <div className="text-center text-xs text-gray-500 mt-1">
            ðŸŽµ Recording... {recordingTime}s
          </div>
        </div>
      )}

      {/* WhatsApp-style recordings */}
      <div className="space-y-3">
        {recordings.map((rec, index) => (
          <div
            key={rec.id}
            className="flex items-center space-x-3 bg-[#dcf8c6] p-3 rounded-xl shadow-md w-fit max-w-full"
          >
            <img
              src="https://www.gravatar.com/avatar?d=mp"
              alt="Avatar"
              className="w-10 h-10 rounded-full"
            />
            <div className="flex flex-col">
              <audio controls src={rec.url} className="w-60" />
              <div className="text-xs text-gray-500 flex justify-between mt-1">
                <span>
                  {rec.duration}s â€¢ {formatFileSize(rec.size)} â€¢ 
                  {rec.isWhatsAppCompatible ? ' âœ… WhatsApp' : ' âš ï¸ Convert'}
                </span>
                <span>{rec.timestamp}</span>
              </div>
              <div className="text-xs text-green-600 font-medium">
                ðŸ“ {rec.filename}
              </div>
            </div>
            <div className="flex flex-col space-y-1">
              <button
                onClick={() => downloadRecording(rec)}
                className="text-green-700 hover:text-green-900 p-1 bg-white rounded shadow-sm"
                title="Save to computer"
              >
                ðŸ’¾
              </button>
              <button
                onClick={() => removeRecording(index)}
                className="text-red-700 hover:text-red-900 p-1 bg-white rounded shadow-sm"
                title="Delete"
              >
                ðŸ—‘ï¸
              </button>
            </div>
          </div>
        ))}
      </div>

      {recordings.length === 0 && !isRecording && (
        <div className="text-center text-gray-500 py-8">
          <div className="text-4xl mb-2">ðŸŽ¤</div>
          <p>Click the microphone to start recording</p>
          <p className="text-sm mt-2">Files will be saved as WhatsApp-compatible .ogg format</p>
        </div>
      )}

      {/* Usage instructions */}
      {recordings.length > 0 && (
        <div className="bg-green-50 border border-green-200 p-4 rounded-lg">
          <h3 className="font-medium text-green-800 mb-2">ðŸ“± How to use with WhatsApp:</h3>
          <ol className="text-sm text-green-700 space-y-1 list-decimal list-inside">
            <li><strong>Save file:</strong> Click ðŸ’¾ to download the .ogg file</li>
            <li><strong>Mobile:</strong> Share saved file via WhatsApp mobile app</li>
            <li><strong>Desktop:</strong> Use WhatsApp Desktop to send saved files</li>
            <li><strong>Format:</strong> Files are optimized for WhatsApp compatibility</li>
          </ol>
        </div>
      )}

      {/* Debug panel */}
      {Object.keys(debugInfo).length > 0 && (
        <details className="mt-4 p-3 bg-gray-100 rounded text-xs">
          <summary className="cursor-pointer font-medium">
            ðŸ”§ Debug Log ({Object.keys(debugInfo).length} entries)
          </summary>
          <div className="mt-2 space-y-1 max-h-40 overflow-y-auto">
            {Object.entries(debugInfo)
              .slice(-8)
              .map(([key, info]) => (
                <div key={key} className="flex items-start space-x-2">
                  <span className="text-gray-400 min-w-16">{info.time}</span>
                  <span className="font-mono text-xs">{info.message}</span>
                  {info.data && (
                    <span className="text-blue-600 text-xs">
                      {JSON.stringify(info.data).slice(0, 100)}
                    </span>
                  )}
                </div>
              ))}
          </div>
        </details>
      )}
    </div>
  );
}
