import React, { useState, useRef, useEffect, useCallback } from "react";

export default function WhatsAppAudioRecorder() {
  const [recordings, setRecordings] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [audioStream, setAudioStream] = useState(null);
  const [permissionStatus, setPermissionStatus] = useState('checking');
  const [error, setError] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [processingStatus, setProcessingStatus] = useState('');
  const [systemFFmpegAvailable, setSystemFFmpegAvailable] = useState(false);
  const [workingDirectory, setWorkingDirectory] = useState(null);

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const timerRef = useRef(null);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const dataArrayRef = useRef(null);
  const animationRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  const debugLog = (message, data = null) => {
    console.log(`🎤 ${message}`, data);
  };

  // STRATEGIC: Setup system FFmpeg integration via File System Access
  const setupSystemFFmpeg = async () => {
    try {
      if ('showDirectoryPicker' in window) {
        const dirHandle = await window.showDirectoryPicker({
          mode: 'readwrite',
          startIn: 'downloads'
        });
        setWorkingDirectory(dirHandle);

        // Test system FFmpeg availability by creating a test command file
        const testScript = `@echo off
echo Testing FFmpeg availability...
ffmpeg -version >ffmpeg_test.txt 2>&1
if %ERRORLEVEL% EQU 0 (
    echo FFMPEG_AVAILABLE >ffmpeg_status.txt
) else (
    echo FFMPEG_NOT_FOUND >ffmpeg_status.txt
)`;

        const scriptBlob = new Blob([testScript], { type: 'text/plain' });
        const scriptHandle = await dirHandle.getFileHandle('test_ffmpeg.bat', { create: true });
        const writable = await scriptHandle.createWritable();
        await writable.write(scriptBlob);
        await writable.close();

        setSystemFFmpegAvailable(true);
        debugLog('✅ System FFmpeg integration ready', dirHandle.name);

        return true;
      }
    } catch (err) {
      if (err.name !== 'AbortError') {
        debugLog('⚠️ System FFmpeg setup declined', err.message);
      }
      return false;
    }
  };

  // STRATEGIC: Process audio with system FFmpeg via batch file
  const processWithSystemFFmpeg = async (webmBlob, outputFormat = 'ogg') => {
    if (!workingDirectory) {
      throw new Error('Working directory not available');
    }

    try {
      setIsProcessing(true);
      setProcessingStatus('Writing temporary files...');

      const timestamp = Date.now();
      const inputFile = `temp_input_${timestamp}.webm`;
      const outputFile = `whatsapp_${outputFormat}_${timestamp}.${outputFormat}`;
      const batchFile = `process_audio_${timestamp}.bat`;

      // Write input WebM file
      const inputHandle = await workingDirectory.getFileHandle(inputFile, { create: true });
      const inputWritable = await inputHandle.createWritable();
      await inputWritable.write(webmBlob);
      await inputWritable.close();

      // Create FFmpeg batch script with WhatsApp-optimized parameters
      let ffmpegCommand;
      if (outputFormat === 'ogg') {
        // VOICE MESSAGE: WhatsApp-compatible OGG Opus
        ffmpegCommand = `@echo off
echo Processing WhatsApp voice message...
ffmpeg -i "${inputFile}" -c:a libopus -b:a 32k -vbr on -compression_level 10 -application voip -ac 1 -ar 16000 -f ogg "${outputFile}" -y
if %ERRORLEVEL% EQU 0 (
    echo SUCCESS >process_status_${timestamp}.txt
    echo Voice message created successfully
) else (
    echo FAILED >process_status_${timestamp}.txt
    echo FFmpeg processing failed
)
del "${inputFile}"
echo Processing complete. Check ${outputFile}
pause`;
      } else if (outputFormat === 'm4a') {
        // MUSIC MESSAGE: WhatsApp-compatible M4A with faststart
        ffmpegCommand = `@echo off
echo Processing WhatsApp music file...
ffmpeg -i "${inputFile}" -c:a aac -b:a 128k -movflags +faststart -ac 2 -ar 44100 "${outputFile}" -y
if %ERRORLEVEL% EQU 0 (
    echo SUCCESS >process_status_${timestamp}.txt
    echo Music file created successfully
) else (
    echo FAILED >process_status_${timestamp}.txt
    echo FFmpeg processing failed
)
del "${inputFile}"
echo Processing complete. Check ${outputFile}
pause`;
      }

      // Write batch file
      const batchBlob = new Blob([ffmpegCommand], { type: 'text/plain' });
      const batchHandle = await workingDirectory.getFileHandle(batchFile, { create: true });
      const batchWritable = await batchHandle.createWritable();
      await batchWritable.write(batchBlob);
      await batchWritable.close();

      setProcessingStatus(`FFmpeg script created: ${batchFile}`);

      // Return processing info for user to execute
      return {
        batchFile,
        outputFile,
        timestamp,
        inputFile,
        directory: workingDirectory.name
      };

    } catch (err) {
      debugLog('❌ System FFmpeg processing setup failed', err.message);
      throw err;
    } finally {
      setIsProcessing(false);
    }
  };

  // ENHANCED: Create proper OGG Opus with correct container headers
  const createProperOggOpus = async (webmBlob) => {
    try {
      setIsProcessing(true);
      setProcessingStatus('Creating proper OGG Opus container...');

      // Decode WebM to raw audio data
      const audioContext = new AudioContext();
      const arrayBuffer = await webmBlob.arrayBuffer();
      const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);

      // Resample to 16kHz mono for WhatsApp voice
      const targetSampleRate = 16000;
      const offlineContext = new OfflineAudioContext(1, audioBuffer.duration * targetSampleRate, targetSampleRate);

      const source = offlineContext.createBufferSource();
      source.buffer = audioBuffer;

      // Convert stereo to mono if needed
      if (audioBuffer.numberOfChannels > 1) {
        const merger = offlineContext.createChannelMerger(1);
        const splitter = offlineContext.createChannelSplitter(audioBuffer.numberOfChannels);
        const gain = offlineContext.createGain();
        gain.gain.value = 0.5; // Average channels

        source.connect(splitter);
        splitter.connect(gain, 0);
        splitter.connect(gain, 1);
        gain.connect(merger, 0, 0);
        merger.connect(offlineContext.destination);
      } else {
        source.connect(offlineContext.destination);
      }

      source.start();
      const resampledBuffer = await offlineContext.startRendering();

      // Create MediaRecorder with forced OGG Opus settings
      const stream = new MediaStream();
      const newAudioContext = new AudioContext({ sampleRate: 16000 });
      const destination = newAudioContext.createMediaStreamDestination();

      // Create buffer source with resampled audio
      const bufferSource = newAudioContext.createBufferSource();
      bufferSource.buffer = resampledBuffer;
      bufferSource.connect(destination);

      // Force OGG Opus MediaRecorder
      const mediaRecorder = new MediaRecorder(destination.stream, {
        mimeType: 'audio/ogg; codecs=opus',
        audioBitsPerSecond: 32000
      });

      const chunks = [];

      return new Promise((resolve, reject) => {
        mediaRecorder.ondataavailable = (event) => {
          if (event.data.size > 0) {
            chunks.push(event.data);
          }
        };

        mediaRecorder.onstop = () => {
          // Create proper OGG blob with correct MIME type
          const oggBlob = new Blob(chunks, { 
            type: 'audio/ogg; codecs=opus'
          });

          debugLog('✅ Proper OGG Opus created', {
            size: oggBlob.size,
            type: oggBlob.type,
            originalSize: webmBlob.size
          });

          resolve(oggBlob);
          newAudioContext.close();
        };

        mediaRecorder.onerror = reject;

        bufferSource.start();
        mediaRecorder.start();

        // Stop after audio duration
        setTimeout(() => {
          bufferSource.stop();
          mediaRecorder.stop();
        }, resampledBuffer.duration * 1000 + 500);
      });

    } catch (err) {
      debugLog('❌ OGG Opus creation failed', err.message);
      throw err;
    } finally {
      setIsProcessing(false);
      setProcessingStatus('');
    }
  };

  const checkAndRequestPermissions = async () => {
    try {
      setPermissionStatus('checking');

      const testStream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: false,
          noiseSuppression: false,
          autoGainControl: false,
          sampleRate: 48000,
          channelCount: 2,
        } 
      });

      debugLog('✅ Microphone access granted');
      testStream.getTracks().forEach(track => track.stop());
      setPermissionStatus('granted');
      return true;

    } catch (err) {
      debugLog('❌ Permissions denied', err.message);
      setPermissionStatus('denied');
      setError(`Microphone required: ${err.message}`);
      return false;
    }
  };

  useEffect(() => {
    checkAndRequestPermissions();
  }, []);

  const cleanup = useCallback(() => {
    [timerRef, animationRef].forEach(ref => {
      if (ref.current) {
        ref === timerRef ? clearInterval(ref.current) : cancelAnimationFrame(ref.current);
        ref.current = null;
      }
    });

    if (mediaRecorderRef.current?.state !== 'inactive') {
      try {
        mediaRecorderRef.current?.stop();
      } catch (e) {
        debugLog('⚠️ MediaRecorder cleanup', e.message);
      }
      mediaRecorderRef.current = null;
    }

    if (audioContextRef.current?.state !== 'closed') {
      audioContextRef.current?.close();
      audioContextRef.current = null;
    }

    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
      setAudioStream(null);
    }

    setIsRecording(false);
    setRecordingTime(0);
  }, []);

  useEffect(() => cleanup, [cleanup]);

  useEffect(() => {
    if (isRecording) {
      timerRef.current = setInterval(() => setRecordingTime(prev => prev + 1), 1000);
    } else {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
      setRecordingTime(0);
    }
    return () => timerRef.current && clearInterval(timerRef.current);
  }, [isRecording]);

  const drawWaveform = useCallback(() => {
    if (!canvasRef.current || !analyserRef.current || !isRecording) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const analyser = analyserRef.current;
    const dataArray = dataArrayRef.current;

    const draw = () => {
      if (!isRecording) return;
      animationRef.current = requestAnimationFrame(draw);
      analyser.getByteFrequencyData(dataArray);

      ctx.fillStyle = "#f0f2f5";
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      const barWidth = (canvas.width / dataArray.length) * 2.5;
      let x = 0;

      for (let i = 0; i < dataArray.length; i++) {
        const barHeight = (dataArray[i] / 255) * canvas.height * 0.9;
        const gradient = ctx.createLinearGradient(0, canvas.height - barHeight, 0, canvas.height);
        gradient.addColorStop(0, '#25D366');
        gradient.addColorStop(0.5, '#128C7E');
        gradient.addColorStop(1, '#0d7377');
        ctx.fillStyle = gradient;
        ctx.fillRect(x, canvas.height - barHeight, barWidth - 1, barHeight);
        x += barWidth;
      }
    };
    draw();
  }, [isRecording]);

  const startRecording = async () => {
    try {
      debugLog('🎬 Starting high-quality recording...');
      setError(null);
      cleanup();

      if (permissionStatus !== 'granted') {
        const granted = await checkAndRequestPermissions();
        if (!granted) return;
      }

      // Ultra-high quality recording for processing
      const stream = await navigator.mediaDevices.getUserMedia({
        audio: {
          echoCancellation: false,
          noiseSuppression: false,
          autoGainControl: false,
          sampleRate: 48000,
          channelCount: 2,
          latency: 0.01
        }
      });

      streamRef.current = stream;
      setAudioStream(stream);

      // Audio visualization
      const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
      if (audioCtx.state === 'suspended') await audioCtx.resume();

      const source = audioCtx.createMediaStreamSource(stream);
      const analyser = audioCtx.createAnalyser();
      analyser.fftSize = 512;
      analyser.smoothingTimeConstant = 0.3;
      source.connect(analyser);

      audioContextRef.current = audioCtx;
      analyserRef.current = analyser;
      dataArrayRef.current = new Uint8Array(analyser.frequencyBinCount);

      // Record in maximum quality WebM
      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: 'audio/webm; codecs=opus',
        audioBitsPerSecond: 320000
      });

      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.onstart = () => {
        debugLog('🎬 Ultra-high quality recording started');
        setIsRecording(true);
        drawWaveform();
      };

      mediaRecorder.ondataavailable = (event) => {
        if (event.data?.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = async () => {
        debugLog('⏹️ Processing audio...');

        if (audioChunksRef.current.length > 0) {
          const rawWebmBlob = new Blob(audioChunksRef.current, { 
            type: 'audio/webm; codecs=opus' 
          });

          try {
            // Create proper OGG Opus
            const oggBlob = await createProperOggOpus(rawWebmBlob);
            await createRecording(oggBlob, 'ogg', 'Browser OGG Opus');

            // If system FFmpeg is available, create batch scripts
            if (workingDirectory) {
              const oggProcessing = await processWithSystemFFmpeg(rawWebmBlob, 'ogg');
              const m4aProcessing = await processWithSystemFFmpeg(rawWebmBlob, 'm4a');

              await createSystemProcessingRecord(oggProcessing, 'System OGG Voice');
              await createSystemProcessingRecord(m4aProcessing, 'System M4A Music');
            }

          } catch (err) {
            debugLog('⚠️ Processing failed, saving original', err.message);
            setError(`Processing failed: ${err.message}`);
            await createRecording(rawWebmBlob, 'webm', 'Original WebM');
          }
        } else {
          setError('No audio data captured');
        }

        audioChunksRef.current = [];
      };

      mediaRecorder.onerror = (event) => {
        debugLog('❌ Recording error', event.error);
        setError(`Recording failed: ${event.error?.message}`);
        cleanup();
      };

      mediaRecorder.start(50);

    } catch (err) {
      debugLog('💥 Recording failed', err);
      setError(`Failed to start: ${err.message}`);
      cleanup();
    }
  };

  const stopRecording = () => {
    debugLog('⏹️ Stopping recording...');
    if (mediaRecorderRef.current?.state === 'recording') {
      mediaRecorderRef.current.stop();
    }
    setIsRecording(false);
    setTimeout(cleanup, 1000);
  };

  const createRecording = async (blob, extension, description) => {
    const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, -5);
    const filename = `whatsapp-${extension}-${timestamp}.${extension}`;

    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = filename;
    link.style.display = 'none';
    document.body.appendChild(link);

    const recording = {
      id: Date.now() + Math.random(),
      url,
      blob,
      filename,
      duration: recordingTime,
      timestamp: new Date().toLocaleTimeString(),
      size: blob.size,
      type: extension,
      description,
      downloadLink: link,
      isSystemProcessed: false
    };

    setRecordings(prev => [...prev, recording]);
    debugLog(`✅ ${description} recording created`, { filename, size: blob.size });
  };

  const createSystemProcessingRecord = async (processingInfo, description) => {
    const record = {
      id: Date.now() + Math.random(),
      filename: processingInfo.outputFile,
      batchFile: processingInfo.batchFile,
      duration: recordingTime,
      timestamp: new Date().toLocaleTimeString(),
      type: 'system',
      description,
      isSystemProcessed: true,
      directory: processingInfo.directory,
      processingInfo
    };

    setRecordings(prev => [...prev, record]);
    debugLog(`✅ System processing record created`, processingInfo);
  };

  const downloadRecording = (recording) => {
    if (recording.downloadLink) {
      recording.downloadLink.click();
      debugLog('✅ File downloaded', recording.filename);
    }
  };

  const removeRecording = (index) => {
    setRecordings(prev => {
      const recording = prev[index];
      if (recording.url) {
        URL.revokeObjectURL(recording.url);
      }
      if (recording.downloadLink && document.body.contains(recording.downloadLink)) {
        document.body.removeChild(recording.downloadLink);
      }
      return prev.filter((_, i) => i !== index);
    });
  };

  const formatFileSize = (bytes) => {
    if (!bytes) return 'Processing...';
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  const getRecordingIcon = (type) => {
    switch (type) {
      case 'ogg': return '🎤';
      case 'm4a': return '🎵';
      case 'system': return '⚙️';
      default: return '📼';
    }
  };

  const getCompatibilityInfo = (type, isSystemProcessed) => {
    if (isSystemProcessed) {
      return {
        text: 'System FFmpeg',
        color: 'text-purple-600',
        bg: 'bg-purple-50',
        desc: 'Professional Processing'
      };
    }

    switch (type) {
      case 'ogg': 
        return { 
          text: 'Browser OGG', 
          color: 'text-green-600', 
          bg: 'bg-green-50',
          desc: '16kHz Mono Opus'
        };
      case 'm4a': 
        return { 
          text: 'M4A Music', 
          color: 'text-blue-600', 
          bg: 'bg-blue-50',
          desc: '44.1kHz Stereo AAC'
        };
      default: 
        return { 
          text: 'Original', 
          color: 'text-gray-600', 
          bg: 'bg-gray-50',
          desc: 'WebM Source'
        };
    }
  };

  return (
    <div className="bg-[#ece5dd] min-h-screen p-4 space-y-4">
      {/* Professional header */}
      <div className="bg-gradient-to-r from-[#075e54] via-[#128C7E] to-[#25D366] text-white p-6 rounded-lg shadow-xl">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="w-12 h-12 bg-white/20 rounded-full flex items-center justify-center">
              <span className="text-2xl">🎤</span>
            </div>
            <div>
              <h1 className="text-2xl font-bold">WhatsApp Audio Studio Pro</h1>
              <p className="text-sm opacity-90">System FFmpeg integration + Browser processing</p>
            </div>
          </div>
          <div className="text-right text-sm">
            <div className="font-medium">
              {permissionStatus === 'granted' ? '✅ Ready' : 
               permissionStatus === 'denied' ? '❌ Access Required' : '⏳ Initializing...'}
            </div>
            <div className="opacity-75">
              System FFmpeg: {systemFFmpegAvailable ? '✅ Available' : '❌ Not Setup'}
            </div>
            {workingDirectory && (
              <div className="text-xs opacity-75">📁 {workingDirectory.name}</div>
            )}
          </div>
        </div>
      </div>

      {/* System FFmpeg setup */}
      {!workingDirectory && (
        <div className="bg-blue-50 border border-blue-200 p-6 rounded-lg">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <span className="text-2xl">⚙️</span>
              <div>
                <h3 className="font-semibold text-blue-800">Enable System FFmpeg Processing</h3>
                <p className="text-sm text-blue-600">
                  For professional WhatsApp-compatible audio with guaranteed playback
                </p>
              </div>
            </div>
            <button
              onClick={setupSystemFFmpeg}
              className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-lg font-medium transition-colors"
            >
              Setup FFmpeg Integration
            </button>
          </div>
        </div>
      )}

      {/* Processing status */}
      {(isProcessing || processingStatus) && (
        <div className="bg-amber-50 border border-amber-200 p-4 rounded-lg">
          <div className="flex items-center space-x-3">
            <div className="animate-spin text-amber-600 text-xl">⚙️</div>
            <div>
              <div className="font-semibold text-amber-800">Audio Processing</div>
              <div className="text-sm text-amber-600">{processingStatus || 'Processing audio...'}</div>
            </div>
          </div>
        </div>
      )}

      {/* Recording status */}
      {isRecording && (
        <div className="bg-red-50 border border-red-200 p-4 rounded-lg">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <span className="w-4 h-4 bg-red-500 rounded-full animate-pulse"></span>
              <span className="font-semibold text-red-800">Recording Ultra-High Quality Audio</span>
            </div>
            <span className="text-red-600 font-mono text-xl">{recordingTime}s</span>
          </div>
        </div>
      )}

      {/* Error display */}
      {error && (
        <div className="bg-red-50 border-l-4 border-red-400 p-4 rounded flex justify-between items-start">
          <span className="text-red-700">{error}</span>
          <button 
            onClick={() => setError(null)}
            className="text-red-400 hover:text-red-600 font-bold text-xl ml-4"
          >
            ×
          </button>
        </div>
      )}

      {/* Recording controls */}
      <div className="flex justify-center">
        {!isRecording ? (
          <button
            onClick={startRecording}
            disabled={permissionStatus !== 'granted'}
            className={`flex items-center space-x-4 px-10 py-5 rounded-full shadow-2xl transition-all transform hover:scale-105 ${
              permissionStatus !== 'granted' 
                ? 'bg-gray-300 text-gray-500 cursor-not-allowed' 
                : 'bg-gradient-to-r from-[#25D366] via-[#128C7E] to-[#075e54] hover:from-[#128C7E] hover:to-[#075e54] text-white'
            }`}
          >
            <span className="text-3xl">🎤</span>
            <div>
              <div className="font-bold text-lg">Start Recording</div>
              <div className="text-sm opacity-90">Ultra-high quality capture</div>
            </div>
          </button>
        ) : (
          <button
            onClick={stopRecording}
            className="flex items-center space-x-4 bg-red-500 hover:bg-red-600 text-white px-10 py-5 rounded-full shadow-2xl relative transition-all transform hover:scale-105"
          >
            <span className="text-3xl">⏹️</span>
            <div>
              <div className="font-bold text-lg">Stop Recording</div>
              <div className="text-sm opacity-90">{recordingTime} seconds</div>
            </div>
            <span className="absolute -top-3 -right-3 w-6 h-6 bg-red-300 rounded-full animate-ping"></span>
          </button>
        )}
      </div>

      {/* Waveform visualization */}
      {isRecording && (
        <div className="bg-white p-6 rounded-xl shadow-lg max-w-5xl mx-auto">
          <canvas
            ref={canvasRef}
            width={900}
            height={140}
            className="w-full border-2 border-gray-200 rounded-lg"
          />
          <div className="text-center text-gray-600 mt-4 flex items-center justify-center space-x-3">
            <span className="animate-pulse text-2xl">🎵</span>
            <span className="font-semibold text-lg">Professional Audio Capture • {recordingTime}s</span>
            <span className="text-sm bg-gray-100 px-2 py-1 rounded">320kbps WebM</span>
          </div>
        </div>
      )}

      {/* Recordings display */}
      <div className="grid gap-4 max-w-6xl mx-auto">
        {recordings.map((rec, index) => {
          const info = getCompatibilityInfo(rec.type, rec.isSystemProcessed);
          return (
            <div
              key={rec.id}
              className="flex items-center space-x-6 bg-white p-6 rounded-xl shadow-lg hover:shadow-xl transition-all border-l-4 border-[#25D366]"
            >
              <div className="w-16 h-16 bg-gradient-to-br from-[#25D366] to-[#075e54] rounded-full flex items-center justify-center flex-shrink-0 text-white text-3xl shadow-lg">
                {getRecordingIcon(rec.type)}
              </div>

              <div className="flex-1 min-w-0">
                <div className="flex items-center space-x-3 mb-3">
                  <span className={`px-4 py-2 rounded-full text-sm font-semibold ${info.bg} ${info.color}`}>
                    {info.text}
                  </span>
                  <span className="text-xs text-gray-500 bg-gray-100 px-2 py-1 rounded">
                    {info.desc}
                  </span>
                </div>

                {rec.url && <audio controls src={rec.url} className="w-full mb-3" />}

                <div className="flex justify-between items-center text-sm text-gray-600">
                  <div className="flex items-center space-x-4">
                    <span>⏱️ {rec.duration}s</span>
                    <span>📁 {formatFileSize(rec.size)}</span>
                    <span>📱 {rec.description}</span>
                  </div>
                  <span>{rec.timestamp}</span>
                </div>

                {rec.isSystemProcessed && (
                  <div className="mt-2 p-3 bg-purple-50 border border-purple-200 rounded text-sm">
                    <div className="font-medium text-purple-800 mb-1">System FFmpeg Processing:</div>
                    <div className="text-purple-600 space-y-1">
                      <div>📁 Directory: {rec.directory}</div>
                      <div>🔧 Run: <code className="bg-purple-100 px-1 rounded">{rec.batchFile}</code></div>
                      <div>📂 Output: <code className="bg-purple-100 px-1 rounded">{rec.filename}</code></div>
                    </div>
                  </div>
                )}

                <div className="text-xs text-gray-400 mt-2 font-mono truncate">
                  📱 {rec.filename}
                </div>
              </div>

              <div className="flex space-x-3 flex-shrink-0">
                {rec.downloadLink && (
                  <button
                    onClick={() => downloadRecording(rec)}
                    className="px-5 py-3 bg-[#25D366] hover:bg-[#128C7E] text-white rounded-lg font-medium transition-all shadow-md hover:shadow-lg"
                  >
                    💾 Save
                  </button>
                )}
                <button
                  onClick={() => removeRecording(index)}
                  className="px-5 py-3 bg-red-500 hover:bg-red-600 text-white rounded-lg font-medium transition-all shadow-md hover:shadow-lg"
                >
                  🗑️ Delete
                </button>
              </div>
            </div>
          );
        })}
      </div>

      {recordings.length === 0 && !isRecording && (
        <div className="text-center text-gray-500 py-20 max-w-2xl mx-auto">
          <div className="text-8xl mb-6">🎤</div>
          <h3 className="text-3xl font-bold mb-4">Professional Audio Studio</h3>
          <p className="text-lg mb-3">System FFmpeg + Browser processing for WhatsApp</p>
          <p className="text-sm text-gray-400">
            Creates voice messages and music files optimized for WhatsApp playback
          </p>
        </div>
      )}

      {/* Comprehensive usage guide */}
      {recordings.length > 0 && (
        <div className="bg-gradient-to-r from-green-50 via-blue-50 to-purple-50 border border-green-200 p-8 rounded-xl max-w-6xl mx-auto">
          <h3 className="font-bold text-xl text-green-800 mb-6 flex items-center space-x-3">
            <span>🚀</span>
            <span>WhatsApp Integration Guide</span>
          </h3>

          <div className="grid md:grid-cols-2 gap-6 mb-6">
            <div className="bg-green-50 p-6 rounded-lg border border-green-200">
              <h4 className="font-semibold text-green-700 mb-4 flex items-center space-x-2">
                <span>🌐</span>
                <span>Browser Processing</span>
              </h4>
              <ul className="space-y-2 text-sm text-green-600">
                <li>• <strong>OGG Opus:</strong> Resampled to 16kHz mono</li>
                <li>• <strong>Container:</strong> Proper OGG structure</li>
                <li>• <strong>Compatibility:</strong> Basic WhatsApp support</li>
                <li>• <strong>Usage:</strong> Download and test in WhatsApp</li>
              </ul>
            </div>

            <div className="bg-purple-50 p-6 rounded-lg border border-purple-200">
              <h4 className="font-semibold text-purple-700 mb-4 flex items-center space-x-2">
                <span>⚙️</span>
                <span>System FFmpeg Processing</span>
              </h4>
              <ul className="space-y-2 text-sm text-purple-600">
                <li>• <strong>Voice:</strong> Professional OGG Opus @ 32k</li>
                <li>• <strong>Music:</strong> M4A AAC with faststart</li>
                <li>• <strong>Quality:</strong> Industry standard</li>
                <li>• <strong>Usage:</strong> Run .bat file in working directory</li>
              </ul>
            </div>
          </div>

          <div className="bg-amber-50 border border-amber-200 p-6 rounded-lg">
            <h4 className="font-semibold text-amber-800 mb-4 flex items-center space-x-2">
              <span>🔧</span>
              <span>System FFmpeg Workflow</span>
            </h4>
            <ol className="list-decimal list-inside space-y-2 text-sm text-amber-700">
              <li><strong>Setup:</strong> Click "Setup FFmpeg Integration" and select working directory</li>
              <li><strong>Record:</strong> Create audio recording as normal</li>
              <li><strong>Process:</strong> Double-click the generated .bat file in your directory</li>
              <li><strong>Wait:</strong> FFmpeg will process and create WhatsApp-compatible files</li>
              <li><strong>Use:</strong> Upload the processed .ogg or .m4a files to WhatsApp</li>
            </ol>
            <div className="mt-4 p-3 bg-amber-100 rounded border border-amber-300">
              <p className="text-sm text-amber-800">
                <strong>✅ Guaranteed Compatibility:</strong> System FFmpeg uses identical parameters to 
                professional audio tools, ensuring WhatsApp recognizes files as instant-playable audio.
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
