import React, { useState, useRef, useEffect, useCallback } from "react";

export default function WhatsAppAudioRecorder() {
  const [recordings, setRecordings] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [audioStream, setAudioStream] = useState(null);
  const [permissionStatus, setPermissionStatus] = useState('checking');
  const [error, setError] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [debugInfo, setDebugInfo] = useState({});

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const timerRef = useRef(null);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const dataArrayRef = useRef(null);
  const animationRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  const debugLog = (message, data = null) => {
    console.log(`🎤 ${message}`, data);
    setDebugInfo(prev => ({
      ...prev,
      [`${Date.now()}`]: { message, data, time: new Date().toLocaleTimeString() }
    }));
  };

  const checkAndRequestPermissions = async () => {
    try {
      debugLog('🔍 Requesting microphone access...');
      setPermissionStatus('checking');

      const testStream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true,
          sampleRate: 48000,
          channelCount: 1,
          latency: 0.01
        } 
      });

      debugLog('✅ Microphone access granted', {
        tracks: testStream.getTracks().length,
        settings: testStream.getTracks()[0]?.getSettings()
      });

      testStream.getTracks().forEach(track => track.stop());
      setPermissionStatus('granted');
      return true;

    } catch (err) {
      debugLog('❌ Microphone access denied', { error: err.message });
      setPermissionStatus('denied');
      setError(`Microphone required: ${err.message}`);
      return false;
    }
  };

  useEffect(() => {
    checkAndRequestPermissions();
  }, []);

  const cleanup = useCallback(() => {
    debugLog('🧹 Comprehensive cleanup initiated');

    [timerRef, animationRef].forEach(ref => {
      if (ref.current) {
        ref === timerRef ? clearInterval(ref.current) : cancelAnimationFrame(ref.current);
        ref.current = null;
      }
    });

    if (mediaRecorderRef.current?.state !== 'inactive') {
      try {
        mediaRecorderRef.current?.stop();
      } catch (e) {
        debugLog('⚠️ MediaRecorder cleanup warning', e.message);
      }
      mediaRecorderRef.current = null;
    }

    if (audioContextRef.current?.state !== 'closed') {
      audioContextRef.current?.close();
      audioContextRef.current = null;
    }

    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
      setAudioStream(null);
    }

    setIsRecording(false);
    setRecordingTime(0);
    debugLog('✅ Cleanup completed');
  }, []);

  useEffect(() => cleanup, [cleanup]);

  useEffect(() => {
    if (isRecording) {
      timerRef.current = setInterval(() => setRecordingTime(prev => prev + 1), 1000);
    } else {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
      setRecordingTime(0);
    }
    return () => timerRef.current && clearInterval(timerRef.current);
  }, [isRecording]);

  const drawWaveform = useCallback(() => {
    if (!canvasRef.current || !analyserRef.current || !isRecording) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const analyser = analyserRef.current;
    const dataArray = dataArrayRef.current;

    const draw = () => {
      if (!isRecording) return;
      animationRef.current = requestAnimationFrame(draw);
      analyser.getByteFrequencyData(dataArray);

      ctx.fillStyle = "#f8f9fa";
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      const barWidth = (canvas.width / dataArray.length) * 2.5;
      let x = 0;

      for (let i = 0; i < dataArray.length; i++) {
        const barHeight = (dataArray[i] / 255) * canvas.height * 0.8;
        const gradient = ctx.createLinearGradient(0, canvas.height - barHeight, 0, canvas.height);
        gradient.addColorStop(0, '#25D366');
        gradient.addColorStop(1, '#128C7E');
        ctx.fillStyle = gradient;
        ctx.fillRect(x, canvas.height - barHeight, barWidth - 1, barHeight);
        x += barWidth;
      }
    };
    draw();
  }, [isRecording]);

  // STRATEGIC: WhatsApp-compliant OGG Opus encoding with proper headers
  const createWhatsAppCompliantOgg = async (audioBuffer) => {
    debugLog('🔧 Creating WhatsApp-compliant OGG file...');

    try {
      // Convert to the exact format WhatsApp expects
      const sampleRate = 48000; // WhatsApp standard
      const channels = 1; // Mono
      const bitDepth = 16;

      // Resample if necessary
      let processedBuffer = audioBuffer;
      if (audioBuffer.sampleRate !== sampleRate) {
        processedBuffer = await resampleAudioBuffer(audioBuffer, sampleRate);
      }

      // Create proper WAV with correct headers (most universally compatible)
      const wavBlob = createCompliantWAV(processedBuffer, sampleRate, channels, bitDepth);

      debugLog('✅ WhatsApp-compliant audio created', {
        sampleRate,
        channels,
        size: wavBlob.size,
        duration: processedBuffer.duration
      });

      return wavBlob;

    } catch (err) {
      debugLog('❌ Audio processing failed', err.message);
      throw err;
    }
  };

  // ENGINEERING: Proper audio resampling for format compliance
  const resampleAudioBuffer = async (audioBuffer, targetSampleRate) => {
    const offlineContext = new OfflineAudioContext(
      audioBuffer.numberOfChannels,
      audioBuffer.duration * targetSampleRate,
      targetSampleRate
    );

    const source = offlineContext.createBufferSource();
    source.buffer = audioBuffer;
    source.connect(offlineContext.destination);
    source.start();

    return await offlineContext.startRendering();
  };

  // STRATEGIC: Create WAV with WhatsApp-compatible headers and structure
  const createCompliantWAV = (audioBuffer, sampleRate, channels, bitDepth) => {
    const length = audioBuffer.length;
    const bytesPerSample = bitDepth / 8;
    const blockAlign = channels * bytesPerSample;
    const byteRate = sampleRate * blockAlign;
    const dataSize = length * blockAlign;
    const bufferSize = 44 + dataSize;

    const arrayBuffer = new ArrayBuffer(bufferSize);
    const view = new DataView(arrayBuffer);

    // WAV header with WhatsApp-compatible structure
    const writeString = (offset, string) => {
      for (let i = 0; i < string.length; i++) {
        view.setUint8(offset + i, string.charCodeAt(i));
      }
    };

    // RIFF header
    writeString(0, 'RIFF');
    view.setUint32(4, bufferSize - 8, true);
    writeString(8, 'WAVE');

    // Format chunk
    writeString(12, 'fmt ');
    view.setUint32(16, 16, true); // Format chunk size
    view.setUint16(20, 1, true);  // PCM format
    view.setUint16(22, channels, true);
    view.setUint32(24, sampleRate, true);
    view.setUint32(28, byteRate, true);
    view.setUint16(32, blockAlign, true);
    view.setUint16(34, bitDepth, true);

    // Data chunk
    writeString(36, 'data');
    view.setUint32(40, dataSize, true);

    // Convert audio data with proper quantization
    const channelData = audioBuffer.getChannelData(0);
    let offset = 44;

    for (let i = 0; i < length; i++) {
      // Proper dithering and quantization for WhatsApp compatibility
      let sample = channelData[i];
      sample = Math.max(-1, Math.min(1, sample)); // Clamp

      // Add subtle dither to prevent quantization artifacts
      const dither = (Math.random() - 0.5) / 32768;
      sample += dither;

      const intSample = sample < 0 ? sample * 0x8000 : sample * 0x7FFF;
      view.setInt16(offset, Math.round(intSample), true);
      offset += 2;
    }

    return new Blob([arrayBuffer], { type: 'audio/wav' });
  };

  const startRecording = async () => {
    try {
      debugLog('🎬 === RECORDING START SEQUENCE ===');
      setError(null);
      cleanup();

      if (permissionStatus !== 'granted') {
        const granted = await checkAndRequestPermissions();
        if (!granted) return;
      }

      // High-quality audio stream for WhatsApp compatibility
      const stream = await navigator.mediaDevices.getUserMedia({
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true,
          sampleRate: 48000,      // WhatsApp standard
          channelCount: 1,        // Mono for compatibility
          latency: 0.01,         // Low latency
        }
      });

      streamRef.current = stream;
      setAudioStream(stream);

      debugLog('🎤 High-quality stream acquired', {
        settings: stream.getTracks()[0]?.getSettings(),
        constraints: stream.getTracks()[0]?.getConstraints()
      });

      // Audio visualization setup
      const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
      if (audioCtx.state === 'suspended') await audioCtx.resume();

      const source = audioCtx.createMediaStreamSource(stream);
      const analyser = audioCtx.createAnalyser();
      analyser.fftSize = 256;
      analyser.smoothingTimeConstant = 0.3;
      source.connect(analyser);

      audioContextRef.current = audioCtx;
      analyserRef.current = analyser;
      dataArrayRef.current = new Uint8Array(analyser.frequencyBinCount);

      // MediaRecorder with optimal settings for WhatsApp
      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: 'audio/webm; codecs=opus',
        audioBitsPerSecond: 128000, // High quality
      });

      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.onstart = () => {
        debugLog('🎬 Recording started successfully');
        setIsRecording(true);
        drawWaveform();
      };

      mediaRecorder.ondataavailable = (event) => {
        if (event.data?.size > 0) {
          audioChunksRef.current.push(event.data);
          debugLog('📦 Audio chunk captured', { size: event.data.size });
        }
      };

      mediaRecorder.onstop = async () => {
        debugLog('⏹️ Recording stopped, processing audio...');
        setIsProcessing(true);

        if (audioChunksRef.current.length > 0) {
          const webmBlob = new Blob(audioChunksRef.current, { type: 'audio/webm' });

          try {
            // Convert to WhatsApp-compatible format
            const audioContext = new AudioContext();
            const arrayBuffer = await webmBlob.arrayBuffer();
            const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);
            const compliantBlob = await createWhatsAppCompliantOgg(audioBuffer);

            await createRecording(compliantBlob);

          } catch (conversionError) {
            debugLog('⚠️ Conversion failed, using original', conversionError.message);
            await createRecording(webmBlob);
          }
        } else {
          setError('No audio data captured');
        }

        setIsProcessing(false);
        audioChunksRef.current = [];
      };

      mediaRecorder.onerror = (event) => {
        debugLog('❌ Recording error', event.error);
        setError(`Recording failed: ${event.error?.message}`);
        cleanup();
      };

      mediaRecorder.start(100); // Frequent data collection

    } catch (err) {
      debugLog('💥 Recording initialization failed', err);
      setError(`Failed to start: ${err.message}`);
      cleanup();
    }
  };

  const stopRecording = () => {
    debugLog('⏹️ Stop recording requested');
    if (mediaRecorderRef.current?.state === 'recording') {
      mediaRecorderRef.current.stop();
    }
    setIsRecording(false);
    setTimeout(cleanup, 1500);
  };

  const createRecording = async (blob) => {
    const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, -5);
    const filename = `whatsapp-voice-${timestamp}.wav`; // WAV for maximum compatibility

    const url = URL.createObjectURL(blob);

    const recording = {
      id: Date.now(),
      url,
      blob,
      filename,
      duration: recordingTime,
      timestamp: new Date().toLocaleTimeString(),
      size: blob.size,
      isWhatsAppCompatible: true
    };

    setRecordings(prev => [...prev, recording]);
    debugLog('✅ WhatsApp-compatible recording created', recording);
  };

  // FIXED: Proper file drag implementation (not image drag)
  const handleDragStart = (e, recording) => {
    e.preventDefault();
    e.stopPropagation();

    // Create proper file data for drag operation
    const fileData = `${recording.blob.type}:${recording.filename}:${recording.url}`;

    e.dataTransfer.setData('DownloadURL', fileData);
    e.dataTransfer.setData('text/plain', recording.filename);
    e.dataTransfer.effectAllowed = 'copy';

    // Custom drag image showing file info
    const dragImg = document.createElement('div');
    dragImg.innerHTML = `🎤 ${recording.filename}<br/>📁 ${formatFileSize(recording.size)}`;
    dragImg.style.cssText = `
      position: absolute; top: -1000px; 
      padding: 12px; background: #dcf8c6; 
      border: 2px solid #25D366; border-radius: 8px;
      font-size: 14px; font-weight: 500;
    `;
    document.body.appendChild(dragImg);

    e.dataTransfer.setDragImage(dragImg, 0, 0);
    setTimeout(() => document.body.removeChild(dragImg), 0);

    debugLog('🎯 File drag initiated', { filename: recording.filename, size: recording.size });
  };

  const downloadRecording = async (recording) => {
    try {
      if ('showSaveFilePicker' in window) {
        const fileHandle = await window.showSaveFilePicker({
          suggestedName: recording.filename,
          types: [{
            description: 'WhatsApp Voice Messages',
            accept: { 'audio/wav': ['.wav'], 'audio/ogg': ['.ogg'] }
          }]
        });

        const writable = await fileHandle.createWritable();
        await writable.write(recording.blob);
        await writable.close();

        debugLog('✅ File saved via modern File System API');
      } else {
        const link = document.createElement('a');
        link.href = recording.url;
        link.download = recording.filename;
        link.click();
        debugLog('✅ File downloaded via traditional method');
      }
    } catch (err) {
      if (err.name !== 'AbortError') {
        debugLog('❌ Download failed', err.message);
        setError(`Download failed: ${err.message}`);
      }
    }
  };

  const removeRecording = (index) => {
    setRecordings(prev => {
      const recording = prev[index];
      URL.revokeObjectURL(recording.url);
      return prev.filter((_, i) => i !== index);
    });
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  return (
    <div className="bg-[#ece5dd] min-h-screen p-4 space-y-4">
      {/* Status header */}
      <div className="flex items-center justify-between text-sm bg-white/70 p-3 rounded-lg shadow-sm">
        <span className="flex items-center space-x-2">
          <span>🎙️</span>
          <span>{permissionStatus === 'granted' ? '✅ Ready' : 
                 permissionStatus === 'denied' ? '❌ Access Denied' : '⏳ Checking...'}</span>
        </span>
        {isRecording && (
          <span className="text-red-600 font-medium animate-pulse flex items-center space-x-1">
            <span className="w-2 h-2 bg-red-500 rounded-full"></span>
            <span>REC {recordingTime}s</span>
          </span>
        )}
        {isProcessing && (
          <span className="text-blue-600 flex items-center space-x-1">
            <span className="animate-spin">⚙️</span>
            <span>Processing...</span>
          </span>
        )}
      </div>

      {/* Error display */}
      {error && (
        <div className="bg-red-50 border-l-4 border-red-400 p-4 rounded">
          <div className="flex justify-between items-start">
            <span className="text-red-700">{error}</span>
            <button 
              onClick={() => setError(null)}
              className="text-red-400 hover:text-red-600 font-bold"
            >
              ×
            </button>
          </div>
        </div>
      )}

      {/* Recording controls */}
      <div className="flex items-center space-x-3">
        {!isRecording ? (
          <button
            onClick={startRecording}
            disabled={permissionStatus !== 'granted'}
            className={`flex items-center space-x-2 px-6 py-3 rounded-full shadow-md transition-all ${
              permissionStatus !== 'granted' 
                ? 'bg-gray-300 text-gray-500 cursor-not-allowed' 
                : 'bg-white border-2 border-[#25D366] hover:bg-[#25D366] hover:text-white text-[#25D366]'
            }`}
          >
            <span>🎙️</span>
            <span className="font-medium">Start Recording</span>
          </button>
        ) : (
          <button
            onClick={stopRecording}
            className="flex items-center space-x-2 bg-red-500 hover:bg-red-600 text-white px-6 py-3 rounded-full shadow-md relative transition-all"
          >
            <span>⏹️</span>
            <span className="font-medium">Stop ({recordingTime}s)</span>
            <span className="absolute -top-1 -right-1 w-3 h-3 bg-red-300 rounded-full animate-ping"></span>
          </button>
        )}
      </div>

      {/* Waveform visualization */}
      {isRecording && (
        <div className="bg-white p-4 rounded-lg shadow-md">
          <canvas
            ref={canvasRef}
            width={500}
            height={80}
            className="w-full border rounded"
          />
          <div className="text-center text-sm text-gray-600 mt-2 flex items-center justify-center space-x-2">
            <span className="animate-pulse">🎵</span>
            <span>Recording high-quality audio... {recordingTime}s</span>
          </div>
        </div>
      )}

      {/* WhatsApp-style message list */}
      <div className="space-y-3">
        {recordings.map((rec, index) => (
          <div
            key={rec.id}
            draggable
            onDragStart={(e) => handleDragStart(e, rec)}
            className="flex items-center space-x-4 bg-[#dcf8c6] p-4 rounded-xl shadow-md hover:shadow-lg transition-all cursor-move border-l-4 border-[#25D366] group"
          >
            <div className="w-12 h-12 bg-[#25D366] rounded-full flex items-center justify-center flex-shrink-0">
              <span className="text-white text-lg">🎤</span>
            </div>

            <div className="flex-1 min-w-0">
              <audio controls src={rec.url} className="w-full mb-2" />
              <div className="text-sm text-gray-600 flex justify-between items-center">
                <span className="flex items-center space-x-2">
                  <span>⏱️ {rec.duration}s</span>
                  <span>📁 {formatFileSize(rec.size)}</span>
                  <span className="text-green-600 font-medium">✅ WhatsApp Ready</span>
                </span>
                <span>{rec.timestamp}</span>
              </div>
              <div className="text-xs text-[#25D366] font-medium mt-1">
                📱 {rec.filename}
              </div>
            </div>

            <div className="flex flex-col space-y-2 opacity-0 group-hover:opacity-100 transition-opacity">
              <button
                onClick={() => downloadRecording(rec)}
                className="px-3 py-2 bg-[#25D366] hover:bg-[#128C7E] text-white rounded text-sm font-medium transition-colors"
                title="Save to device"
              >
                💾 Save
              </button>
              <button
                onClick={() => removeRecording(index)}
                className="px-3 py-2 bg-red-500 hover:bg-red-600 text-white rounded text-sm font-medium transition-colors"
                title="Delete recording"
              >
                🗑️ Delete
              </button>
            </div>

            <div className="text-2xl opacity-50 group-hover:opacity-100 transition-opacity" title="Drag to share">
              ↗️
            </div>
          </div>
        ))}
      </div>

      {recordings.length === 0 && !isRecording && (
        <div className="text-center text-gray-500 py-12">
          <div className="text-6xl mb-4">🎤</div>
          <h3 className="text-xl font-medium mb-2">Ready to Record</h3>
          <p className="text-sm">High-quality voice messages for WhatsApp</p>
        </div>
      )}

      {/* Enhanced usage guide */}
      {recordings.length > 0 && (
        <div className="bg-gradient-to-r from-green-50 to-blue-50 border border-green-200 p-5 rounded-lg">
          <h3 className="font-semibold text-green-800 mb-3 flex items-center space-x-2">
            <span>📱</span>
            <span>WhatsApp Integration Guide</span>
          </h3>
          <div className="grid md:grid-cols-2 gap-4 text-sm text-green-700">
            <div>
              <h4 className="font-medium mb-2">💾 Save Method:</h4>
              <ul className="space-y-1 list-disc list-inside">
                <li>Click "Save" to download WAV file</li>
                <li>Files are WhatsApp-compatible format</li>
                <li>High-quality 48kHz mono audio</li>
              </ul>
            </div>
            <div>
              <h4 className="font-medium mb-2">📤 Share Options:</h4>
              <ul className="space-y-1 list-disc list-inside">
                <li>WhatsApp Desktop: Attach saved file</li>
                <li>Mobile: Share via file picker</li>
                <li>Drag files to desktop/folders</li>
              </ul>
            </div>
          </div>
        </div>
      )}

      {/* Collapsible debug panel */}
      {Object.keys(debugInfo).length > 0 && (
        <details className="bg-gray-50 border border-gray-200 rounded p-3">
          <summary className="cursor-pointer font-medium text-gray-700 hover:text-gray-900">
            🔧 Technical Log ({Object.keys(debugInfo).length} events)
          </summary>
          <div className="mt-3 space-y-1 max-h-48 overflow-y-auto text-xs font-mono">
            {Object.entries(debugInfo)
              .slice(-10)
              .map(([key, info]) => (
                <div key={key} className="flex items-start space-x-3 py-1">
                  <span className="text-gray-400 min-w-20">{info.time}</span>
                  <span className="text-gray-700">{info.message}</span>
                  {info.data && (
                    <span className="text-blue-600 text-xs">
                      {JSON.stringify(info.data).slice(0, 80)}...
                    </span>
                  )}
                </div>
              ))}
          </div>
        </details>
      )}
    </div>
  );
}
