import React, { useState, useRef, useEffect, useCallback } from "react";

export default function WhatsAppAudioRecorder() {
  const [recordings, setRecordings] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [audioStream, setAudioStream] = useState(null);
  const [permissionStatus, setPermissionStatus] = useState('checking');
  const [error, setError] = useState(null);
  const [debugInfo, setDebugInfo] = useState({});

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const timerRef = useRef(null);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const dataArrayRef = useRef(null);
  const animationRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  // Debug logger
  const debugLog = (message, data = null) => {
    console.log(`üé§ ${message}`, data);
    setDebugInfo(prev => ({
      ...prev,
      [`${Date.now()}`]: { message, data, time: new Date().toLocaleTimeString() }
    }));
  };

  // Comprehensive permission check with fallback
  const checkAndRequestPermissions = async () => {
    try {
      debugLog('Checking microphone permissions...');
      setPermissionStatus('checking');

      // Method 1: Direct getUserMedia test
      const testStream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true,
        } 
      });

      debugLog('Permission test successful', {
        tracks: testStream.getTracks().length,
        settings: testStream.getTracks()[0]?.getSettings()
      });

      // Clean up test stream immediately
      testStream.getTracks().forEach(track => {
        track.stop();
        debugLog('Test track stopped', track.readyState);
      });

      setPermissionStatus('granted');
      return true;

    } catch (err) {
      debugLog('Permission denied', { error: err.message, name: err.name });
      setPermissionStatus('denied');
      setError(`Microphone access required: ${err.message}`);
      return false;
    }
  };

  useEffect(() => {
    checkAndRequestPermissions();
  }, []);

  // Cleanup with extensive logging
  const cleanup = useCallback(() => {
    debugLog('Starting cleanup...');

    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
      debugLog('Timer cleared');
    }

    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
      animationRef.current = null;
      debugLog('Animation frame cleared');
    }

    if (mediaRecorderRef.current) {
      const state = mediaRecorderRef.current.state;
      debugLog('MediaRecorder cleanup', { state });

      if (state === 'recording') {
        mediaRecorderRef.current.stop();
        debugLog('MediaRecorder stopped');
      }
      mediaRecorderRef.current = null;
    }

    if (audioContextRef.current && audioContextRef.current.state !== 'closed') {
      audioContextRef.current.close();
      audioContextRef.current = null;
      debugLog('AudioContext closed');
    }

    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => {
        track.stop();
        debugLog('Stream track stopped', track.readyState);
      });
      streamRef.current = null;
      setAudioStream(null);
    }

    setIsRecording(false);
    setRecordingTime(0);
    debugLog('Cleanup completed');
  }, []);

  useEffect(() => {
    return cleanup;
  }, [cleanup]);

  // Enhanced timer with debug
  useEffect(() => {
    if (isRecording) {
      debugLog('Starting recording timer');
      timerRef.current = setInterval(() => {
        setRecordingTime(prev => {
          const newTime = prev + 1;
          if (newTime % 5 === 0) debugLog(`Recording time: ${newTime}s`);
          return newTime;
        });
      }, 1000);
    } else {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
      setRecordingTime(0);
    }

    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, [isRecording]);

  // Fixed waveform with proper canvas handling
  const drawWaveform = useCallback(() => {
    if (!canvasRef.current || !analyserRef.current || !isRecording) {
      return;
    }

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const analyser = analyserRef.current;
    const dataArray = dataArrayRef.current;

    const draw = () => {
      if (!isRecording || !analyserRef.current) return;

      animationRef.current = requestAnimationFrame(draw);
      analyser.getByteFrequencyData(dataArray);

      // Clear canvas
      ctx.fillStyle = "#f8f9fa";
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // Draw bars
      const barWidth = canvas.width / dataArray.length * 2;
      let x = 0;

      for (let i = 0; i < dataArray.length; i++) {
        const barHeight = (dataArray[i] / 255) * canvas.height * 0.7;

        const hue = (i / dataArray.length) * 120; // Green to blue
        ctx.fillStyle = `hsl(${hue + 100}, 60%, 50%)`;
        ctx.fillRect(x, canvas.height - barHeight, barWidth - 1, barHeight);

        x += barWidth;
      }
    };

    draw();
  }, [isRecording]);

  // Robust recording start with extensive error handling
  const startRecording = async () => {
    try {
      debugLog('=== STARTING RECORDING ===');
      setError(null);
      cleanup();

      if (permissionStatus !== 'granted') {
        const granted = await checkAndRequestPermissions();
        if (!granted) return;
      }

      // Get audio stream with detailed logging
      debugLog('Requesting audio stream...');
      const stream = await navigator.mediaDevices.getUserMedia({
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true,
          sampleRate: 48000,
          channelCount: 1,
        }
      });

      streamRef.current = stream;
      setAudioStream(stream);

      const tracks = stream.getTracks();
      debugLog('Stream acquired', {
        trackCount: tracks.length,
        settings: tracks[0]?.getSettings(),
        constraints: tracks[0]?.getConstraints()
      });

      // Setup audio context
      debugLog('Setting up audio context...');
      const audioCtx = new (window.AudioContext || window.webkitAudioContext)();

      if (audioCtx.state === 'suspended') {
        await audioCtx.resume();
        debugLog('AudioContext resumed');
      }

      const source = audioCtx.createMediaStreamSource(stream);
      const analyser = audioCtx.createAnalyser();
      analyser.fftSize = 256;
      analyser.smoothingTimeConstant = 0.3;
      source.connect(analyser);

      audioContextRef.current = audioCtx;
      analyserRef.current = analyser;
      dataArrayRef.current = new Uint8Array(analyser.frequencyBinCount);

      // Test supported formats with detailed logging
      const testFormats = [
        'audio/ogg; codecs=opus',
        'audio/webm; codecs=opus', 
        'audio/webm',
        'audio/ogg',
        'audio/mp4',
        'audio/wav'
      ];

      let selectedFormat = null;
      for (const format of testFormats) {
        if (MediaRecorder.isTypeSupported(format)) {
          selectedFormat = format;
          debugLog('Format supported', format);
          break;
        } else {
          debugLog('Format not supported', format);
        }
      }

      if (!selectedFormat) {
        throw new Error('No supported audio format found');
      }

      debugLog('Creating MediaRecorder...', { format: selectedFormat });

      // Create MediaRecorder with comprehensive event handling
      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: selectedFormat,
        audioBitsPerSecond: selectedFormat.includes('opus') ? 64000 : 128000,
      });

      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      // Comprehensive event handlers
      mediaRecorder.onstart = () => {
        debugLog('MediaRecorder started', mediaRecorder.state);
        setIsRecording(true);
        drawWaveform();
      };

      mediaRecorder.ondataavailable = (event) => {
        debugLog('Data available', { 
          size: event.data.size, 
          type: event.data.type,
          totalChunks: audioChunksRef.current.length + 1
        });

        if (event.data && event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = () => {
        debugLog('MediaRecorder stopped', {
          chunks: audioChunksRef.current.length,
          totalSize: audioChunksRef.current.reduce((sum, chunk) => sum + chunk.size, 0)
        });

        if (audioChunksRef.current.length > 0) {
          const blob = new Blob(audioChunksRef.current, { type: selectedFormat });
          debugLog('Final blob created', { size: blob.size, type: blob.type });

          if (blob.size > 0) {
            createRecording(blob, selectedFormat);
          } else {
            setError('Recording produced empty file');
          }
        } else {
          setError('No audio data captured during recording');
        }

        audioChunksRef.current = [];
      };

      mediaRecorder.onerror = (event) => {
        debugLog('MediaRecorder error', event.error);
        setError(`Recording failed: ${event.error?.message || 'Unknown error'}`);
        cleanup();
      };

      // Start recording with frequent data collection
      debugLog('Starting MediaRecorder...');
      mediaRecorder.start(100); // Collect data every 100ms for better reliability

    } catch (err) {
      debugLog('Recording start failed', { error: err.message, stack: err.stack });
      setError(`Failed to start recording: ${err.message}`);
      cleanup();
    }
  };

  const stopRecording = () => {
    debugLog('=== STOPPING RECORDING ===');

    if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
      mediaRecorderRef.current.stop();
      debugLog('Stop command sent to MediaRecorder');
    } else {
      debugLog('MediaRecorder not in recording state', 
        mediaRecorderRef.current?.state || 'no recorder');
    }

    setIsRecording(false);

    // Delayed cleanup to ensure onstop fires
    setTimeout(() => {
      if (mediaRecorderRef.current?.state === 'recording') {
        debugLog('Force stopping MediaRecorder after timeout');
        cleanup();
      }
    }, 2000);
  };

  const createRecording = (blob, mimeType) => {
    const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, -5);
    const extension = mimeType.includes('ogg') ? 'ogg' : 
                     mimeType.includes('webm') ? 'webm' : 'audio';
    const filename = `voice-${timestamp}.${extension}`;

    const url = URL.createObjectURL(blob);

    // Auto-download functionality
    const downloadLink = document.createElement('a');
    downloadLink.href = url;
    downloadLink.download = filename;
    downloadLink.style.display = 'none';
    document.body.appendChild(downloadLink);

    const recording = {
      id: Date.now(),
      url,
      blob,
      filename,
      duration: recordingTime,
      timestamp: new Date().toLocaleTimeString(),
      size: blob.size,
      mimeType,
      downloadLink
    };

    setRecordings(prev => [...prev, recording]);
    debugLog('Recording created', recording);
  };

  // Drag and drop handlers
  const handleDragStart = (e, recording) => {
    e.dataTransfer.setData('DownloadURL', 
      `${recording.mimeType}:${recording.filename}:${recording.url}`);
    e.dataTransfer.effectAllowed = 'copy';
  };

  const downloadRecording = (recording) => {
    recording.downloadLink.click();
  };

  const removeRecording = (index) => {
    setRecordings(prev => {
      const recording = prev[index];
      URL.revokeObjectURL(recording.url);
      if (recording.downloadLink && document.body.contains(recording.downloadLink)) {
        document.body.removeChild(recording.downloadLink);
      }
      return prev.filter((_, i) => i !== index);
    });
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  return (
    <div className="bg-[#ece5dd] min-h-screen p-4 space-y-4">
      {/* Simple status bar */}
      <div className="flex items-center justify-between text-sm">
        <span>
          üéôÔ∏è {permissionStatus === 'granted' ? '‚úÖ Ready' : 
               permissionStatus === 'denied' ? '‚ùå No Access' : 
               permissionStatus === 'checking' ? '‚è≥ Checking...' : '‚ùì Unknown'}
        </span>
        {isRecording && (
          <span className="text-red-600 font-medium animate-pulse">
            üî¥ REC {recordingTime}s
          </span>
        )}
      </div>

      {/* Error display */}
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
          <button 
            onClick={() => setError(null)}
            className="float-right text-red-500 hover:text-red-700 font-bold"
          >
            √ó
          </button>
        </div>
      )}

      {/* Recording controls */}
      <div className="flex items-center space-x-2">
        {!isRecording ? (
          <button
            onClick={startRecording}
            disabled={permissionStatus !== 'granted'}
            className={`px-4 py-2 rounded-full shadow-md transition-colors ${
              permissionStatus !== 'granted' 
                ? 'bg-gray-300 text-gray-500 cursor-not-allowed' 
                : 'bg-white border border-gray-300 hover:bg-gray-100 text-black'
            }`}
            title={permissionStatus !== 'granted' ? 'Microphone access required' : 'Start recording'}
          >
            üéôÔ∏è
          </button>
        ) : (
          <button
            onClick={stopRecording}
            className="bg-white border border-gray-300 hover:bg-gray-100 text-black px-4 py-2 rounded-full shadow-md relative"
            title="Stop recording"
          >
            ‚èπÔ∏è {recordingTime}s
            <span className="absolute -top-3 right-0 w-3 h-3 bg-red-500 rounded-full animate-ping" />
          </button>
        )}
      </div>

      {/* Waveform visualization */}
      {isRecording && (
        <div className="bg-white p-3 rounded-lg shadow">
          <canvas
            ref={canvasRef}
            width={400}
            height={60}
            className="w-full border rounded"
          />
          <div className="text-center text-xs text-gray-500 mt-1">
            üéµ Recording... {recordingTime}s
          </div>
        </div>
      )}

      {/* WhatsApp-style message list */}
      <div className="space-y-3">
        {recordings.map((rec, index) => (
          <div
            key={rec.id}
            draggable
            onDragStart={(e) => handleDragStart(e, rec)}
            className="flex items-center space-x-3 bg-[#dcf8c6] p-3 rounded-xl shadow-md w-fit max-w-full cursor-move hover:shadow-lg transition-shadow"
          >
            <img
              src="https://www.gravatar.com/avatar?d=mp"
              alt="Avatar"
              className="w-10 h-10 rounded-full"
            />
            <div className="flex flex-col">
              <audio controls src={rec.url} className="w-60" />
              <div className="text-xs text-gray-500 flex justify-between mt-1">
                <span>{rec.duration}s ‚Ä¢ {formatFileSize(rec.size)}</span>
                <span>{rec.timestamp}</span>
              </div>
            </div>
            <button
              onClick={() => downloadRecording(rec)}
              className="text-green-700 hover:text-green-900 p-1"
              title="Download"
            >
              üíæ
            </button>
            <button
              onClick={() => removeRecording(index)}
              className="text-red-700 hover:text-red-900 p-1"
              title="Delete"
            >
              üóëÔ∏è
            </button>
          </div>
        ))}
      </div>

      {recordings.length === 0 && !isRecording && (
        <div className="text-center text-gray-500 py-8">
          <div className="text-4xl mb-2">üé§</div>
          <p>Click the microphone to start recording</p>
        </div>
      )}

      {/* Debug panel (toggle-able) */}
      {Object.keys(debugInfo).length > 0 && (
        <details className="mt-8 p-3 bg-gray-100 rounded text-xs">
          <summary className="cursor-pointer font-medium">üîß Debug Log ({Object.keys(debugInfo).length} entries)</summary>
          <div className="mt-2 space-y-1 max-h-40 overflow-y-auto">
            {Object.entries(debugInfo)
              .slice(-10) // Show last 10 entries
              .map(([key, info]) => (
                <div key={key} className="flex items-start space-x-2">
                  <span className="text-gray-400">{info.time}</span>
                  <span className="font-mono">{info.message}</span>
                  {info.data && <span className="text-blue-600">{JSON.stringify(info.data)}</span>}
                </div>
              ))}
          </div>
        </details>
      )}
    </div>
  );
}
