import React, { useState, useRef, useEffect, useCallback } from "react";

export default function WhatsAppAudioRecorder() {
  const [recordings, setRecordings] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [audioStream, setAudioStream] = useState(null);
  const [permissionStatus, setPermissionStatus] = useState('prompt'); // 'granted', 'denied', 'prompt'
  const [error, setError] = useState(null);

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const timerRef = useRef(null);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const dataArrayRef = useRef(null);
  const animationRef = useRef(null);
  const canvasRef = useRef(null);

  // Check microphone permissions on component mount
  useEffect(() => {
    checkPermissions();
  }, []);

  const checkPermissions = async () => {
    try {
      const result = await navigator.permissions.query({ name: 'microphone' });
      setPermissionStatus(result.state);

      result.addEventListener('change', () => {
        setPermissionStatus(result.state);
        if (result.state === 'denied' && isRecording) {
          stopRecording();
        }
      });
    } catch (err) {
      console.warn('Permission API not supported, fallback to getUserMedia');
    }
  };

  // Cleanup function to properly stop all recording activities
  const cleanup = useCallback(() => {
    // Stop timer
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }

    // Stop animation
    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
      animationRef.current = null;
    }

    // Close audio context
    if (audioContextRef.current && audioContextRef.current.state !== 'closed') {
      audioContextRef.current.close();
      audioContextRef.current = null;
    }

    // Stop media recorder
    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
      try {
        mediaRecorderRef.current.stop();
      } catch (e) {
        console.warn('MediaRecorder was already stopped');
      }
      mediaRecorderRef.current = null;
    }

    // Stop audio stream
    if (audioStream) {
      audioStream.getTracks().forEach(track => {
        track.stop();
      });
      setAudioStream(null);
    }

    // Reset states
    setIsRecording(false);
    setRecordingTime(0);
    setError(null);
  }, [audioStream]);

  // Main cleanup useEffect
  useEffect(() => {
    return cleanup;
  }, [cleanup]);

  // Timer effect
  useEffect(() => {
    if (isRecording) {
      timerRef.current = setInterval(() => {
        setRecordingTime((prev) => prev + 1);
      }, 1000);
    } else {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
      setRecordingTime(0);
    }

    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, [isRecording]);

  const drawWaveform = useCallback(() => {
    if (!canvasRef.current || !analyserRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const analyser = analyserRef.current;
    const dataArray = dataArrayRef.current;

    const draw = () => {
      if (!isRecording) return; // Stop drawing if not recording

      animationRef.current = requestAnimationFrame(draw);
      analyser.getByteTimeDomainData(dataArray);

      ctx.fillStyle = "#fff";
      ctx.fillRect(0, 0, canvas.width, canvas.height);
      ctx.lineWidth = 2;
      ctx.strokeStyle = "#25D366";
      ctx.beginPath();

      const sliceWidth = (canvas.width * 1.0) / dataArray.length;
      let x = 0;

      for (let i = 0; i < dataArray.length; i++) {
        const v = dataArray[i] / 128.0;
        const y = (v * canvas.height) / 2;

        if (i === 0) {
          ctx.moveTo(x, y);
        } else {
          ctx.lineTo(x, y);
        }

        x += sliceWidth;
      }
      ctx.lineTo(canvas.width, canvas.height / 2);
      ctx.stroke();
    };

    draw();
  }, [isRecording]);

  const startRecording = async () => {
    try {
      setError(null);

      // First, clean up any existing recording
      cleanup();

      // Check permissions first
      await checkPermissions();
      if (permissionStatus === 'denied') {
        setError('Microphone access denied. Please enable microphone permissions.');
        return;
      }

      // Get new stream
      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          sampleRate: 44100
        } 
      });

      setAudioStream(stream);

      // Set up audio context for visualization
      const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
      const source = audioCtx.createMediaStreamSource(stream);
      const analyser = audioCtx.createAnalyser();
      source.connect(analyser);
      analyser.fftSize = 2048;
      const bufferLength = analyser.frequencyBinCount;
      const dataArray = new Uint8Array(bufferLength);

      audioContextRef.current = audioCtx;
      analyserRef.current = analyser;
      dataArrayRef.current = dataArray;

      // Set up media recorder
      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: MediaRecorder.isTypeSupported("audio/webm") ? "audio/webm" : "audio/ogg",
        audioBitsPerSecond: 128000,
      });

      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = () => {
        const mimeType = mediaRecorder.mimeType || 'audio/webm';
        const blob = new Blob(audioChunksRef.current, { type: mimeType });
        const url = URL.createObjectURL(blob);

        setRecordings((prev) => [...prev, { 
          url, 
          blob, 
          duration: recordingTime,
          timestamp: new Date().toLocaleTimeString()
        }]);

        audioChunksRef.current = [];
      };

      mediaRecorder.onerror = (event) => {
        console.error('MediaRecorder error:', event);
        setError('Recording failed: ' + event.error);
        cleanup();
      };

      // Start recording
      mediaRecorder.start(100); // Collect data every 100ms
      setIsRecording(true);

      // Start waveform drawing
      drawWaveform();

    } catch (err) {
      console.error("Recording error:", err);
      setError(`Recording failed: ${err.message}`);
      cleanup();
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
      mediaRecorderRef.current.stop();
    }
    cleanup();
  };

  const copyToClipboard = async (blob) => {
    try {
      if (!navigator.clipboard.write) {
        throw new Error('Clipboard API not supported');
      }

      const file = new File([blob], "voice.webm", { type: blob.type });
      const item = new ClipboardItem({ [file.type]: file });
      await navigator.clipboard.write([item]);
      alert("Audio copied to clipboard!");
    } catch (err) {
      console.error("Clipboard write failed:", err);
      alert("Copy failed: " + err.message);
    }
  };

  const saveRecording = (blob) => {
    const a = document.createElement("a");
    a.href = URL.createObjectURL(blob);
    a.download = `recording_${Date.now()}.webm`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(a.href);
  };

  const removeRecording = (index) => {
    setRecordings((prev) => {
      const newRecordings = prev.filter((_, i) => i !== index);
      // Clean up object URL to prevent memory leaks
      URL.revokeObjectURL(prev[index].url);
      return newRecordings;
    });
  };

  return (
    <div className="bg-[#ece5dd] min-h-screen p-4 space-y-4">
      {/* Permission Status */}
      <div className="text-sm text-gray-600 mb-2">
        Microphone: {permissionStatus === 'granted' ? '‚úÖ Granted' : 
                     permissionStatus === 'denied' ? '‚ùå Denied' : '‚è≥ Pending'}
      </div>

      {/* Error Display */}
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
          <button 
            onClick={() => setError(null)}
            className="float-right text-red-500 hover:text-red-700"
          >
            √ó
          </button>
        </div>
      )}

      {/* Recording Controls */}
      <div className="flex items-center space-x-2">
        {!isRecording ? (
          <button
            onClick={startRecording}
            disabled={permissionStatus === 'denied'}
            className={`px-4 py-2 rounded-full shadow-md transition-colors ${
              permissionStatus === 'denied' 
                ? 'bg-gray-300 text-gray-500 cursor-not-allowed' 
                : 'bg-white border border-gray-300 hover:bg-gray-100 text-black'
            }`}
            title={permissionStatus === 'denied' ? 'Microphone access denied' : 'Start recording'}
          >
            üéôÔ∏è Start Recording
          </button>
        ) : (
          <button
            onClick={stopRecording}
            className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-full shadow-md relative transition-colors"
            title="Stop recording"
          >
            ‚èπÔ∏è Stop ({recordingTime}s)
            <span className="absolute -top-1 -right-1 w-3 h-3 bg-red-400 rounded-full animate-ping" />
          </button>
        )}
      </div>

      {/* Waveform Visualization */}
      {isRecording && (
        <div className="bg-white p-3 rounded-lg shadow">
          <canvas
            ref={canvasRef}
            width={400}
            height={80}
            className="w-full border rounded"
          />
          <div className="text-center text-sm text-gray-500 mt-2">
            Recording... {recordingTime}s
          </div>
        </div>
      )}

      {/* Recordings List */}
      <div className="space-y-3">
        {recordings.length === 0 ? (
          <div className="text-center text-gray-500 py-8">
            No recordings yet. Click "Start Recording" to begin.
          </div>
        ) : (
          recordings.map((rec, index) => (
            <div
              key={index}
              className="flex items-center space-x-3 bg-[#dcf8c6] p-3 rounded-xl shadow-md w-fit max-w-full"
            >
              <img
                src="https://www.gravatar.com/avatar?d=mp"
                alt="Avatar"
                className="w-10 h-10 rounded-full flex-shrink-0"
              />
              <div className="flex flex-col min-w-0 flex-1">
                <audio controls src={rec.url} className="w-60 min-w-0" />
                <div className="text-xs text-gray-500 flex justify-between">
                  <span>{rec.duration}s</span>
                  <span>{rec.timestamp}</span>
                </div>
              </div>
              <div className="flex space-x-1 flex-shrink-0">
                <button
                  onClick={() => copyToClipboard(rec.blob)}
                  className="text-green-700 hover:text-green-900 p-1 rounded transition-colors"
                  title="Copy to clipboard"
                >
                  üìã
                </button>
                <button
                  onClick={() => saveRecording(rec.blob)}
                  className="text-blue-700 hover:text-blue-900 p-1 rounded transition-colors"
                  title="Download recording"
                >
                  üíæ
                </button>
                <button
                  onClick={() => removeRecording(index)}
                  className="text-red-700 hover:text-red-900 p-1 rounded transition-colors"
                  title="Delete recording"
                >
                  üóëÔ∏è
                </button>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Debug Info */}
      {process.env.NODE_ENV === 'development' && (
        <div className="mt-8 p-4 bg-gray-100 rounded text-xs">
          <h4 className="font-bold mb-2">Debug Info:</h4>
          <div>Recording: {isRecording ? 'Yes' : 'No'}</div>
          <div>Stream: {audioStream ? 'Active' : 'None'}</div>
          <div>MediaRecorder: {mediaRecorderRef.current?.state || 'None'}</div>
          <div>Permission: {permissionStatus}</div>
        </div>
      )}
    </div>
  );
}
