import React, { useState, useRef, useEffect, useCallback } from "react";

export default function WhatsAppAudioRecorder() {
  const [recordings, setRecordings] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [audioStream, setAudioStream] = useState(null);
  const [permissionStatus, setPermissionStatus] = useState('checking');
  const [error, setError] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [draggedItem, setDraggedItem] = useState(null);
  const [fileSystemHandle, setFileSystemHandle] = useState(null);
  const [processingStatus, setProcessingStatus] = useState('');

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const timerRef = useRef(null);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const dataArrayRef = useRef(null);
  const animationRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  const debugLog = (message, data = null) => {
    console.log(`🎤 ${message}`, data);
  };

  // BREAKTHROUGH: Setup persistent file system access for drag operations
  const setupFileSystemAccess = async () => {
    try {
      if ('showDirectoryPicker' in window) {
        const dirHandle = await window.showDirectoryPicker({
          mode: 'readwrite',
          startIn: 'desktop'
        });
        setFileSystemHandle(dirHandle);
        debugLog('✅ File system access granted', dirHandle.name);
        return dirHandle;
      }
    } catch (err) {
      if (err.name !== 'AbortError') {
        debugLog('⚠️ File system access declined', err.message);
      }
    }
    return null;
  };

  const checkAndRequestPermissions = async () => {
    try {
      setPermissionStatus('checking');

      // Request both microphone and file system access
      const testStream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: false,    // STRATEGIC: Disable for better quality
          noiseSuppression: false,    // WhatsApp handles this
          autoGainControl: false,     // Manual control for consistency
          sampleRate: 48000,          // High quality capture
          channelCount: 2,            // Stereo capture, mono conversion later
        } 
      });

      debugLog('✅ Microphone access granted');
      testStream.getTracks().forEach(track => track.stop());
      setPermissionStatus('granted');

      // Prompt for file system access
      await setupFileSystemAccess();

      return true;

    } catch (err) {
      debugLog('❌ Permissions denied', err.message);
      setPermissionStatus('denied');
      setError(`Microphone required: ${err.message}`);
      return false;
    }
  };

  useEffect(() => {
    checkAndRequestPermissions();
  }, []);

  const cleanup = useCallback(() => {
    [timerRef, animationRef].forEach(ref => {
      if (ref.current) {
        ref === timerRef ? clearInterval(ref.current) : cancelAnimationFrame(ref.current);
        ref.current = null;
      }
    });

    if (mediaRecorderRef.current?.state !== 'inactive') {
      try {
        mediaRecorderRef.current?.stop();
      } catch (e) {
        debugLog('⚠️ MediaRecorder cleanup', e.message);
      }
      mediaRecorderRef.current = null;
    }

    if (audioContextRef.current?.state !== 'closed') {
      audioContextRef.current?.close();
      audioContextRef.current = null;
    }

    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
      setAudioStream(null);
    }

    setIsRecording(false);
    setRecordingTime(0);
  }, []);

  useEffect(() => cleanup, [cleanup]);

  useEffect(() => {
    if (isRecording) {
      timerRef.current = setInterval(() => setRecordingTime(prev => prev + 1), 1000);
    } else {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
      setRecordingTime(0);
    }
    return () => timerRef.current && clearInterval(timerRef.current);
  }, [isRecording]);

  const drawWaveform = useCallback(() => {
    if (!canvasRef.current || !analyserRef.current || !isRecording) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const analyser = analyserRef.current;
    const dataArray = dataArrayRef.current;

    const draw = () => {
      if (!isRecording) return;
      animationRef.current = requestAnimationFrame(draw);
      analyser.getByteFrequencyData(dataArray);

      ctx.fillStyle = "#f0f2f5";
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      const barWidth = (canvas.width / dataArray.length) * 2.5;
      let x = 0;

      for (let i = 0; i < dataArray.length; i++) {
        const barHeight = (dataArray[i] / 255) * canvas.height * 0.9;
        const gradient = ctx.createLinearGradient(0, canvas.height - barHeight, 0, canvas.height);
        gradient.addColorStop(0, '#25D366');
        gradient.addColorStop(0.5, '#128C7E');
        gradient.addColorStop(1, '#0d7377');
        ctx.fillStyle = gradient;
        ctx.fillRect(x, canvas.height - barHeight, barWidth - 1, barHeight);
        x += barWidth;
      }
    };
    draw();
  }, [isRecording]);

  // STRATEGIC: WhatsApp-Compatible Audio Processing Pipeline
  const createWhatsAppAudio = async (webmBlob, isVoiceMessage = true) => {
    debugLog('🔧 Creating WhatsApp-compatible audio...');
    setIsProcessing(true);

    try {
      const audioContext = new AudioContext();
      const arrayBuffer = await webmBlob.arrayBuffer();
      const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);

      if (isVoiceMessage) {
        // VOICE MESSAGE: 16kHz mono Opus at 24k bitrate
        setProcessingStatus('Converting to voice message format...');
        return await createVoiceMessageFormat(audioBuffer);
      } else {
        // MUSIC MESSAGE: AAC in M4A at 128k bitrate
        setProcessingStatus('Converting to high-quality M4A...');
        return await createMusicMessageFormat(audioBuffer);
      }

    } catch (err) {
      debugLog('❌ Audio conversion failed', err.message);
      throw err;
    } finally {
      setIsProcessing(false);
      setProcessingStatus('');
    }
  };

  // ENGINEERING: Voice Message Format (16kHz mono Opus)
  const createVoiceMessageFormat = async (audioBuffer) => {
    // Resample to 16kHz mono for WhatsApp voice messages
    const targetSampleRate = 16000;
    const offlineContext = new OfflineAudioContext(
      1, // Mono
      audioBuffer.duration * targetSampleRate,
      targetSampleRate
    );

    const source = offlineContext.createBufferSource();
    source.buffer = audioBuffer;

    // Mix stereo to mono if needed
    if (audioBuffer.numberOfChannels > 1) {
      const merger = offlineContext.createChannelMerger(1);
      const splitter = offlineContext.createChannelSplitter(audioBuffer.numberOfChannels);
      const gain = offlineContext.createGain();
      gain.gain.value = 1 / audioBuffer.numberOfChannels;

      source.connect(splitter);
      for (let i = 0; i < audioBuffer.numberOfChannels; i++) {
        splitter.connect(gain, i);
      }
      gain.connect(merger, 0, 0);
      merger.connect(offlineContext.destination);
    } else {
      source.connect(offlineContext.destination);
    }

    source.start();
    const resampledBuffer = await offlineContext.startRendering();

    // Create optimized OGG Opus for voice
    return await encodeAsOpus(resampledBuffer, 24000); // 24k bitrate
  };

  // ENGINEERING: Music Message Format (AAC simulation via high-quality WAV)
  const createMusicMessageFormat = async (audioBuffer) => {
    // Since browsers can't encode AAC directly, create high-quality WAV
    // that will be accepted by WhatsApp as music
    const sampleRate = 44100;
    let processedBuffer = audioBuffer;

    // Resample to 44.1kHz if needed
    if (audioBuffer.sampleRate !== sampleRate) {
      const offlineContext = new OfflineAudioContext(
        2, // Stereo for music
        audioBuffer.duration * sampleRate,
        sampleRate
      );

      const source = offlineContext.createBufferSource();
      source.buffer = audioBuffer;
      source.connect(offlineContext.destination);
      source.start();

      processedBuffer = await offlineContext.startRendering();
    }

    // Create high-quality WAV (WhatsApp accepts WAV as music format)
    return createHighQualityWAV(processedBuffer);
  };

  // STRATEGIC: Opus encoding simulation (best effort in browser)
  const encodeAsOpus = async (audioBuffer, bitrate) => {
    // Create MediaRecorder with Opus settings closest to WhatsApp specs
    const stream = new MediaStream();
    const audioContext = new AudioContext({ sampleRate: audioBuffer.sampleRate });
    const source = audioContext.createBufferSource();
    const destination = audioContext.createMediaStreamDestination();

    source.buffer = audioBuffer;
    source.connect(destination);

    const mediaRecorder = new MediaRecorder(destination.stream, {
      mimeType: 'audio/ogg; codecs=opus',
      audioBitsPerSecond: bitrate
    });

    const chunks = [];

    return new Promise((resolve, reject) => {
      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) chunks.push(event.data);
      };

      mediaRecorder.onstop = () => {
        const oggBlob = new Blob(chunks, { type: 'audio/ogg; codecs=opus' });
        resolve(oggBlob);
        audioContext.close();
      };

      mediaRecorder.onerror = reject;

      source.start();
      mediaRecorder.start();

      // Stop after audio duration
      setTimeout(() => {
        source.stop();
        mediaRecorder.stop();
      }, audioBuffer.duration * 1000 + 100);
    });
  };

  // ENGINEERING: High-quality WAV for music messages
  const createHighQualityWAV = (audioBuffer) => {
    const length = audioBuffer.length;
    const channels = audioBuffer.numberOfChannels;
    const sampleRate = audioBuffer.sampleRate;
    const bitDepth = 16;

    const bytesPerSample = bitDepth / 8;
    const blockAlign = channels * bytesPerSample;
    const byteRate = sampleRate * blockAlign;
    const dataSize = length * blockAlign;
    const bufferSize = 44 + dataSize;

    const arrayBuffer = new ArrayBuffer(bufferSize);
    const view = new DataView(arrayBuffer);

    // WAV header
    const writeString = (offset, string) => {
      for (let i = 0; i < string.length; i++) {
        view.setUint8(offset + i, string.charCodeAt(i));
      }
    };

    writeString(0, 'RIFF');
    view.setUint32(4, bufferSize - 8, true);
    writeString(8, 'WAVE');
    writeString(12, 'fmt ');
    view.setUint32(16, 16, true);
    view.setUint16(20, 1, true);
    view.setUint16(22, channels, true);
    view.setUint32(24, sampleRate, true);
    view.setUint32(28, byteRate, true);
    view.setUint16(32, blockAlign, true);
    view.setUint16(34, bitDepth, true);
    writeString(36, 'data');
    view.setUint32(40, dataSize, true);

    // Convert audio data
    let offset = 44;
    for (let i = 0; i < length; i++) {
      for (let channel = 0; channel < channels; channel++) {
        const channelData = audioBuffer.getChannelData(channel);
        let sample = channelData[i];
        sample = Math.max(-1, Math.min(1, sample));
        const intSample = sample < 0 ? sample * 0x8000 : sample * 0x7FFF;
        view.setInt16(offset, Math.round(intSample), true);
        offset += 2;
      }
    }

    return new Blob([arrayBuffer], { type: 'audio/wav' });
  };

  // BREAKTHROUGH: Create actual file system files for drag operations
  const createFileSystemFile = async (blob, filename) => {
    if (!fileSystemHandle) {
      debugLog('⚠️ No file system access - using fallback');
      return null;
    }

    try {
      const fileHandle = await fileSystemHandle.getFileHandle(filename, {
        create: true
      });

      const writable = await fileHandle.createWritable();
      await writable.write(blob);
      await writable.close();

      debugLog('✅ File created in file system', filename);
      return fileHandle;

    } catch (err) {
      debugLog('❌ File system write failed', err.message);
      return null;
    }
  };

  const startRecording = async () => {
    try {
      debugLog('🎬 Starting professional recording...');
      setError(null);
      cleanup();

      if (permissionStatus !== 'granted') {
        const granted = await checkAndRequestPermissions();
        if (!granted) return;
      }

      // High-quality stereo recording for best processing
      const stream = await navigator.mediaDevices.getUserMedia({
        audio: {
          echoCancellation: false,
          noiseSuppression: false,
          autoGainControl: false,
          sampleRate: 48000,
          channelCount: 2,
          latency: 0.01
        }
      });

      streamRef.current = stream;
      setAudioStream(stream);

      // Audio visualization
      const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
      if (audioCtx.state === 'suspended') await audioCtx.resume();

      const source = audioCtx.createMediaStreamSource(stream);
      const analyser = audioCtx.createAnalyser();
      analyser.fftSize = 512;
      analyser.smoothingTimeConstant = 0.2;
      source.connect(analyser);

      audioContextRef.current = audioCtx;
      analyserRef.current = analyser;
      dataArrayRef.current = new Uint8Array(analyser.frequencyBinCount);

      // Record in highest quality WebM
      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: 'audio/webm; codecs=opus',
        audioBitsPerSecond: 320000 // Very high quality for post-processing
      });

      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.onstart = () => {
        debugLog('🎬 High-quality recording active');
        setIsRecording(true);
        drawWaveform();
      };

      mediaRecorder.ondataavailable = (event) => {
        if (event.data?.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = async () => {
        debugLog('⏹️ Processing high-quality audio...');

        if (audioChunksRef.current.length > 0) {
          const rawBlob = new Blob(audioChunksRef.current, { 
            type: 'audio/webm; codecs=opus' 
          });

          // Create both voice and music versions
          try {
            const voiceBlob = await createWhatsAppAudio(rawBlob, true);
            const musicBlob = await createWhatsAppAudio(rawBlob, false);

            await createRecording(voiceBlob, 'voice');
            await createRecording(musicBlob, 'music');

          } catch (err) {
            debugLog('⚠️ Using original format', err.message);
            await createRecording(rawBlob, 'original');
          }
        } else {
          setError('No audio data captured');
        }

        audioChunksRef.current = [];
      };

      mediaRecorder.onerror = (event) => {
        debugLog('❌ Recording error', event.error);
        setError(`Recording failed: ${event.error?.message}`);
        cleanup();
      };

      mediaRecorder.start(50); // High frequency for quality

    } catch (err) {
      debugLog('💥 Recording failed', err);
      setError(`Failed to start: ${err.message}`);
      cleanup();
    }
  };

  const stopRecording = () => {
    debugLog('⏹️ Stopping recording...');
    if (mediaRecorderRef.current?.state === 'recording') {
      mediaRecorderRef.current.stop();
    }
    setIsRecording(false);
    setTimeout(cleanup, 1000);
  };

  const createRecording = async (blob, type) => {
    const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, -5);
    const extensions = {
      voice: 'ogg',
      music: 'wav',
      original: 'webm'
    };
    const filename = `whatsapp-${type}-${timestamp}.${extensions[type]}`;

    const url = URL.createObjectURL(blob);

    // BREAKTHROUGH: Create actual file system file
    const fileHandle = await createFileSystemFile(blob, filename);

    const recording = {
      id: Date.now() + Math.random(),
      url,
      blob,
      filename,
      duration: recordingTime,
      timestamp: new Date().toLocaleTimeString(),
      size: blob.size,
      type,
      fileHandle,
      format: type === 'voice' ? 'OGG Opus (Voice)' : 
               type === 'music' ? 'WAV (Music)' : 'WebM Original'
    };

    setRecordings(prev => [...prev, recording]);
    debugLog(`✅ ${type} recording created`, { filename, size: blob.size });
  };

  // ENHANCED: Drag with file system integration
  const handleDragStart = (e, recording) => {
    setDraggedItem(recording.id);

    if (recording.fileHandle && fileSystemHandle) {
      // Create file system path for drag
      const filePath = `${fileSystemHandle.name}/${recording.filename}`;
      e.dataTransfer.setData('text/uri-list', `file:///${filePath}`);
      e.dataTransfer.setData('text/plain', recording.filename);
    } else {
      // Fallback to URL
      e.dataTransfer.setData('DownloadURL', 
        `${recording.blob.type}:${recording.filename}:${recording.url}`);
    }

    e.dataTransfer.effectAllowed = 'copy';
    debugLog('🎯 Enhanced drag started', { filename: recording.filename, hasFileSystem: !!recording.fileHandle });
  };

  const handleDragEnd = () => {
    setDraggedItem(null);
  };

  const downloadRecording = async (recording) => {
    try {
      const link = document.createElement('a');
      link.href = recording.url;
      link.download = recording.filename;
      link.click();
      debugLog('✅ File downloaded', recording.filename);
    } catch (err) {
      setError(`Download failed: ${err.message}`);
    }
  };

  const removeRecording = (index) => {
    setRecordings(prev => {
      const recording = prev[index];
      URL.revokeObjectURL(recording.url);
      return prev.filter((_, i) => i !== index);
    });
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  const getRecordingIcon = (type) => {
    switch (type) {
      case 'voice': return '🎤';
      case 'music': return '🎵';
      default: return '🎧';
    }
  };

  const getCompatibilityStatus = (type) => {
    switch (type) {
      case 'voice': return { text: 'Voice Message', color: 'text-green-600', bg: 'bg-green-50' };
      case 'music': return { text: 'Music Quality', color: 'text-blue-600', bg: 'bg-blue-50' };
      default: return { text: 'Original', color: 'text-gray-600', bg: 'bg-gray-50' };
    }
  };

  return (
    <div className="bg-[#ece5dd] min-h-screen p-4 space-y-4">
      {/* Professional status bar */}
      <div className="flex items-center justify-between bg-gradient-to-r from-[#075e54] to-[#128C7E] text-white px-6 py-3 rounded-lg shadow-lg">
        <div className="flex items-center space-x-3">
          <span className="text-2xl">🎤</span>
          <div>
            <h1 className="font-semibold">WhatsApp Audio Studio</h1>
            <p className="text-sm opacity-90">Professional voice & music recording</p>
          </div>
        </div>
        <div className="text-right">
          <div className="text-sm">
            {permissionStatus === 'granted' ? '✅ Ready' : 
             permissionStatus === 'denied' ? '❌ Access Required' : '⏳ Initializing...'}
          </div>
          {fileSystemHandle && (
            <div className="text-xs opacity-75">📁 File System: {fileSystemHandle.name}</div>
          )}
        </div>
      </div>

      {/* Processing status */}
      {isProcessing && (
        <div className="bg-blue-50 border border-blue-200 p-4 rounded-lg">
          <div className="flex items-center space-x-3">
            <div className="animate-spin text-blue-600">⚙️</div>
            <div>
              <div className="font-medium text-blue-800">Processing Audio...</div>
              <div className="text-sm text-blue-600">{processingStatus}</div>
            </div>
          </div>
        </div>
      )}

      {/* Recording status */}
      {isRecording && (
        <div className="bg-red-50 border border-red-200 p-4 rounded-lg">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <span className="w-3 h-3 bg-red-500 rounded-full animate-pulse"></span>
              <span className="font-medium text-red-800">Recording High-Quality Audio</span>
            </div>
            <span className="text-red-600 font-mono text-lg">{recordingTime}s</span>
          </div>
        </div>
      )}

      {/* Error display */}
      {error && (
        <div className="bg-red-50 border-l-4 border-red-400 p-4 rounded flex justify-between items-center">
          <span className="text-red-700">{error}</span>
          <button 
            onClick={() => setError(null)}
            className="text-red-400 hover:text-red-600 font-bold text-xl"
          >
            ×
          </button>
        </div>
      )}

      {/* Recording controls */}
      <div className="flex justify-center space-x-4">
        {!isRecording ? (
          <>
            <button
              onClick={startRecording}
              disabled={permissionStatus !== 'granted'}
              className={`flex items-center space-x-3 px-8 py-4 rounded-full shadow-lg transition-all transform hover:scale-105 ${
                permissionStatus !== 'granted' 
                  ? 'bg-gray-300 text-gray-500 cursor-not-allowed' 
                  : 'bg-gradient-to-r from-[#25D366] to-[#128C7E] hover:from-[#128C7E] hover:to-[#0d7377] text-white'
              }`}
            >
              <span className="text-2xl">🎤</span>
              <span className="font-semibold">Start Recording</span>
            </button>

            {!fileSystemHandle && (
              <button
                onClick={setupFileSystemAccess}
                className="flex items-center space-x-2 px-6 py-4 bg-blue-500 hover:bg-blue-600 text-white rounded-full shadow-lg transition-all"
              >
                <span>📁</span>
                <span>Enable File Access</span>
              </button>
            )}
          </>
        ) : (
          <button
            onClick={stopRecording}
            className="flex items-center space-x-3 bg-red-500 hover:bg-red-600 text-white px-8 py-4 rounded-full shadow-lg relative transition-all transform hover:scale-105"
          >
            <span className="text-2xl">⏹️</span>
            <span className="font-semibold">Stop Recording ({recordingTime}s)</span>
            <span className="absolute -top-2 -right-2 w-4 h-4 bg-red-300 rounded-full animate-ping"></span>
          </button>
        )}
      </div>

      {/* Waveform visualization */}
      {isRecording && (
        <div className="bg-white p-6 rounded-lg shadow-lg max-w-4xl mx-auto">
          <canvas
            ref={canvasRef}
            width={800}
            height={120}
            className="w-full border rounded-lg"
          />
          <div className="text-center text-gray-600 mt-3 flex items-center justify-center space-x-2">
            <span className="animate-pulse text-lg">🎵</span>
            <span className="font-medium">High-Quality Audio Capture • {recordingTime}s</span>
          </div>
        </div>
      )}

      {/* Professional recordings display */}
      <div className="space-y-4 max-w-4xl mx-auto">
        {recordings.map((rec, index) => {
          const status = getCompatibilityStatus(rec.type);
          return (
            <div
              key={rec.id}
              draggable
              onDragStart={(e) => handleDragStart(e, rec)}
              onDragEnd={handleDragEnd}
              className={`flex items-center space-x-4 bg-white p-4 rounded-xl shadow-md hover:shadow-lg transition-all cursor-move border-l-4 border-[#25D366] ${
                draggedItem === rec.id ? 'opacity-60 scale-95' : 'opacity-100 scale-100'
              }`}
            >
              <div className="w-14 h-14 bg-gradient-to-br from-[#25D366] to-[#128C7E] rounded-full flex items-center justify-center flex-shrink-0 text-white text-2xl">
                {getRecordingIcon(rec.type)}
              </div>

              <div className="flex-1 min-w-0">
                <div className="flex items-center space-x-2 mb-2">
                  <span className={`px-3 py-1 rounded-full text-xs font-medium ${status.bg} ${status.color}`}>
                    {status.text}
                  </span>
                  <span className="text-xs text-gray-500">{rec.format}</span>
                  {rec.fileHandle && (
                    <span className="text-xs bg-blue-100 text-blue-600 px-2 py-1 rounded">
                      📁 File System
                    </span>
                  )}
                </div>

                <audio controls src={rec.url} className="w-full mb-2" />

                <div className="text-sm text-gray-600 flex justify-between items-center">
                  <span className="flex items-center space-x-3">
                    <span>⏱️ {rec.duration}s</span>
                    <span>📁 {formatFileSize(rec.size)}</span>
                    <span className="text-green-600 font-medium">✅ WhatsApp Ready</span>
                  </span>
                  <span>{rec.timestamp}</span>
                </div>

                <div className="text-xs text-gray-400 mt-1 truncate font-mono">
                  {rec.filename}
                </div>
              </div>

              <div className="flex space-x-2 flex-shrink-0">
                <button
                  onClick={() => downloadRecording(rec)}
                  className="px-4 py-2 bg-[#25D366] hover:bg-[#128C7E] text-white rounded-lg text-sm font-medium transition-colors shadow-sm"
                  title="Download file"
                >
                  💾 Save
                </button>
                <button
                  onClick={() => removeRecording(index)}
                  className="px-4 py-2 bg-red-500 hover:bg-red-600 text-white rounded-lg text-sm font-medium transition-colors shadow-sm"
                  title="Delete recording"
                >
                  🗑️ Delete
                </button>
              </div>

              <div className="text-2xl opacity-60 hover:opacity-100 transition-opacity" title="Drag to share">
                ↗️
              </div>
            </div>
          );
        })}
      </div>

      {recordings.length === 0 && !isRecording && (
        <div className="text-center text-gray-500 py-16">
          <div className="text-7xl mb-4">🎤</div>
          <h3 className="text-2xl font-medium mb-3">Professional Audio Recording</h3>
          <p className="text-lg mb-2">Create WhatsApp-compatible voice and music messages</p>
          <p className="text-sm">High-quality recording with automatic format optimization</p>
        </div>
      )}

      {/* Enhanced usage guide */}
      {recordings.length > 0 && (
        <div className="bg-gradient-to-r from-green-50 to-blue-50 border border-green-200 p-6 rounded-lg max-w-4xl mx-auto">
          <h3 className="font-semibold text-green-800 mb-4 flex items-center space-x-2">
            <span>📱</span>
            <span>WhatsApp Integration Guide</span>
          </h3>

          <div className="grid md:grid-cols-2 gap-6 text-sm">
            <div>
              <h4 className="font-medium text-green-700 mb-3">🎤 Voice Messages:</h4>
              <ul className="space-y-2 text-green-600">
                <li>• <strong>Format:</strong> OGG Opus at 24k bitrate</li>
                <li>• <strong>Quality:</strong> 16kHz mono (WhatsApp standard)</li>
                <li>• <strong>Usage:</strong> Perfect for voice notes</li>
                <li>• <strong>Size:</strong> Optimized for mobile data</li>
              </ul>
            </div>

            <div>
              <h4 className="font-medium text-blue-700 mb-3">🎵 Music Messages:</h4>
              <ul className="space-y-2 text-blue-600">
                <li>• <strong>Format:</strong> High-quality WAV</li>
                <li>• <strong>Quality:</strong> 44.1kHz stereo</li>
                <li>• <strong>Usage:</strong> Music and high-quality audio</li>
                <li>• <strong>Size:</strong> Larger but superior quality</li>
              </ul>
            </div>
          </div>

          <div className="mt-6 p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
            <h4 className="font-medium text-yellow-800 mb-2">🚀 File System Access Benefits:</h4>
            <p className="text-sm text-yellow-700">
              When file system access is enabled, files are saved directly to your computer and can be dragged 
              from your file explorer to WhatsApp Web or desktop applications.
            </p>
          </div>
        </div>
      )}
    </div>
  );
}
