import React, { useState, useRef, useEffect, useCallback } from "react";

export default function WhatsAppAudioRecorder() {
  const [recordings, setRecordings] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [audioStream, setAudioStream] = useState(null);
  const [permissionStatus, setPermissionStatus] = useState('checking');
  const [error, setError] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [processingStatus, setProcessingStatus] = useState('');
  const [workingDirectory, setWorkingDirectory] = useState(null);
  const [supportedFormats, setSupportedFormats] = useState([]);
  const [selectedFormat, setSelectedFormat] = useState(null);
  const [platformInfo, setPlatformInfo] = useState({ os: 'unknown', scriptExt: '.sh' });
  const [ffmpegSetupStatus, setFFmpegSetupStatus] = useState({
    isConfigured: false,
    directoryName: null,
    lastVerified: null,
    platformOS: null
  });
  const [fileCounter, setFileCounter] = useState(1);

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const timerRef = useRef(null);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const dataArrayRef = useRef(null);
  const animationRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);

  const debugLog = (message, data = null) => {
    console.log(`üé§ ${message}`, data);
  };

  // STRATEGIC: Simple incremental file counter management
  const COUNTER_KEY = 'whatsapp-audio-studio-file-counter';

  const getNextFileNumber = () => {
    try {
      const savedCounter = localStorage.getItem(COUNTER_KEY);
      const currentCounter = savedCounter ? parseInt(savedCounter, 10) : 1;
      const nextCounter = currentCounter;

      // Save incremented counter for next time
      localStorage.setItem(COUNTER_KEY, (currentCounter + 1).toString());

      setFileCounter(nextCounter);
      debugLog('üìä File counter', { current: nextCounter, next: currentCounter + 1 });
      return nextCounter;
    } catch (err) {
      debugLog('‚ö†Ô∏è Counter management failed, using timestamp fallback', err.message);
      return Date.now();
    }
  };

  const resetFileCounter = () => {
    try {
      localStorage.setItem(COUNTER_KEY, '1');
      setFileCounter(1);
      debugLog('üîÑ File counter reset to 1');
    } catch (err) {
      debugLog('‚ö†Ô∏è Failed to reset counter', err.message);
    }
  };

  const saveFFmpegConfig = (directoryHandle, directoryName) => {
    try {
      const config = {
        isConfigured: true,
        directoryName: directoryName,
        lastVerified: Date.now(),
        platformOS: platformInfo.os,
        version: '1.0'
      };
      localStorage.setItem(STORAGE_KEY, JSON.stringify(config));
      setFFmpegSetupStatus(config);
      debugLog('‚úÖ FFmpeg config saved to localStorage', config);
    } catch (err) {
      debugLog('‚ö†Ô∏è Failed to save FFmpeg config', err.message);
    }
  };

  const loadFFmpegConfig = () => {
    try {
      const savedConfig = localStorage.getItem(STORAGE_KEY);
      if (savedConfig) {
        const config = JSON.parse(savedConfig);
        // Check if config is recent (within 7 days) and same platform
        const weekAgo = Date.now() - (7 * 24 * 60 * 60 * 1000);

        if (config.lastVerified > weekAgo && config.platformOS === platformInfo.os) {
          setFFmpegSetupStatus(config);
          debugLog('‚úÖ FFmpeg config restored from localStorage', config);
          return config;
        } else {
          debugLog('‚ö†Ô∏è FFmpeg config expired or platform changed', {
            expired: config.lastVerified <= weekAgo,
            platformChanged: config.platformOS !== platformInfo.os
          });
          clearFFmpegConfig();
        }
      }
    } catch (err) {
      debugLog('‚ö†Ô∏è Failed to load FFmpeg config', err.message);
      clearFFmpegConfig();
    }
    return null;
  };

  const clearFFmpegConfig = () => {
    try {
      localStorage.removeItem(STORAGE_KEY);
      setFFmpegSetupStatus({
        isConfigured: false,
        directoryName: null,
        lastVerified: null,
        platformOS: null
      });
      debugLog('üßπ FFmpeg config cleared');
    } catch (err) {
      debugLog('‚ö†Ô∏è Failed to clear FFmpeg config', err.message);
    }
  };

  const validateAndRestoreFFmpegSetup = async () => {
    const savedConfig = loadFFmpegConfig();
    if (!savedConfig || !savedConfig.isConfigured) return false;

    try {
      setProcessingStatus('Restoring FFmpeg integration...');

      // Try to restore directory access
      if ('showDirectoryPicker' in window) {
        // Note: We can't automatically restore the directory handle
        // User needs to re-grant access, but we can show friendly message
        debugLog('üìÅ FFmpeg integration remembered, awaiting directory access');
        return true;
      }
    } catch (err) {
      debugLog('‚ö†Ô∏è Failed to restore FFmpeg setup', err.message);
      clearFFmpegConfig();
    } finally {
      setProcessingStatus('');
    }
    return false;
  };

  // STRATEGIC: Cross-platform OS detection
  const detectPlatform = () => {
    const userAgent = navigator.userAgent.toLowerCase();
    const platform = navigator.platform.toLowerCase();

    let os = 'linux';
    let scriptExt = '.sh';
    let executable = './';

    if (platform.includes('win') || userAgent.includes('windows')) {
      os = 'windows';
      scriptExt = '.bat';
      executable = '';
    } else if (platform.includes('mac') || userAgent.includes('mac')) {
      os = 'macos';
      scriptExt = '.sh';
      executable = './';
    }

    const info = { os, scriptExt, executable };
    setPlatformInfo(info);
    debugLog('‚úÖ Platform detected', info);
    return info;
  };

  // STRATEGIC: Detect all supported MediaRecorder formats
  const detectSupportedFormats = () => {
    const formats = [
      { mimeType: 'audio/webm; codecs=opus', name: 'WebM Opus', priority: 1 },
      { mimeType: 'audio/webm', name: 'WebM', priority: 2 },
      { mimeType: 'audio/ogg; codecs=opus', name: 'OGG Opus', priority: 3 },
      { mimeType: 'audio/ogg', name: 'OGG', priority: 4 },
      { mimeType: 'audio/mp4', name: 'MP4', priority: 5 },
      { mimeType: 'audio/wav', name: 'WAV', priority: 6 },
      { mimeType: 'audio/mpeg', name: 'MP3', priority: 7 }
    ];

    const supported = formats.filter(format => 
      MediaRecorder.isTypeSupported(format.mimeType)
    ).sort((a, b) => a.priority - b.priority);

    setSupportedFormats(supported);

    if (supported.length > 0) {
      setSelectedFormat(supported[0]);
      debugLog('‚úÖ MediaRecorder formats detected', {
        supported: supported.map(f => f.name),
        selected: supported[0].name
      });
    } else {
      setError('No supported audio recording formats found');
      debugLog('‚ùå No MediaRecorder support detected');
    }

    return supported;
  };

  // STRATEGIC: Enhanced cross-platform FFmpeg setup with persistent storage
  const setupSystemFFmpeg = async () => {
    try {
      if ('showDirectoryPicker' in window) {
        const dirHandle = await window.showDirectoryPicker({
          mode: 'readwrite',
          startIn: 'downloads'
        });
        setWorkingDirectory(dirHandle);

        const platform = detectPlatform();

        // Create platform-specific setup script
        let setupScript, setupFilename;

        if (platform.os === 'windows') {
          setupFilename = '00_setup_ffmpeg.bat';
          setupScript = `@echo off
echo ========================================
echo WhatsApp Audio Studio - FFmpeg Setup
echo Platform: Windows
echo Directory: %CD%
echo ========================================
echo.
echo Testing FFmpeg installation...
ffmpeg -version >nul 2>&1
if %ERRORLEVEL% EQU 0 (
    echo ‚úÖ FFmpeg is installed and working
    echo.
    ffmpeg -version | findstr "ffmpeg version"
    echo.
    echo ‚úÖ WhatsApp Audio Studio ready!
    echo üìÅ Working directory configured
    echo üé§ You can now record and process audio
    echo.
) else (
    echo ‚ùå FFmpeg not found in PATH
    echo.
    echo INSTALLATION REQUIRED:
    echo 1. Download FFmpeg from: https://ffmpeg.org/download.html
    echo 2. Extract to C:\\ffmpeg\\
    echo 3. Add C:\\ffmpeg\\bin to your PATH environment variable
    echo 4. Restart this application
    echo.
)
echo ========================================
pause`;
        } else {
          // Linux/macOS shell script
          setupFilename = '00_setup_ffmpeg.sh';
          setupScript = `#!/bin/bash
echo "========================================"
echo "WhatsApp Audio Studio - FFmpeg Setup"
echo "Platform: ${platform.os === 'macos' ? 'macOS' : 'Linux'}"
echo "Directory: $(pwd)"
echo "========================================"
echo
echo "Testing FFmpeg installation..."
if command -v ffmpeg >/dev/null 2>&1; then
    echo "‚úÖ FFmpeg is installed and working"
    echo
    ffmpeg -version | head -1
    echo
    echo "‚úÖ WhatsApp Audio Studio ready!"
    echo "üìÅ Working directory configured"
    echo "üé§ You can now record and process audio"
    echo
else
    echo "‚ùå FFmpeg not found in PATH"
    echo
    echo "INSTALLATION REQUIRED:"
    if [[ "$OSTYPE" == "darwin"* ]]; then
        echo "macOS: brew install ffmpeg"
        echo "or download from: https://ffmpeg.org/download.html"
    else
        echo "Ubuntu/Debian: sudo apt update && sudo apt install ffmpeg"
        echo "CentOS/RHEL: sudo yum install ffmpeg"
        echo "Arch: sudo pacman -S ffmpeg"
        echo "or download from: https://ffmpeg.org/download.html"
    fi
    echo
fi
echo "========================================"
read -p "Press Enter to continue..."`;
        }

        const setupBlob = new Blob([setupScript], { type: 'text/plain' });
        const setupHandle = await dirHandle.getFileHandle(setupFilename, { create: true });
        const setupWritable = await setupHandle.createWritable();
        await setupWritable.write(setupBlob);
        await setupWritable.close();

        // Create enhanced README with cache info
        const readme = `# WhatsApp Audio Studio - System FFmpeg Integration

## ‚úÖ Integration Status: CONFIGURED
**Directory:** ${dirHandle.name}  
**Platform:** ${platform.os.charAt(0).toUpperCase() + platform.os.slice(1)}  
**Setup Date:** ${new Date().toLocaleString()}  
**Configuration:** Cached in browser for 7 days  

## How it works:
1. Record audio in the web browser
2. Raw audio files are saved here as .webm
3. FFmpeg scripts are automatically generated
4. Run the scripts to create WhatsApp-compatible files

## Required Software:
### FFmpeg Installation:

**${platform.os === 'windows' ? 'Windows' : platform.os === 'macos' ? 'macOS' : 'Linux'}:**
${platform.os === 'windows' ? 
`- Download from: https://ffmpeg.org/download.html
- Extract to C:\\ffmpeg\\
- Add C:\\ffmpeg\\bin to your PATH environment variable` :
platform.os === 'macos' ?
`- Homebrew: \`brew install ffmpeg\`
- MacPorts: \`sudo port install ffmpeg\`
- Manual: https://ffmpeg.org/download.html` :
`- Ubuntu/Debian: \`sudo apt update && sudo apt install ffmpeg\`
- CentOS/RHEL: \`sudo yum install ffmpeg\`
- Arch: \`sudo pacman -S ffmpeg\`
- Manual: https://ffmpeg.org/download.html`}

## Generated Files:
- process_[timestamp]${platform.scriptExt} - Self-executing, self-cleaning FFmpeg script
- whatsapp_voice_[timestamp].ogg - Voice message (16kHz mono, 32kbps)

## Optimized Workflow:
‚úÖ **Self-Executable:** Scripts auto-chmod +x for double-click  
‚úÖ **Self-Cleaning:** Auto-deletes input .webm files after processing  
‚úÖ **Self-Destructing:** Scripts remove themselves when done  
‚úÖ **Minimal Output:** Only creates WhatsApp-ready .ogg files  
‚úÖ **Clean Directory:** No leftover temporary files  

## Usage:
1. Run: \`${platform.executable}00_setup_ffmpeg${platform.scriptExt}\` to test FFmpeg
2. Record audio in the web app
3. Double-click: \`${platform.executable}process_[timestamp]${platform.scriptExt}\`
4. Upload the generated .ogg file to WhatsApp

## WhatsApp Compatibility:
‚úÖ Voice messages: Upload .ogg files for instant voice playback
‚úÖ Optimized parameters: Identical to professional audio tools
‚úÖ Guaranteed compatibility: Uses exact WhatsApp specifications

## Cache & Persistence:
üß† **Smart Memory:** Integration status cached for 7 days  
üîÑ **Auto-Restore:** Setup remembered across browser sessions  
üßπ **Auto-Cleanup:** Expired configurations automatically cleared  

No need to reconfigure unless you change directories or platforms!`;

        const readmeBlob = new Blob([readme], { type: 'text/plain' });
        const readmeHandle = await dirHandle.getFileHandle('README.md', { create: true });
        const readmeWritable = await readmeHandle.createWritable();
        await readmeWritable.write(readmeBlob);
        await readmeWritable.close();

        // STRATEGIC: Save configuration to localStorage
        saveFFmpegConfig(dirHandle, dirHandle.name);

        debugLog('‚úÖ Enhanced FFmpeg integration setup complete', {
          platform: platform.os,
          directory: dirHandle.name,
          setupScript: setupFilename,
          cached: true,
          expiry: '7 days'
        });

        return true;
      }
    } catch (err) {
      if (err.name !== 'AbortError') {
        debugLog('‚ö†Ô∏è System FFmpeg setup declined', err.message);
        setError('File system access required for FFmpeg integration');
      }
      return false;
    }
  };

  // STRATEGIC: Smart directory re-access for cached configurations  
  const restoreWorkingDirectory = async () => {
    if (!ffmpegSetupStatus.isConfigured) return false;

    try {
      setProcessingStatus('Restoring working directory access...');

      if ('showDirectoryPicker' in window) {
        const dirHandle = await window.showDirectoryPicker({
          mode: 'readwrite',
          startIn: 'downloads'
        });

        setWorkingDirectory(dirHandle);

        // Update last verified timestamp
        saveFFmpegConfig(dirHandle, dirHandle.name);

        debugLog('‚úÖ Working directory restored', {
          directory: dirHandle.name,
          cached: true
        });

        return true;
      }
    } catch (err) {
      if (err.name !== 'AbortError') {
        debugLog('‚ö†Ô∏è Failed to restore working directory', err.message);
        // Don't clear config - user might try again
      }
    } finally {
      setProcessingStatus('');
    }
    return false;
  };

  // STRATEGIC: Create minimal, self-cleaning, auto-executable FFmpeg scripts
  const createFFmpegProcessingScripts = async (webmBlob, timestamp) => {
    if (!workingDirectory) return null;

    try {
      setProcessingStatus('Creating clean, self-destructing FFmpeg script...');

      const inputFile = `input_${timestamp}.webm`;
      const voiceOutput = `whatsapp_voice_${timestamp}.ogg`;
      const processScript = `process_${timestamp}${platformInfo.scriptExt}`;

      // Write input WebM file
      const inputHandle = await workingDirectory.getFileHandle(inputFile, { create: true });
      const inputWritable = await inputHandle.createWritable();
      await inputWritable.write(webmBlob);
      await inputWritable.close();

      // Create minimal, self-cleaning platform-specific script
      let ffmpegScript;

      if (platformInfo.os === 'windows') {
        ffmpegScript = `@echo off
title WhatsApp Voice Processor
echo ========================================
echo üé§ WhatsApp Voice Message Creator
echo ========================================
echo.
echo üîç Checking FFmpeg installation...

ffmpeg -version >nul 2>&1
if %ERRORLEVEL% NEQ 0 (
    echo ‚ùå FFmpeg not found! Please install FFmpeg first.
    echo üì• Download: https://ffmpeg.org/download.html
    pause & exit /b 1
)

echo ‚úÖ FFmpeg ready
echo üéµ Processing: ${inputFile} ‚Üí ${voiceOutput}
echo.

ffmpeg -i "${inputFile}" -c:a libopus -b:a 32k -vbr on -compression_level 10 -application voip -ac 1 -ar 16000 -f ogg "${voiceOutput}" -y

if %ERRORLEVEL% EQU 0 (
    echo.
    echo ‚úÖ SUCCESS: WhatsApp voice message created!
    echo üìÅ File: ${voiceOutput}
    echo üöÄ Ready to upload to WhatsApp
    echo.
    echo üßπ Cleaning up temporary files...
    del "${inputFile}" 2>nul
    echo ‚úÖ Cleaned: ${inputFile}
    echo.
    echo üóëÔ∏è Self-destructing in 3 seconds...
    timeout /t 3 /nobreak >nul
    del "%~f0" 2>nul
) else (
    echo.
    echo ‚ùå FAILED: Voice message creation unsuccessful
    echo üìÅ Check if ${inputFile} exists and is valid
    pause
)`;
      } else {
        // Linux/macOS: Self-executable, self-cleaning shell script
        ffmpegScript = `#!/bin/bash

# Make this script executable (for double-click support)
chmod +x "$0" 2>/dev/null

echo "========================================"
echo "üé§ WhatsApp Voice Message Creator"
echo "========================================"
echo
echo "üîç Checking FFmpeg installation..."

if ! command -v ffmpeg >/dev/null 2>&1; then
    echo "‚ùå FFmpeg not found! Please install FFmpeg first:"
    if [[ "$OSTYPE" == "darwin"* ]]; then
        echo "üì• macOS: brew install ffmpeg"
    else
        echo "üì• Ubuntu/Debian: sudo apt install ffmpeg"
        echo "üì• CentOS/RHEL: sudo yum install ffmpeg"
        echo "üì• Arch: sudo pacman -S ffmpeg"
    fi
    echo
    read -p "Press Enter to exit..."
    exit 1
fi

echo "‚úÖ FFmpeg ready"
echo "üéµ Processing: ${inputFile} ‚Üí ${voiceOutput}"
echo

# Process audio with WhatsApp-optimized parameters
ffmpeg -i "${inputFile}" \\
    -c:a libopus \\
    -b:a 32k \\
    -vbr on \\
    -compression_level 10 \\
    -application voip \\
    -ac 1 \\
    -ar 16000 \\
    -f ogg \\
    "${voiceOutput}" \\
    -y

if [ $? -eq 0 ]; then
    echo
    echo "‚úÖ SUCCESS: WhatsApp voice message created!"
    echo "üìÅ File: ${voiceOutput}"
    echo "üöÄ Ready to upload to WhatsApp"
    echo
    echo "üßπ Cleaning up temporary files..."
    rm -f "${inputFile}" && echo "‚úÖ Cleaned: ${inputFile}"
    echo
    echo "üóëÔ∏è Self-destructing in 3 seconds..."
    sleep 3
    rm -f "$0"
else
    echo
    echo "‚ùå FAILED: Voice message creation unsuccessful"
    echo "üìÅ Check if ${inputFile} exists and is valid"
    read -p "Press Enter to exit..."
fi`;
      }

      // Write processing script
      const scriptBlob = new Blob([ffmpegScript], { type: 'text/plain' });
      const scriptHandle = await workingDirectory.getFileHandle(processScript, { create: true });
      const scriptWritable = await scriptHandle.createWritable();
      await scriptWritable.write(scriptBlob);
      await scriptWritable.close();

      debugLog('‚úÖ Cross-platform FFmpeg processing scripts created', {
        platform: platformInfo.os,
        input: inputFile,
        script: processScript,
        outputs: [voiceOutput, musicOutput]
      });

      debugLog('‚úÖ Clean, self-destructing FFmpeg script created', {
        platform: platformInfo.os,
        input: inputFile,
        script: processScript,
        output: voiceOutput,
        features: ['auto-executable', 'self-cleaning', 'self-destructing']
      });

      return {
        inputFile,
        processScript,
        voiceOutput,
        timestamp,
        directory: workingDirectory.name,
        platform: platformInfo.os,
        executable: platformInfo.executable
      };

    } catch (err) {
      debugLog('‚ùå FFmpeg script creation failed', err.message);
      throw err;
    }
  };

  const checkAndRequestPermissions = async () => {
    try {
      setPermissionStatus('checking');

      const testStream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: false,
          noiseSuppression: false,
          autoGainControl: false,
          sampleRate: 48000,
          channelCount: 2,
        } 
      });

      debugLog('‚úÖ Microphone access granted');
      testStream.getTracks().forEach(track => track.stop());
      setPermissionStatus('granted');

      // Detect supported formats and platform after getting permission
      detectSupportedFormats();
      const platform = detectPlatform();

      // STRATEGIC: Load cached FFmpeg configuration
      const cachedConfig = loadFFmpegConfig();
      if (cachedConfig && cachedConfig.isConfigured) {
        debugLog('üß† FFmpeg integration remembered from cache', {
          directory: cachedConfig.directoryName,
          platform: cachedConfig.platformOS,
          daysAgo: Math.floor((Date.now() - cachedConfig.lastVerified) / (24 * 60 * 60 * 1000))
        });
      }

      return true;

    } catch (err) {
      debugLog('‚ùå Permissions denied', err.message);
      setPermissionStatus('denied');
      setError(`Microphone required: ${err.message}`);
      return false;
    }
  };

  useEffect(() => {
    checkAndRequestPermissions();
  }, []);

  const cleanup = useCallback(() => {
    [timerRef, animationRef].forEach(ref => {
      if (ref.current) {
        ref === timerRef ? clearInterval(ref.current) : cancelAnimationFrame(ref.current);
        ref.current = null;
      }
    });

    if (mediaRecorderRef.current?.state !== 'inactive') {
      try {
        mediaRecorderRef.current?.stop();
      } catch (e) {
        debugLog('‚ö†Ô∏è MediaRecorder cleanup', e.message);
      }
      mediaRecorderRef.current = null;
    }

    if (audioContextRef.current?.state !== 'closed') {
      audioContextRef.current?.close();
      audioContextRef.current = null;
    }

    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
      setAudioStream(null);
    }

    setIsRecording(false);
    setRecordingTime(0);
  }, []);

  useEffect(() => cleanup, [cleanup]);

  useEffect(() => {
    if (isRecording) {
      timerRef.current = setInterval(() => setRecordingTime(prev => prev + 1), 1000);
    } else {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
      setRecordingTime(0);
    }
    return () => timerRef.current && clearInterval(timerRef.current);
  }, [isRecording]);

  const drawWaveform = useCallback(() => {
    if (!canvasRef.current || !analyserRef.current || !isRecording) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const analyser = analyserRef.current;
    const dataArray = dataArrayRef.current;

    const draw = () => {
      if (!isRecording) return;
      animationRef.current = requestAnimationFrame(draw);
      analyser.getByteFrequencyData(dataArray);

      ctx.fillStyle = "#f0f2f5";
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      const barWidth = (canvas.width / dataArray.length) * 2.5;
      let x = 0;

      for (let i = 0; i < dataArray.length; i++) {
        const barHeight = (dataArray[i] / 255) * canvas.height * 0.9;
        const gradient = ctx.createLinearGradient(0, canvas.height - barHeight, 0, canvas.height);
        gradient.addColorStop(0, '#25D366');
        gradient.addColorStop(0.5, '#128C7E');
        gradient.addColorStop(1, '#0d7377');
        ctx.fillStyle = gradient;
        ctx.fillRect(x, canvas.height - barHeight, barWidth - 1, barHeight);
        x += barWidth;
      }
    };
    draw();
  }, [isRecording]);

  const startRecording = async () => {
    try {
      debugLog('üé¨ Starting cross-platform recording...');
      setError(null);
      cleanup();

      if (permissionStatus !== 'granted') {
