import React, { useState, useRef, useEffect } from "react";

export default function WhatsAppAudioRecorder() {
  const [recordings, setRecordings] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [audioStream, setAudioStream] = useState(null);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const timerRef = useRef(null);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const dataArrayRef = useRef(null);
  const animationRef = useRef(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    return () => {
      clearInterval(timerRef.current);
      if (audioContextRef.current) {
        audioContextRef.current.close();
        audioContextRef.current = null;
      }
      cancelAnimationFrame(animationRef.current);
      if (audioStream) {
        audioStream.getTracks().forEach(track => track.stop());
      }
    };
  }, []);

  useEffect(() => {
    if (isRecording) {
      timerRef.current = setInterval(() => {
        setRecordingTime((prev) => prev + 1);
      }, 1000);
    } else {
      clearInterval(timerRef.current);
      setRecordingTime(0);
    }
  }, [isRecording]);

  const drawWaveform = () => {
    if (!canvasRef.current || !analyserRef.current) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const analyser = analyserRef.current;
    const dataArray = dataArrayRef.current;

    const draw = () => {
      animationRef.current = requestAnimationFrame(draw);
      analyser.getByteTimeDomainData(dataArray);

      ctx.fillStyle = "#fff";
      ctx.fillRect(0, 0, canvas.width, canvas.height);
      ctx.lineWidth = 2;
      ctx.strokeStyle = "#25D366";
      ctx.beginPath();

      const sliceWidth = (canvas.width * 1.0) / dataArray.length;
      let x = 0;

      for (let i = 0; i < dataArray.length; i++) {
        const v = dataArray[i] / 128.0;
        const y = (v * canvas.height) / 2;

        if (i === 0) {
          ctx.moveTo(x, y);
        } else {
          ctx.lineTo(x, y);
        }

        x += sliceWidth;
      }
      ctx.lineTo(canvas.width, canvas.height / 2);
      ctx.stroke();
    };
    draw();
  };

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      if (!stream) throw new Error("No stream available");

      setAudioStream(stream);

      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: "audio/ogg; codecs=opus",
        audioBitsPerSecond: 192000,
      });

      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = () => {
        const blob = new Blob(audioChunksRef.current, { type: "audio/ogg; codecs=opus" });
        const url = URL.createObjectURL(blob);
        setRecordings((prev) => [...prev, { url, blob, duration: recordingTime }]);

        audioChunksRef.current = [];
        cancelAnimationFrame(animationRef.current);
        if (audioContextRef.current) {
          audioContextRef.current.close();
          audioContextRef.current = null;
        }
      };

      const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
      const source = audioCtx.createMediaStreamSource(stream);
      const analyser = audioCtx.createAnalyser();
      source.connect(analyser);
      analyser.fftSize = 2048;
      const bufferLength = analyser.frequencyBinCount;
      const dataArray = new Uint8Array(bufferLength);

      audioContextRef.current = audioCtx;
      analyserRef.current = analyser;
      dataArrayRef.current = dataArray;

      drawWaveform();
      mediaRecorder.start();
      setIsRecording(true);

    } catch (err) {
      console.error("Recording error:", err);
      alert("Could not start recording. Check microphone permissions.");
    }
  };

  const stopRecording = () => {
    try {
      mediaRecorderRef.current?.stop();
    } catch (e) {
      console.warn("Tried to stop recorder that wasn't started.");
    }
    setIsRecording(false);
    if (audioStream) {
      audioStream.getTracks().forEach((track) => track.stop());
    }
  };

  const copyToClipboard = async (blob) => {
    try {
      const file = new File([blob], "voice.ogg", { type: "audio/ogg; codecs=opus" });
      const item = new ClipboardItem({ [file.type]: file });
      await navigator.clipboard.write([item]);
      alert("Audio copied to clipboard!");
    } catch (err) {
      console.error("Clipboard write failed:", err);
      alert("Copy failed: " + err.message);
    }
  };

  const saveRecording = (blob) => {
    const a = document.createElement("a");
    a.href = URL.createObjectURL(blob);
    a.download = `recording_${Date.now()}.ogg`;
    a.click();
  };

  const removeRecording = (index) => {
    setRecordings((prev) => prev.filter((_, i) => i !== index));
  };

  return (
    <div className="bg-[#ece5dd] min-h-screen p-4 space-y-4">
      <div className="flex items-center space-x-2">
        {!isRecording ? (
          <button
            onClick={startRecording}
            className="bg-white border border-gray-300 hover:bg-gray-100 text-black px-4 py-2 rounded-full shadow-md relative"
            title="Start recording"
          >
            üéôÔ∏è
          </button>
        ) : (
          <button
            onClick={stopRecording}
            className="bg-white border border-gray-300 hover:bg-gray-100 text-black px-4 py-2 rounded-full shadow-md relative"
            title="Stop recording"
          >
            ‚èπÔ∏è {recordingTime}s
            <span className="absolute -top-3 right-0 w-3 h-3 bg-red-500 rounded-full animate-ping" />
          </button>
        )}
      </div>

      {isRecording && (
        <canvas
          ref={canvasRef}
          width={300}
          height={60}
          className="bg-white border rounded shadow"
        />
      )}

      <div className="space-y-3">
        {recordings.map((rec, index) => (
          <div
            key={index}
            className="flex items-center space-x-3 bg-[#dcf8c6] p-3 rounded-xl shadow-md w-fit max-w-full"
          >
            <img
              src="https://www.gravatar.com/avatar?d=mp"
              alt="Avatar"
              className="w-10 h-10 rounded-full"
            />
            <div className="flex flex-col">
              <audio controls src={rec.url} className="w-60" />
              <span className="text-xs text-gray-500">{rec.duration}s</span>
            </div>
            <button
              onClick={() => copyToClipboard(rec.blob)}
              className="text-green-700 hover:text-green-900"
              title="Copy to clipboard"
            >
              üìã
            </button>
            <button
              onClick={() => saveRecording(rec.blob)}
              className="text-blue-700 hover:text-blue-900"
              title="Download recording"
            >
              üíæ
            </button>
            <button
              onClick={() => removeRecording(index)}
              className="text-red-700 hover:text-red-900"
              title="Delete recording"
            >
              üóëÔ∏è
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
