import React, { useState, useRef, useEffect, useCallback } from "react";

export default function WhatsAppAudioRecorder() {
  const [recordings, setRecordings] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [audioStream, setAudioStream] = useState(null);
  const [permissionStatus, setPermissionStatus] = useState('prompt');
  const [error, setError] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);

  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const timerRef = useRef(null);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const dataArrayRef = useRef(null);
  const animationRef = useRef(null);
  const canvasRef = useRef(null);
  const recordingStartTimeRef = useRef(null);

  // Check and request microphone permissions
  const requestPermissions = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true,
          sampleRate: 48000, // WhatsApp compatible
          channelCount: 1, // Mono for smaller files
        } 
      });

      setPermissionStatus('granted');

      // Stop the test stream immediately
      stream.getTracks().forEach(track => track.stop());

      return true;
    } catch (err) {
      console.error('Permission error:', err);
      setPermissionStatus('denied');
      setError(`Microphone access denied: ${err.message}`);
      return false;
    }
  };

  useEffect(() => {
    requestPermissions();
  }, []);

  // Cleanup function
  const cleanup = useCallback(() => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }

    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
      animationRef.current = null;
    }

    if (audioContextRef.current && audioContextRef.current.state !== 'closed') {
      audioContextRef.current.close();
      audioContextRef.current = null;
    }

    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
      try {
        mediaRecorderRef.current.stop();
      } catch (e) {
        console.warn('MediaRecorder cleanup warning:', e);
      }
    }

    if (audioStream) {
      audioStream.getTracks().forEach(track => track.stop());
      setAudioStream(null);
    }

    setIsRecording(false);
    setRecordingTime(0);
  }, [audioStream]);

  useEffect(() => {
    return cleanup;
  }, [cleanup]);

  // Timer for recording duration
  useEffect(() => {
    if (isRecording) {
      recordingStartTimeRef.current = Date.now();
      timerRef.current = setInterval(() => {
        const elapsed = Math.floor((Date.now() - recordingStartTimeRef.current) / 1000);
        setRecordingTime(elapsed);
      }, 100);
    } else {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
    }

    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, [isRecording]);

  // Waveform visualization
  const drawWaveform = useCallback(() => {
    if (!canvasRef.current || !analyserRef.current || !isRecording) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const analyser = analyserRef.current;
    const dataArray = dataArrayRef.current;

    const draw = () => {
      if (!isRecording) return;

      animationRef.current = requestAnimationFrame(draw);
      analyser.getByteFrequencyData(dataArray);

      ctx.fillStyle = "#f0f0f0";
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      const barWidth = (canvas.width / dataArray.length) * 2.5;
      let barHeight;
      let x = 0;

      for (let i = 0; i < dataArray.length; i++) {
        barHeight = (dataArray[i] / 255) * canvas.height * 0.8;

        const gradient = ctx.createLinearGradient(0, canvas.height - barHeight, 0, canvas.height);
        gradient.addColorStop(0, '#25D366');
        gradient.addColorStop(1, '#128C7E');

        ctx.fillStyle = gradient;
        ctx.fillRect(x, canvas.height - barHeight, barWidth, barHeight);

        x += barWidth + 1;
      }
    };

    draw();
  }, [isRecording]);

  // Create WhatsApp-compatible audio file
  const createWhatsAppAudio = async (audioBlob, duration) => {
    try {
      setIsProcessing(true);

      // Create filename with timestamp
      const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, -5);
      const filename = `voice-${timestamp}.ogg`;

      // Create file object
      const file = new File([audioBlob], filename, { 
        type: 'audio/ogg; codecs=opus',
        lastModified: Date.now()
      });

      // Create object URL for preview
      const url = URL.createObjectURL(audioBlob);

      // Auto-download for drag & drop usage
      const downloadLink = document.createElement('a');
      downloadLink.href = url;
      downloadLink.download = filename;
      downloadLink.style.display = 'none';
      document.body.appendChild(downloadLink);

      const recording = {
        id: Date.now(),
        url,
        blob: audioBlob,
        file,
        filename,
        duration,
        timestamp: new Date().toLocaleTimeString(),
        size: audioBlob.size,
        downloadLink
      };

      setRecordings(prev => [...prev, recording]);
      setIsProcessing(false);

      return recording;
    } catch (err) {
      console.error('Audio processing error:', err);
      setError('Failed to process audio: ' + err.message);
      setIsProcessing(false);
      return null;
    }
  };

  const startRecording = async () => {
    try {
      setError(null);
      cleanup();

      if (permissionStatus !== 'granted') {
        const granted = await requestPermissions();
        if (!granted) return;
      }

      // Get fresh audio stream with optimal settings for WhatsApp
      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true,
          sampleRate: 48000,
          channelCount: 1,
        } 
      });

      setAudioStream(stream);

      // Setup audio context for visualization
      const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
      await audioCtx.resume(); // Ensure audio context is running

      const source = audioCtx.createMediaStreamSource(stream);
      const analyser = audioCtx.createAnalyser();
      analyser.fftSize = 256;
      analyser.smoothingTimeConstant = 0.8;
      source.connect(analyser);

      const bufferLength = analyser.frequencyBinCount;
      const dataArray = new Uint8Array(bufferLength);

      audioContextRef.current = audioCtx;
      analyserRef.current = analyser;
      dataArrayRef.current = dataArray;

      // Setup MediaRecorder with WhatsApp-compatible format
      const mimeTypes = [
        'audio/ogg; codecs=opus',
        'audio/webm; codecs=opus',
        'audio/ogg',
        'audio/webm'
      ];

      let selectedMimeType = null;
      for (const mimeType of mimeTypes) {
        if (MediaRecorder.isTypeSupported(mimeType)) {
          selectedMimeType = mimeType;
          break;
        }
      }

      if (!selectedMimeType) {
        throw new Error('No supported audio format found');
      }

      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: selectedMimeType,
        audioBitsPerSecond: 64000, // Good quality, WhatsApp compatible
      });

      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data && event.data.size > 0) {
          audioChunksRef.current.push(event.data);
          console.log('Data chunk received:', event.data.size, 'bytes');
        }
      };

      mediaRecorder.onstop = async () => {
        console.log('MediaRecorder stopped, chunks:', audioChunksRef.current.length);

        if (audioChunksRef.current.length > 0) {
          const finalBlob = new Blob(audioChunksRef.current, { 
            type: selectedMimeType 
          });

          console.log('Final blob size:', finalBlob.size, 'bytes');

          if (finalBlob.size > 0) {
            const actualDuration = recordingTime > 0 ? recordingTime : 1;
            await createWhatsAppAudio(finalBlob, actualDuration);
          } else {
            setError('Recording produced empty file');
          }
        } else {
          setError('No audio data captured');
        }

        audioChunksRef.current = [];
      };

      mediaRecorder.onerror = (event) => {
        console.error('MediaRecorder error:', event);
        setError('Recording error: ' + (event.error?.message || 'Unknown error'));
        cleanup();
      };

      mediaRecorder.onstart = () => {
        console.log('MediaRecorder started successfully');
        setIsRecording(true);
        drawWaveform();
      };

      // Start recording with small time slices for better data collection
      mediaRecorder.start(250); // Collect data every 250ms

      console.log('MediaRecorder start requested, state:', mediaRecorder.state);

    } catch (err) {
      console.error("Recording startup error:", err);
      setError(`Failed to start recording: ${err.message}`);
      cleanup();
    }
  };

  const stopRecording = () => {
    console.log('Stop recording requested');

    if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
      mediaRecorderRef.current.stop();
      console.log('MediaRecorder stop called');
    }

    // Don't cleanup immediately, let onstop handle it
    setIsRecording(false);

    // Cleanup after a delay to ensure onstop fires
    setTimeout(cleanup, 1000);
  };

  // Drag and drop functionality
  const handleDragStart = (e, recording) => {
    // Set the file for drag operation
    e.dataTransfer.setData('DownloadURL', `${recording.file.type}:${recording.filename}:${recording.url}`);
    e.dataTransfer.effectAllowed = 'copy';

    // Create drag image
    const dragImg = document.createElement('div');
    dragImg.innerHTML = `üé§ ${recording.filename}`;
    dragImg.style.padding = '8px';
    dragImg.style.background = '#dcf8c6';
    dragImg.style.border = '1px solid #25D366';
    dragImg.style.borderRadius = '8px';
    dragImg.style.position = 'absolute';
    dragImg.style.top = '-1000px';
    document.body.appendChild(dragImg);

    e.dataTransfer.setDragImage(dragImg, 0, 0);

    setTimeout(() => document.body.removeChild(dragImg), 0);
  };

  const downloadRecording = (recording) => {
    recording.downloadLink.click();
  };

  const removeRecording = (index) => {
    setRecordings(prev => {
      const recording = prev[index];
      URL.revokeObjectURL(recording.url);
      if (recording.downloadLink) {
        document.body.removeChild(recording.downloadLink);
      }
      return prev.filter((_, i) => i !== index);
    });
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  return (
    <div className="bg-[#ece5dd] min-h-screen p-4 space-y-4 max-w-4xl mx-auto">
      {/* Header */}
      <div className="bg-[#075e54] text-white p-4 rounded-lg shadow-lg">
        <h1 className="text-xl font-semibold">üé§ WhatsApp Voice Recorder</h1>
        <p className="text-sm opacity-90">Record, drag & drop to WhatsApp</p>
      </div>

      {/* Status Bar */}
      <div className="bg-white p-3 rounded-lg shadow flex items-center justify-between">
        <div className="flex items-center space-x-4">
          <span className="text-sm">
            üéôÔ∏è {permissionStatus === 'granted' ? '‚úÖ Ready' : 
                 permissionStatus === 'denied' ? '‚ùå No Access' : '‚è≥ Checking...'}
          </span>
          {isRecording && (
            <span className="text-red-600 font-medium animate-pulse">
              üî¥ Recording {recordingTime}s
            </span>
          )}
          {isProcessing && (
            <span className="text-blue-600">‚è≥ Processing...</span>
          )}
        </div>
      </div>

      {/* Error Display */}
      {error && (
        <div className="bg-red-50 border-l-4 border-red-400 p-4 rounded">
          <div className="flex">
            <div className="flex-shrink-0">‚ùå</div>
            <div className="ml-3">
              <p className="text-sm text-red-700">{error}</p>
            </div>
            <button 
              onClick={() => setError(null)}
              className="ml-auto text-red-400 hover:text-red-600"
            >
              √ó
            </button>
          </div>
        </div>
      )}

      {/* Recording Controls */}
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <div className="flex items-center justify-center space-x-4">
          {!isRecording ? (
            <button
              onClick={startRecording}
              disabled={permissionStatus !== 'granted' || isProcessing}
              className={`px-8 py-4 rounded-full text-lg font-medium shadow-lg transition-all transform hover:scale-105 ${
                permissionStatus !== 'granted' || isProcessing
                  ? 'bg-gray-300 text-gray-500 cursor-not-allowed' 
                  : 'bg-[#25D366] hover:bg-[#128C7E] text-white'
              }`}
            >
              üéôÔ∏è Start Recording
            </button>
          ) : (
            <button
              onClick={stopRecording}
              className="px-8 py-4 bg-red-500 hover:bg-red-600 text-white rounded-full text-lg font-medium shadow-lg transition-all transform hover:scale-105 relative"
            >
              ‚èπÔ∏è Stop Recording
              <span className="absolute -top-2 -right-2 w-4 h-4 bg-red-400 rounded-full animate-ping" />
            </button>
          )}
        </div>

        {/* Waveform */}
        {isRecording && (
          <div className="mt-6 bg-gray-50 p-4 rounded-lg">
            <canvas
              ref={canvasRef}
              width={600}
              height={100}
              className="w-full border rounded bg-white"
            />
            <div className="text-center text-sm text-gray-600 mt-2">
              üéµ Audio Visualization ‚Ä¢ {recordingTime}s
            </div>
          </div>
        )}
      </div>

      {/* Recordings */}
      <div className="space-y-3">
        {recordings.length === 0 ? (
          <div className="bg-white p-8 rounded-lg shadow text-center text-gray-500">
            <div className="text-4xl mb-2">üé§</div>
            <p>No recordings yet</p>
            <p className="text-sm">Your voice messages will appear here</p>
          </div>
        ) : (
          <>
            <h2 className="text-lg font-semibold text-gray-700">
              üì± Your Voice Messages ({recordings.length})
            </h2>
            {recordings.map((rec, index) => (
              <div
                key={rec.id}
                draggable
                onDragStart={(e) => handleDragStart(e, rec)}
                className="flex items-center space-x-4 bg-[#dcf8c6] p-4 rounded-xl shadow-md hover:shadow-lg transition-shadow cursor-move border-l-4 border-[#25D366]"
              >
                <img
                  src="https://www.gravatar.com/avatar?d=mp"
                  alt="Avatar"
                  className="w-12 h-12 rounded-full flex-shrink-0"
                />

                <div className="flex-1 min-w-0">
                  <audio controls src={rec.url} className="w-full mb-2" />
                  <div className="text-xs text-gray-600 flex justify-between items-center">
                    <span>‚è±Ô∏è {rec.duration}s ‚Ä¢ üìÖ {rec.timestamp}</span>
                    <span>üìÅ {formatFileSize(rec.size)} ‚Ä¢ üéµ .ogg</span>
                  </div>
                  <div className="text-xs text-gray-500 mt-1">
                    üìù {rec.filename}
                  </div>
                </div>

                <div className="flex flex-col space-y-2 flex-shrink-0">
                  <button
                    onClick={() => downloadRecording(rec)}
                    className="px-3 py-1 bg-[#25D366] hover:bg-[#128C7E] text-white rounded text-sm transition-colors"
                    title="Download file"
                  >
                    üíæ Save
                  </button>
                  <button
                    onClick={() => removeRecording(index)}
                    className="px-3 py-1 bg-red-500 hover:bg-red-600 text-white rounded text-sm transition-colors"
                    title="Delete recording"
                  >
                    üóëÔ∏è Delete
                  </button>
                </div>

                <div className="text-2xl cursor-move" title="Drag to WhatsApp">
                  ‚ÜóÔ∏è
                </div>
              </div>
            ))}
          </>
        )}
      </div>

      {/* Instructions */}
      {recordings.length > 0 && (
        <div className="bg-blue-50 border border-blue-200 p-4 rounded-lg">
          <h3 className="font-medium text-blue-800 mb-2">üìã How to use:</h3>
          <ul className="text-sm text-blue-700 space-y-1">
            <li>‚Ä¢ üñ±Ô∏è <strong>Drag & Drop:</strong> Drag the voice message directly to WhatsApp</li>
            <li>‚Ä¢ üíæ <strong>Download:</strong> Click "Save" to download the .ogg file</li>
            <li>‚Ä¢ üì± <strong>Mobile:</strong> Downloaded files can be shared via WhatsApp</li>
            <li>‚Ä¢ üéµ <strong>Format:</strong> OGG/Opus - WhatsApp compatible</li>
          </ul>
        </div>
      )}
    </div>
  );
}
